SET FOREIGN_KEY_CHECKS=0;
-- ----------------------------
-- Table structure for article
-- ----------------------------
DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '文章ID',
  `cid` smallint(5) NOT NULL COMMENT '栏目id',
  `title` varchar(100) NOT NULL COMMENT '标题',
  `duction` varchar(255) NOT NULL COMMENT '简介',
  `thumb` varchar(255) DEFAULT '' COMMENT '缩略图',
  `content` text COMMENT '内容',
  `keywords` varchar(255) DEFAULT NULL COMMENT '关键词',
  `author` varchar(20) NOT NULL COMMENT '作者',
  `tag` tinyint(1) DEFAULT '0' COMMENT '来源 0 原创 1 转载',
  `istop` tinyint(1) DEFAULT '0' COMMENT '置顶 0 no 1 yes',
  `isshow` tinyint(1) NOT NULL COMMENT '0 显示 1 不显示',
  `time` varchar(10) NOT NULL COMMENT '创建时间',
  `number` int(15) NOT NULL COMMENT '点击量',
  `feedback` int(11) NOT NULL COMMENT '评论量',
  `ip` varchar(50) NOT NULL COMMENT '发布者ip',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=57 DEFAULT CHARSET=utf8;
-- ----------------------------
-- Records of article
-- ----------------------------
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('1','10','游泉州清源山','我乘座人间的车 来到你的眼前 探寻传说中的清源 铁拐李的拐杖之声 杳然远去……','/uploads/20180405\126acc100dba9d42adffbd161bf97b2f.jpg','<p><img src="http://file26.mafengwo.net/M00/88/4D/wKgB4lJlEcWAcVSNAA4iIBWx3DA06.jpeg" width="500"/></p><p>我乘座人间的车 来到你的眼前<br/>探寻传说中的清源<br/>铁拐李的拐杖之声 杳然远去<br/>空剩下 烟雾萦锁&nbsp;</p><p><br/></p><p>梦里的杜鹃花 悄然<br/>悄然飘落 坐在岩石上<br/>对着一泓碧波<br/>落花与心灵对话</p><p><br/></p><p>几声鸣唱　小鸟不知忧愁　依旧<br/>俯视　城中千万人家<br/>几点灯火在尘世里明明灭灭</p>','诗歌,泉州,清源山','王赤清','0','0','0','1517152312','506','0','127.0.0.1');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('56','17','git常用命令','git 常用命令','/uploads/20180405\5ae8490096ce8ca733bda139661d337f.jpg','<pre class="brush:as3;toolbar:false">git&nbsp;remote&nbsp;-v	#查看当前项目clone地址

git&nbsp;add&nbsp;.	#项目添加到暂存区

git&nbsp;add&nbsp;x&nbsp;x	#&nbsp;x为文件，多个文件1空格隔开

git&nbsp;commit&nbsp;-m&#39;x&#39;	#提交说明(注释)

git&nbsp;push&nbsp;origin&nbsp;master	#提交到仓库

git&nbsp;ls-files		#查看缓存区文件

git&nbsp;rm&nbsp;-r&nbsp;--cached&nbsp;&#39;文件路径&#39;&nbsp;	#删除缓存中文件

git&nbsp;rm&nbsp;--f&nbsp;			#删除物理/缓存文件，且文件不会回到垃圾桶.注意：工作之前

git&nbsp;pull		#从远程服务器拉取最近版本并合并到本地

git&nbsp;clone		#从服务器克隆到本地

git&nbsp;status&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;#展示状态信息

git&nbsp;branch&nbsp;xx&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;#创建&nbsp;xx&nbsp;分支

git&nbsp;checkout&nbsp;xx&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;#切换&nbsp;xx&nbsp;分支

git&nbsp;checkout&nbsp;-b&nbsp;xx&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;#相当于上面两条，创建并切换到&nbsp;xx　分支</pre><p><br/></p>','git,命令','清蝎','0','0','0','1517153486','510','0','127.0.0.1');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('55','11','有个厉害的的“程序员”老婆是什么感受？','其实小编觉得有个厉害的程序员老婆一定会是一件非常酷炫的事，如果太厉害了，也不怕。有个机智的网友已经给出了一个解决问题的方法：“没事，怀孕生娃立刻记忆力和脑速就慢了。就等这个机会吧。”对于这个，小编是真的服气。','/uploads/20180405\eea2d9f5a816f2143281652af79d8600.jpg','<p>　　<span style="color: rgb(51, 51, 51); font-family: ">为什么不写个有个程序员老婆是啥感觉呢？小编觉得这个话题非常有意思，今天小编分享给大家的是“有个厉害的程序员老婆（女朋友）是什么体验？”。带大家了解一下如果自己的自己的老婆是个程序员大牛的会有什么苦笑不得的事情发生。</span></p><p><span style="color: rgb(51, 51, 51); font-family: "><img src="/uploads/20180129/1517209817647671.gif"/></span></p><p><br/></p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: "><strong style="box-sizing: inherit;">程序员甲：</strong></p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: ">　　现在的媳妇以前是在网络安全圈混的。。。有次我在公司跟前女友聊天，结果前女友越聊越入戏，然后我果断删除聊天记录，格式化全部硬盘。。然后回家后直接被天蝎座的媳妇堵在门外嘶吼质问。。后来才知道我笔记本被装了木马。。我每次聊天时她就在看现场直播。。。我杀了一遍又一遍，至今没查到是什么病毒木马。。。我估计一会我又要被发现提起以前的事了。。。匿名有用么我擦。。。。。嘤嘤嘤。。</p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: "><br/></p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: "><span style="color: rgb(51, 51, 51); font-family: ">网友：</span></p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: "><img src="/uploads/20180129/1517209903900612.gif"/></p><p><span style="color: rgb(51, 51, 51); font-family: "><strong style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: ">程序员乙：</strong></span><br/></p><p><span style="color: rgb(51, 51, 51); font-family: "><strong style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: "><img src="/uploads/20180129/1517210102624089.gif"/></strong></span></p><p><span style="color: rgb(51, 51, 51); font-family: "><strong style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: "></strong></span></p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: ">网友：</p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: ">0：还要匿名，好辛苦</p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: ">1：试着和女票一起看啊。</p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: ">2：心疼……</p><p><span style="color: rgb(51, 51, 51); font-family: "><strong style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: "><img src="/uploads/20180129/1517210151927808.gif"/></strong></span><br/></p><p><span style="color: rgb(51, 51, 51); font-family: "><strong style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: "><strong style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: ">程序员丙：</strong></strong></span></p><p><br/></p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: ">　　她大一的时候系统还是我帮装的，后来她就莫名其妙的钻研起电脑来，到她大二破了我邮箱密码的时候我心里一万匹草泥马跑过…话说那时候我俩还没有谈恋爱，后来觉得这妹子干什么都有一股认真劲儿，就开始动不动把电脑弄坏让她帮忙修……后来，在帮我修了多次电脑，教会了我CDR之后，我俩就在一起了。其实每次看她调出一堆我看不懂的什么代码认真的戴着眼镜排查系统文件的时候，我在旁边总觉得，哇，好美…</p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: ">　　日常解决的那些问题也不太好说，主要我也不太专业搞不明白，两件事情印象比较深刻，1.她教会了我不要所有的密码都用一样的，而且据她讲密码和姿势一样，复杂没有用，关键是要够长。2.有天加班在办公室的电脑上和同事QQ聊天，略有暧昧，然后电脑就黑屏了…短信响起，她发来“挺骚啊…”。艹后面这件事情总感觉有点儿1984的感觉。</p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: ">　　其实还是挺幸福的一件事情，妹子逻辑能力和动手能力强，经常带着我一起动手解决问题…比如说给家里刮大白修水龙头贴瓷砖…常常会想以后有小Baby了会和他讲这样一个故事：从前，你妈妈是个拯救世界的女hero,你爸负责挖掘女hero的原力然后到处宣扬她的光辉事迹、做家务和做饭…</p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: "><strong style="box-sizing: inherit;">程序媛丁：</strong></p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: ">　　好想邀请我老公来答……但是他不玩知乎……反正他的SIS，1024什么的都是我帮他找地址。蓝屏什么的基本上也是我排查……他的小视屏没有我找不到的……前几天他的iphone，我还帮他换了电池、静音键排线、电源键排线……</p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: "><strong style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: ">程序员戊：</strong></p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: "><strong style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: "><img src="http://codebay.cn/wp-content/uploads/2018/01/image.php_-10.gif"/></strong></p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: "><strong style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: "><img src="/uploads/20180129/1517210240718200.gif"/></strong></p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: "><strong>ps:</strong></p><p style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: ">　　<span style="color: rgb(51, 51, 51); font-family: ">其实小编觉得有个厉害的程序员老婆一定会是一件非常酷炫的事，如果太厉害了，也不怕。有个机智的网友已经给出了一个解决问题的方法：“没事，怀孕生娃立刻记忆力和脑速就慢了。就等这个机会吧。”对于这个，小编是真的服气。</span></p><p><span style="color: rgb(51, 51, 51); font-family: "><strong style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: "><strong style="box-sizing: inherit; color: rgb(51, 51, 51); font-family: "><br/></strong></strong></span><br/></p>','程序员,大牛','网络','1','0','0','1517210700','501','0','127.0.0.1');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('54','9','网站推荐系统“猜你喜欢”是怎么猜中你心思的？','　　现在人们上网购物都习惯了系统给出的“猜你(还会)喜欢”，有时候它好像比你自己都还要了解你。推荐系统究竟是怎样“猜”中你心思的呢?','/uploads/20180405\3724e2956d77cdd76566599aab87598a.jpg','<p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: ">英文原文：<a href="http://spectrum.ieee.org/computing/software/deconstructing-recommender-systems" style="border: 0px; margin: 0px; padding: 0px; text-decoration-line: none; color: rgb(0, 153, 204);">Deconstructing Recommender Systems</a>，编译：<a href="http://www.guokr.com/article/382553/" style="border: 0px; margin: 0px; padding: 0px; text-decoration-line: none; color: rgb(0, 153, 204);">ccyou@guokr</a></p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: ">如今，到网上购物的人已经习惯了收到系统为他们做出的个性化推荐。Netflix 会推荐你可能会喜欢看的视频。TiVo 会自动把节目录下来，如果你感兴趣就可以看。Pandora 会通过预测我们想要听什么歌曲从而生成个性化的音乐流。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: ">所有这些推荐结果都来自于各式各样的推荐系统。它们依靠计算机算法运行，根据顾客的浏览、搜索、下单和喜好，为顾客选择他们可能会喜欢、有可能会购买的商品，从而为消费者服务。推荐系统的设计初衷是帮助在线零售商提高销售额，现在这是一块儿规模巨大且不断增长的业务。与此同时，推荐系统的开发也已经从上世纪 90 年代中期只有几十个人研究，发展到了今天拥有数百名研究人员，分别供职于各高校、大型在线零售商和数十家专注于这类系统的其他企业。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: ">这些年来，推荐系统有了相当的进展。开始时它们还相对较为粗糙，往往对行为做出不准确的预测；但随着更多的和不同类型的网站用户数据变得可用，推荐系统得以将创新算法应用于这些数据之上，它们迅速得到了改善。今天，推荐系统都是些极其复杂和精专的系统，常常看起来比你自己还要了解你。同时，推荐系统正在向零售网站以外的领域拓展：大学用它们来引导学生选课，移动电话公司靠它们来预测哪些用户有可能转投另一家供应商，会议主办方也测试过用它们来分配论文给审稿专家。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: "><a href="http://jbcdn2.b0.upaiyun.com/2012/11/angd2e.jpg" style="border: 0px; margin: 0px; padding: 0px; text-decoration-line: none; color: rgb(0, 153, 204);"><img class="aligncenter size-full wp-image-30649" title="解构推荐系统：“猜你喜欢”是怎么猜中你的心思" alt="解构推荐系统：“猜你喜欢”是怎么猜中你的心思" src="/uploads/20180207/1517986127.jpg" width="260" height="196"/></a></p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: ">我们两人从推荐系统的早期开始便一直在开发和研究它们，最初是以学术研究者的身份，参与 GroupLens 计划（GroupLens Project）。1992 年起，GroupLens 通过对美国兴趣论坛网站 Usenet 讨论区里的消息进行排序，将用户指向他们可能会感兴趣、但自己尚未发现的话题线索。几年以后，我们成立了 Net Perceptions，这是一家推荐算法公司，在互联网第一次热潮期间（1997 年 – 2000 年），一直处于业界领先地位。有鉴于此，虽然这些公司极少公开谈论他们的推荐系统是如何运作的，我们的经验使我们能够深入了解亚马逊和其他在线零售商幕后的情景。（在本文中，我们的分析是在观察和推理的基础上得出的，不包含任何内部消息）。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: ">下面就是我们所看到的。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: "><strong style="border: 0px; margin: 0px; padding: 0px;">推荐算法是怎么“猜你喜欢”的？</strong></p><p><a href="http://jbcdn2.b0.upaiyun.com/2012/11/ez6tvy.jpg" style="border: 0px; margin: 0px; padding: 0px; text-decoration-line: none; color: rgb(0, 153, 204);"><img class="aligncenter  wp-image-30650" title="解构推荐系统：“猜你喜欢”是怎么猜中你的心思" alt="解构推荐系统：“猜你喜欢”是怎么猜中你的心思" src="/uploads/20180207/1517986129.jpg" width="540" height="236"/></a><br/>来源：recommenderapi.com</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">你有没有想过自己在亚马逊眼中是什么样子？答案是：你是一个很大、很大的表格里一串很长的数字。这串数字描述了你所看过的每一样东西，你点击的每一个链接以及你在亚马逊网站上买的每一件商品；表格里的其余部分则代表了其他数百万到亚马逊购物的人。你每次登陆网站，你的数字就会发生改变；在此期间，你在网站上每动一下，这个数字就会跟着改变。这个信息又会反过来影响你在访问的每个页面上会看到什么，还有你会从亚马逊公司收到什么邮件和优惠信息。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">许多年来，推荐系统的开发者试过用各种各样的方法来采集和解析所有这些数据。最近这段时间，多数人都选择使用被称为个性化协同推荐（Personalized Collaborative Recommender）的算法。这也是亚马逊、Netflix、Facebook 的好友推荐，以及一家英国流行音乐网站 Last.fm 的核心算法。说它 “个性化”，是因为这种算法会追踪用户的每一个行为（如浏览过的页面、订单记录和商品评分），以此进行推荐；它们可不是瞎猫碰上死耗子——全凭运气。说它 “协同”，则是因为这种算法会根据许多其他的顾客也购买了这些商品或者对其显示出好感，而将两样物品视为彼此关联，它不是通过分析商品特征或者关键词来进行判断的。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">不同类型的个性化协同推荐系统最晚从 1992 年开始便已经出现。除了 GroupLens 计划，另一项早期的推荐系统是 MIT 的 Ringo，它会根据用户的音乐播放列表从而给用户推荐其他他们有可能会喜欢的音乐。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;"><strong style="border: 0px; margin: 0px; padding: 0px;">User-User 算法：计算用户之间的相似度</strong></p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">GroupLens 和 Ringo 都使用了一种简单的协同算法，被称为 “用户关联”（user-user）的算法。这种类型的算法会计算一对用户之间的 “距离”，根据的是他们对同一物品打分的相似程度。举例来说，如果吉姆和简都给《电子世界争霸战》（Tron）这部电影打了 5 分，那么他们之间的距离就是 0。如果吉姆给它的续集《创：战纪》（Tron: Legacy ）这部电影打了 5 分，而简只打了 3 分，那么他们之间的距离就变大了。按照这样的计算得出来品味相对 “靠近” 的用户，我们把他们称之为共有一个 “邻集”（neighborhood）。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">但是，这种用户关联的策略效果并不是很好。首先，形成有意义的邻集很难：很多用户两两之间只有很少几个共同评分，有的就完全没有；而仅有的那几个都打了分的项目呢，往往是票房大片，基本上人人都喜欢的那种。再来，由于用户之间的距离可以变得很快，算法必须当场就进行大部分的计算；而这可能会比一个在网站上这儿点点那儿戳戳的人下一个动作发出之前需要更久的时间。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;"><strong style="border: 0px; margin: 0px; padding: 0px;">Item-Item 算法：计算物品之间的关联</strong></p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">因此，大部分的推荐系统如今都依靠一种“物-物关联”（item-item）的算法，这种算法计算的是两本书、两部电影或者两个其他什么东西之间的距离，依据的是给它们打过分的用户的相似度。喜欢 Tom Clancy 书的人很可能会给 Clive Cussler 的作品打高分，因此 Clancy 和 Cussler 的书就共处一个邻集。一对物品之间的距离可能是根据成百上千万的用户的评分计算得出，在一段时间里往往保持相对稳定，因此推荐系统可以预先计算距离，并更快的生成推荐结果。亚马逊和 Netflix 都曾公开表示过他们使用的是物-物关联算法的变种，但对细节都绝口不提。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">用户关联算法和物-物关联算法都有的一个问题，是用户评分的不一致性。当给他们机会再评一次分时，用户往往会对同一件物品给出不同的得分。品味在变、心情在变，印象也在变。MIT 在上世纪 90 年代进行的一项研究表明，在最初打分一年以后，用户的评分会发生平均 1 分（满分 7 分）的变动。研究人员们也在一直在尝试不同的方法在模型中纳入这一变量；比如说，如果用户给某个商品了打一个分，但这个评分与推荐算法所了解的关于这个人和这个商品的所有其他信息不相符，有的推荐算法就会邀请用户再次对这个商品进行评价。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;"><strong style="border: 0px; margin: 0px; padding: 0px;">降维算法：把事物特征一般化</strong></p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">不过，用户关联算法和物-物关联算法还存在一个比一致性更大的问题：它们太死了。就是说，它们能发现都喜欢同一样东西的人，但却忽略了爱好非常相似的潜在用户组合。比如说你喜欢莫奈的睡莲。那么，在这个法国印象派大师画的 250 幅睡莲中，你最喜欢哪一幅？在一群喜欢莫奈的人当中，完全可能每个人喜欢的睡莲都不相同，而基本的算法就有可能识别不出这些人都有着共同的爱好。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">大约十年前，研究者们想出了一个办法，通过一个叫降维（Dimensionality Reduction）的过程，把事物更一般化的表现出来。这种方法在计算量上比用户关联和物-物关联算法要密集得多，因此也就没有那么快的得到采用。但随着计算机变更快更便宜，降维算法也逐步取得了一些进展。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">为了弄清降维算法是怎么工作的，我们来看看你爱吃的东西，以及如何把它跟其他一百万人爱吃的东西做比较。你可以把这些信息用一个巨型矩阵表示出来，每一条竖线代表一样食物，每个人爱吃什么东西就自然形成了一行。在你的这一行上面或许会显示你给了烤牛排 5 颗星、红烧小排 4 星半、烤鸡翅 2 颗星、冻豆腐卷 1 颗星、奶酪烤蘑菇 5 颗星、盐水毛豆 4 颗星，等等。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">然而，使用这个矩阵的推荐算法并不关心你给哪种食物评了多少颗星。它想要了解的是你一般而言的喜好，这样它可以将这个信息应用到更丰富多样的食物上。比如说，基于你上面给出的信息，算法可能会认为你喜欢牛肉、咸的东西和烤制菜品，不喜欢鸡肉和任何油炸的东西，不喜欢也不讨厌蔬菜，依此类推。你爱吃的食物所拥有的特点或者说维度，它的数量和符合你要求的食物的数量比起来要小得多——至多可能 50 或 100。通过查对这些维度，推荐算法可以迅速决定你是否会喜欢一种新的食物（比方说盐焗排骨），方法就是把这种食物的各项维度（咸的、牛肉做的、不是鸡肉、不是炒的、不是蔬菜、不是烤的）同你的资料进行比对。这种更为一般性的呈现使得推荐算法能准确的发现有着相似但不同喜好的用户。而且，它大幅压缩了矩阵的规模，使算法变得更加高效。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">这是一个很酷的解决方案。不过，你爱吃的食物的维度该上哪儿去找呢？肯定不是去问厨师。推荐系统会使用一种称为奇异值分解的数学方法来计算维度。这种方法涉及到把最初的一个巨型矩阵分解为两个 “口味矩阵”——其中一个包含了所有的用户和 100 项口味维度，另一个则包含了所有的食物和 100 项口味维度——再加上第三个矩阵，当乘以前面两个矩阵中的任意一个时，会得到最初的那个矩阵（※此处已更改）。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">不像上面例子中说的那样，计算用的维度既不是描述性的，也一点儿都不直观；它们是纯抽象的值。这并没有什么，只要这些值最终生成准确的推荐结果就行了。这种方法的主要缺点是，创建矩阵所需要的时间会随着客户和产品数量的增多而飞速增长——创建一个拥有 2.5 亿名客户和 1000 万种产品的矩阵，需要花上创建一个 25 万名客户和 1 万种产品的矩阵 10 亿倍那么多的时间。而且这一过程还需要经常重复。一旦收到新的评分，矩阵就已经过时；在像亚马逊这样的公司，每一秒钟都会收到新的评论。幸运的是，就算略微过时，矩阵仍然能以一个挺不错的水平运作。研究人员们也已经在设计新的算法，为奇异值分解提供可用的近似值并显著缩短计算时间。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">&nbsp;</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">&nbsp;讲完了推荐算法是如何“猜你喜欢”的&nbsp;，现在，你对于每回上网购物时在线零售商是如何打量你，并努力把你的喜好和其他人的相匹配有了一个基本的概念。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">推荐系统还有另外两大特点，也对你最终看到的推荐结果有着显著的影响：第一，在弄清楚你和其他购物者的相似度有多高之前，推荐系统必须先弄明白你真正喜欢什么；第二，推荐系统依照一组商业规则运行，以确保推荐结果既让你觉得有用，也使商家有利可图。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;"><strong style="border: 0px; margin: 0px; padding: 0px;">推荐算法是如何赢得你的信任，又让商家有钱可赚的？</strong></p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;"><strong style="border: 0px; margin: 0px; padding: 0px;">采集你的上网数据</strong></p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">举个例子，来看亚马逊的艺术品商店，上次我们去看的时候那里有 900 多万册印刷品和海报在降价促销。亚马逊的艺术品商店有这样几个方法来评估你的喜好。它会让你在 1 到 5 颗星的等级上给某一件艺术作品打分，它也会记录下你把哪些画点击放大了来看、哪些画你反反复复看了好多次，你把哪些放进了心愿单，还有你最终实际下单买了什么。它还会追踪在你浏览过的每一个页面上都显示了哪些画作。在线零售商会使用你在其网站行进的路径（你浏览过的页面和点击商品的链接） 来向你推荐相关联的商品。此外，它还把你的购买记录和打分信息结合起来，建立一个你长期购买偏好的档案。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">像亚马逊这样的公司会收集大量此类有关客户的数据。在你登录期间，你在它网站上的几乎每一个动作都会被记下来，留作将来使用。多亏有了浏览器 cookie，连匿名购物者的上网记录商家也能维持，最终这些数据将在匿名购物者创建账户或者登陆时，链接到顾客的个人资料。这种爆炸式的数据采集并非为在线商家所独有，沃尔玛便以其对现金收据数据的深入挖掘而著称于业界。但是，网上商店处在一个更有利的位置去查看和记录，不止是消费者买了些什么，还包括你曾考虑过、浏览过和决定不买哪些商品。在全世界大部分地区，所有这类活动都是任人监视和记录的；只有在欧洲，数据隐私法在一定程度上限制了这种操作。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">当然，不论法律如何，顾客发现自己的数据被人滥用后，都会产生强烈的抵触情绪。早在 2000 年 9 月，亚马逊吃过一次苦头：有一部分顾客发现他们收到的报价更高，因为网站将他们识别为老顾客，而不是匿名进入或是从某个比价网站转接进来的顾客。亚马逊声称这只是一项随机的价格测试，其呈现出来的结果与老顾客身份之间的关联纯属巧合。话是这样说，它还是叫停了这项操作。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;"><strong style="border: 0px; margin: 0px; padding: 0px;">在商业规则下运行</strong></p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">加在推荐算法之上的种种商业规则，旨在防止算法给出愚蠢的推荐，并帮助在线零售商在不失去你信任的前提下实现营业额的最大化。最起码，推荐系统应该避免人们说的超市悖论（Supermarket Paradox）。例如，差不多每个去超市的人都喜欢吃香蕉，也经常会买一些。那么，推荐系统该不该向每一位顾客都推荐香蕉呢？答案是否定的——这样做既帮不上顾客，也提高不了香蕉的销量。所以，智能的超市推荐系统始终会包括有一条规则，明确地将香蕉排除在推荐结果之外。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">这个例子可能听起来没什么，但在我们早期经手的一个项目中，我们的推荐系统就曾经向几乎每一个到访我们网站的人推荐披头士的《白色专辑》（White Album）。从统计学的意义上讲，这是个很棒的推荐：顾客此前都没有从这个电子商务网点购买过这张专辑，而大多数顾客对《白色专辑》的评价都很高。尽管如此，这个推荐仍然是无效的——任何一个对《白色专辑》感兴趣的人都已经有了一张了。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">当然，大部分的推荐规则都是更加微妙的。比如说，当约翰在 9 月份在 Netflix 索动作影片时，结果中不会出现《复仇者联盟》（The Avengers），因为这部大片在当时还没有租借版，这样的推荐结果不会让 Netflix 有钱赚。因此，约翰被导向了《钢铁侠 2》（Iron Man 2），这部片已经可以用流媒体播放了。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">其他的规则还包括禁止推荐为招徕顾客而亏本销售的商品（loss leader）；反过来，鼓励推荐滞销品。在经营Net Perceptions期间，我们就曾与一位客户合作，他利用推荐系统来识别库存积压商品的潜在客户，取得了相当大的成功。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;"><strong style="border: 0px; margin: 0px; padding: 0px;">赢取你的信任</strong></p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">然而，这种事情很快就会变得棘手起来。一个只会推销高利润商品的推荐算法是不会赢得顾客的信任的。这就像是去餐馆，那儿的服务生极力向你推荐某道鱼一样。这个鱼真的是他觉得最好吃的吗？还是大厨催着底下的人赶在鱼变质前把它给卖出去？</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">为了建立信任感，更复杂的推荐算法会尽力保持一定的透明度，让顾客对系统为什么会向自己推荐这件商品有一个大致的概念，并且在不喜欢收到的推荐结果时，可以更改他们的个人资料。比如说，你可以删除你在亚马逊上买来送礼的购物记录；毕竟，那些东西反映的不是你个人的喜好。你还可以知道系统为什么会向你推荐某些产品。当亚马逊为约翰挑选了 Jonathan Franzen 的小说《自由》之后，约翰点击标签上的链接“为什么推荐给我？”。随即显示出一份简要的说明，原来是他放在心愿单里的几本书触发了这一推荐。不过，由于他还没有读过心愿单上的那几本书，约翰就不去管《自由》这个推荐结果了。像这样的解释说明会让用户明白推荐结果是否有用。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">但是，完善个人资料和解释推荐结果往往不足以保证系统不出错。最近，亚马逊用高清大屏幕电视机（HDTV）的促销电子邮件对乔进行了轰炸——每周 3 封，连续扔了一个月。除了给乔寄了过多的电子邮件，这家零售商还没有意识到，乔已经用他妻子的账户买了一台电视机。此外，这些电子邮件并没有提供一种很明显的方法，让乔可以说“谢谢，但我不感兴趣”。最终，乔取消了他在亚马逊的一些邮件订阅；他并不在意收不到各种信息，而且他有了更多的时间来真的看他的电视。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;"><strong style="border: 0px; margin: 0px; padding: 0px;">推荐算法的作用究竟有多大？</strong></p><p><a href="http://jbcdn2.b0.upaiyun.com/2012/11/dsifh8.jpg" style="border: 0px; margin: 0px; padding: 0px; text-decoration-line: none; color: rgb(0, 153, 204);"><img class="aligncenter  wp-image-30651" title="解构推荐系统：“猜你喜欢”是怎么猜中你的心思" alt="解构推荐系统：“猜你喜欢”是怎么猜中你的心思" src="/uploads/20180207/1517986130.jpg" width="540" height="248"/></a><br/>alumni.berkeley.edu</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">那好，推荐算法究竟起了多大作用呢？它们当然一直都在增加在线销售额；据阿伦森集团（Aaronson Group）的分析师杰克•阿伦森（Jack Aaronson）估计，由于推荐算法带动销售额的增长，对推荐算法的投资能获得 10%-30% 的收益。而且，它们还只是刚刚起步。现在，对我们这些研究推荐系统的人来说，最大的挑战在于弄清楚如何去判断新的方法和算法才最好。这可不像基准化分析微处理器那么简单，因为不同的推荐系统有着非常不同的目标。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">评价一个算法最简单的方法，是看它的预测和用户的实际评价之间差异有多大。举例来说，假如约翰给青春浪漫小说《暮光之城》（Twilight ）一颗星，亚马逊或许会注意到算法根据其他相似用户的评价曾预计约翰会给两颗星，也即出现了一颗星的偏差。但是，卖家更加关心算法在用户评价高的商品上出的错，因为好评多的物品是顾客更有可能购买的；约翰反正也不会买《暮光之城》。所以，把这个评价计入考虑对理解推荐算法起了多大作用没什么帮助。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">另一个常见方法是看算法给出的推荐结果和顾客实际购买的商品，之间匹配度有多高。不过，这种方法也可能起到误导作用，因为这样分析会将用户自己设法找到的商品错误地算在推荐算法的头上，而用户自己找得到东西恰恰是最不应该被推荐的！鉴于这些方法的缺点，研究人员一直在研究新的评判指标，不只看精度，也会关注像发现意外惊喜和多样性等其他属性。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">发现意外惊喜（Serendipity）会加权不寻常的推荐结果，尤其是那些对某一个用户极具价值，但对其他同类用户而言没什么用的推荐结果。调整为发现意外惊喜的算法会注意到《白色相簿》似乎是一个对几乎每个人来说都不错的推荐，因此会改为寻找一个不太常见的选择——也许是 Joan Armatrading 的《爱和情感》。这个不那么热门的推荐结果不太可能击中目标，但一旦它遇上了，则将给用户带来一个大得多的惊喜。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">看推荐结果的多样性同样也很能说明问题。比方说，一个超爱看 Dick Francis 神秘类小说的用户，在看到推荐表单里全都是 Dick Francis 的作品时，仍有可能会感到失望。一个真正多样化的推荐表单会包括不同作者和不同类型的书，还有电影、游戏和其他的产品。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">推荐系统研究则需要突破各种各样的阻碍，远不止是在现有的系统上进行微调。研究者们眼下正在考虑的是，推荐算法应该在怎样一个程度上帮助用户发掘一个网站的内容集合中他们未曾了解的部分。比方说，把买书的人送去亚马逊的服装部门，而不是给一些安全的、顾客更有可能接受的推荐结果。在零售世界之外，推荐算法可以帮助人们接触到新的想法；就算我们不同意其中的一些，但整体作用大概会是积极的，因为这将有助于减少社会的巴尔干化（Balkanization，即碎片化）。推荐算法能不能做到这一点，还要不让人感到厌烦或者不信任，仍需拭目以待。</p><p style="border: 0px; margin-top: 0px; margin-bottom: 20px; padding: 0px;">但有一点是明确的：推荐系统只会变得越来越好，收集越来越多关于你的数据，并在别的、意想不到的地方展示出来。如果你喜欢这篇文章，亚马逊会很乐意向你推荐其他所有你可能会喜欢的关于推荐系统的书。</p>','猜你喜欢,物联网','网络','1','1','0','1517986181','503','0','127.0.0.1');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('53','16','MySQL索引类型一览 让MySQL高效运行起来','索引是快速搜索的关键。MySQL索引的建立对于MySQL的高效运行是很重要的。下面介绍几种常见的MySQL索引类型。在数据库表中，对字段建立索引可以大大提高查询速度。假如我们创建了一个 mytable表：CREATE TABLE mytable(   ID INT NOT NULL,    username VARCHAR(16) NOT NULL  );   我们随机向里面插入了10000条记录，其中有一条：5555, admin。在查找username="admin"的记录 SELECT * FROM','/uploads/20180407\e9bc6c33147b8079844f40abf781ca93.jpg','<p><span class="top11"></span></p><p>索引是快速搜索的关键。MySQL索引的建立对于MySQL的高效运行是很重要的。下面介绍几种常见的MySQL索引类型。</p><p>在数据库表中，对字段建立索引可以大大提高查询速度。假如我们创建了一个 mytable表：</p><pre class="brush:as3;toolbar:false">CREATE&nbsp;TABLE&nbsp;mytable(ID&nbsp;INT&nbsp;NOT&nbsp;NULL,username&nbsp;VARCHAR(16)&nbsp;NOT&nbsp;NULL);</pre><p>我们随机向里面插入了10000条记录，其中有一条：5555, admin。</p><p>在查找username=&quot;admin&quot;的记录 SELECT * FROM mytable WHERE username=&#39;admin&#39;;时，如果在username上已经建立了索引，MySQL无须任何扫描，即准确可找到该记录。相反，MySQL会扫描所有记录，即要查询10000条记录。</p><p>索引分单列索引和组合索引。单列索引，即一个索引只包含单个列，一个表可以有多个单列索引，但这不是组合索引。组合索引，即一个索包含多个列。</p><p>MySQL索引类型包括：</p><p><strong>（1）普通索引</strong></p><p>这是最基本的索引，它没有任何限制。它有以下几种创建方式：</p><ul class=" list-paddingleft-2" style="list-style-type: disc;"><li><p>创建索引</p></li></ul><pre class="brush:as3;toolbar:false">CREATE&nbsp;INDEX&nbsp;indexName&nbsp;ON&nbsp;mytable(username(length));</pre><p>如果是CHAR，VARCHAR类型，length可以小于字段实际长度；如果是BLOB和TEXT类型，必须指定 length，下同。</p><ul class=" list-paddingleft-2" style="list-style-type: disc;"><li><p>修改表结构</p></li></ul><pre class="brush:as3;toolbar:false">ALTER&nbsp;mytable&nbsp;ADD&nbsp;INDEX&nbsp;[indexName]&nbsp;ON&nbsp;(username(length))&nbsp;&nbsp;//创建表的时候直接指定
CREATE&nbsp;TABLE&nbsp;mytable(
ID&nbsp;INT&nbsp;NOT&nbsp;NULL,
username&nbsp;VARCHAR(16)&nbsp;NOT&nbsp;NULL,
INDEX&nbsp;[indexName]&nbsp;(username(length))
);&nbsp;&nbsp;//删除索引的语法：
DROP&nbsp;INDEX&nbsp;[indexName]&nbsp;ON&nbsp;mytable;</pre><p><strong>（2）唯一索引</strong></p><p>它与前面的普通索引类似，不同的就是：索引列的值必须唯一，但允许有空值。如果是组合索引，则列值的组合必须唯一。它有以下几种创建方式：</p><ul class=" list-paddingleft-2" style="list-style-type: disc;"><li><p>创建索引</p></li></ul><pre class="brush:as3;toolbar:false">CREATE&nbsp;UNIQUE&nbsp;INDEX&nbsp;indexName&nbsp;ON&nbsp;mytable(username(length))&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//修改表结构
ALTER&nbsp;mytable&nbsp;ADD&nbsp;UNIQUE&nbsp;[indexName]&nbsp;ON&nbsp;(username(length))&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//创建表的时候直接指定
CREATE&nbsp;TABLE&nbsp;mytable(
ID&nbsp;INT&nbsp;NOT&nbsp;NULL,
username&nbsp;VARCHAR(16)&nbsp;NOT&nbsp;NULL,
UNIQUE&nbsp;[indexName]&nbsp;(username(length))
&nbsp;);</pre><p><strong>（3）主键索引</strong></p><p>它是一种特殊的唯一索引，不允许有空值。一般是在建表的时候同时创建主键索引：</p><pre class="brush:as3;toolbar:false">CREATE&nbsp;TABLE&nbsp;mytable(
ID&nbsp;INT&nbsp;NOT&nbsp;NULL,
username&nbsp;VARCHAR(16)&nbsp;NOT&nbsp;NULL,
PRIMARY&nbsp;KEY(ID)
);</pre><p>当然也可以用 ALTER 命令。记住：一个表只能有一个主键。<br/></p><p><strong>（4）组合索引</strong></p><p>为了形象地对比单列索引和组合索引，为表添加多个字段：</p><p>CREATE TABLE mytable(&nbsp;&nbsp; ID INT NOT NULL,&nbsp;&nbsp;&nbsp; username VARCHAR(16) NOT NULL,&nbsp;&nbsp; city VARCHAR(50) NOT NULL,&nbsp;&nbsp; age INT NOT NULL&nbsp; );&nbsp; 为了进一步榨取MySQL的效率，就要考虑建立组合索引。就是将 name, city, age建到一个索引里：</p><p>ALTER TABLE mytable ADD INDEX name_city_age (name(10),city,age); 建表时，usernname长度为 16，这里用 10。这是因为一般情况下名字的长度不会超过10，这样会加速索引查询速度，还会减少索引文件的大小，提高INSERT的更新速度。</p><p>如果分别在 usernname，city，age上建立单列索引，让该表有3个单列索引，查询时和上述的组合索引效率也会大不一样，远远低于我们的组合索引。虽然此时有了三个索引，但MySQL只能用到其中的那个它认为似乎是最有效率的单列索引。</p><p>建立这样的组合索引，其实是相当于分别建立了下面三组组合索引：</p><p>usernname,city,age&nbsp;&nbsp; usernname,city&nbsp;&nbsp; usernname&nbsp; 为什么没有 city，age这样的组合索引呢？这是因为MySQL组合索引“最左前缀”的结果。简单的理解就是只从最左面的开始组合。并不是只要包含这三列的查询都会用到该组合索引，下面的几个SQL就会用到这个组合索引：</p><p>SELECT * FROM mytable WHREE username=&quot;admin&quot; AND city=&quot;郑州&quot;&nbsp; SELECT * FROM mytable WHREE username=&quot;admin&quot; 而下面几个则不会用到：</p><p>SELECT * FROM mytable WHREE age=20 AND city=&quot;郑州&quot;&nbsp; SELECT * FROM mytable WHREE city=&quot;郑州&quot;</p><p><strong>（5）建立索引的时机</strong></p><p>到这里我们已经学会了建立索引，那么我们需要在什么情况下建立索引呢？一般来说，在WHERE和JOIN中出现的列需要建立索引，但也不完全如此，因为MySQL只对&lt;，</p><p>SELECT t.Name&nbsp; FROM mytable t LEFT JOIN mytable m&nbsp;&nbsp;&nbsp; ON t.Name=m.username WHERE m.age=20 AND m.city=&#39;郑州&#39; 此时就需要对city和age建立索引，由于mytable表的userame也出现在了JOIN子句中，也有对它建立索引的必要。</p><p>刚才提到只有某些时候的LIKE才需建立索引。因为在以通配符%和_开头作查询时，MySQL不会使用索引。例如下句会使用索引：</p><p>SELECT * FROM mytable WHERE username like&#39;admin%&#39; 而下句就不会使用：</p><p>SELECT * FROM mytable WHEREt Name like&#39;%admin&#39; 因此，在使用LIKE时应注意以上的区别。</p><p><strong>（6）索引的不足之处</strong></p><p>上面都在说使用索引的好处，但过多的使用索引将会造成滥用。因此索引也会有它的缺点：</p><p>◆虽然索引大大提高了查询速度，同时却会降低更新表的速度，如对表进行INSERT、UPDATE和DELETE。因为更新表时，MySQL不仅要保存数据，还要保存一下索引文件。</p><p>◆建立索引会占用磁盘空间的索引文件。一般情况这个问题不太严重，但如果你在一个大表上创建了多种组合索引，索引文件的会膨胀很快。</p><p>索引只是提高效率的一个因素，如果你的MySQL有大数据量的表，就需要花时间研究建立最优秀的索引，或优化查询语句。</p><p><strong>（7）使用索引的注意事项</strong></p><p>使用索引时，有以下一些技巧和注意事项：</p><p>◆索引不会包含有NULL值的列</p><p>只要列中包含有NULL值都将不会被包含在索引中，复合索引中只要有一列含有NULL值，那么这一列对于此复合索引就是无效的。所以我们在数据库设计时不要让字段的默认值为NULL。</p><p>◆使用短索引</p><p>对串列进行索引，如果可能应该指定一个前缀长度。例如，如果有一个CHAR(255)的列，如果在前10个或20个字符内，多数值是惟一的，那么就不要对整个列进行索引。短索引不仅可以提高查询速度而且可以节省磁盘空间和I/O操作。</p><p>◆索引列排序</p><p>MySQL查询只使用一个索引，因此如果where子句中已经使用了索引的话，那么order by中的列是不会使用索引的。因此数据库默认排序可以符合要求的情况下不要使用排序操作；尽量不要包含多个列的排序，如果需要最好给这些列创建复合索引。</p><p>◆like语句操作</p><p>一般情况下不鼓励使用like操作，如果非使用不可，如何使用也是一个问题。like “%aaa%” 不会使用索引而like “aaa%”可以使用索引。</p><p>◆不要在列上进行运算</p><p>select * from users where YEAR(adddate)&lt;2007; 将在每个行上进行运算，这将导致索引失效而进行全表扫描，因此我们可以改成</p><p>select * from users where adddate&lt;‘2007-01-01’;&nbsp;</p><p>◆不使用NOT IN和&lt;&gt;操作</p><p>以上，就对其中MySQL索引类型进行了介绍。</p>','网络','admin','1','0','0','1523086675','502','0','127.0.0.1');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('52','11','OSX 每天自动获取 Bing 壁纸','自从换了 mac 之后，直观的感觉是桌面变的更空旷了（本来我就不喜欢在桌面放东西）。所以，壁纸成了装点桌面的唯一工具。正好 Bing 的背景图片每天都会换一张，并且几乎每张图片都让人感觉非常大气，所以就萌生了用 Bing 图片做壁纸的想法（话说让我知道 Bing 每天都会换背景图，还是因为之前做 Kratos 的时候有小伙伴提出网页的背景图能不能调用 Bing 的接口 。我先看了一下 Apple Store 里面有没有这种 App，果不其然还真有','/uploads/20180412\db5160ccde642cb70457120822262fd8.jpg','<p><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);">自从换了 mac 之后，直观的感觉是桌面变的更空旷了（本来我就不喜欢在桌面放东西）。所以，壁纸成了装点桌面的唯一工具。正好 Bing 的背景图片每天都会换一张，并且几乎每张图片都让人感觉非常大气，所以就萌生了用 Bing 图片做壁纸的想法（话说让我知道 Bing 每天都会换背景图，还是因为之前做 Kratos 的时候有小伙伴提出网页的背景图能不能调用 Bing 的接口 </span><img src="https://www.vtrois.com/wp-content/themes/volcano/static/images/smilies/lol.png" alt=":lol:" class="wp-smiley" width="50" height="50" title=":lol:"/><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);"> ）。我先看了一下 Apple Store 里面有没有这种 App，果不其然还真有，但是这价格的话，emmmmm 算了还是自己整吧。</span></p><p><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);"></span></p><h2 class="h2">利弊分析</h2><p><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);"></span></p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; white-space: pre-line; line-height: 1.875rem; color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; background-color: rgb(255, 255, 255);">Bing 的图片有 4 种像素，分别是 1920x1200、1920x1080、800x480、400x240，而 mac 可以支持到 2880 x 1800，所以在视觉上看着壁纸不是非常的清晰的（系统自带的壁纸是 5120 × 3684）。</p><p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; white-space: pre-line; line-height: 1.875rem; color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; background-color: rgb(255, 255, 255);">这里制作的自动化脚本是每次开机启动的，如果一天开机好几次的话，脚本会自带检测是否已经下载过图片，从而不会重复下载。</p><h2 class="h2">战前准备</h2><ul style="list-style-type: square;" class=" list-paddingleft-2"><li><p>MAC 中自带的 Automator</p></li><li><p><a href="https://github.com/thejandroman/bing-wallpaper" target="_blank" style="box-sizing: border-box; color: rgb(0, 162, 255); text-decoration-line: none; background-color: transparent;">bing-wallpaper</a>&nbsp;脚本</p></li><li><p>充满智慧的大脑&nbsp;<img src="https://www.vtrois.com/wp-content/themes/volcano/static/images/smilies/razz.png" alt=":razz:" class="wp-smiley" width="50" height="50" title=":razz:"/></p></li></ul><h2 class="h2">战斗开始</h2><p><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);">1、首先我们知道 Bing 图片是有一个接口的，也就是 https://cn.bing.com/HPImageArchive.aspx?format=js&amp;n=1 ，他的主要内容如下，这里我们需要的是第 7 行 </span><code style="box-sizing: border-box; font-family: SFMono-Regular, Menlo, Monaco, Consolas, &quot;Liberation Mono&quot;, &quot;Courier New&quot;, monospace; font-size: 14.4px; color: rgb(51, 51, 51); word-break: inherit; margin: 0px 0.1875rem; padding: 0.125rem 0.25rem; border-radius: 0.25rem; background-color: rgb(239, 240, 241); white-space: pre-line;">url</code><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);">，也就是图片的地址，所以在脚本中只要拉这个 </span><code style="box-sizing: border-box; font-family: SFMono-Regular, Menlo, Monaco, Consolas, &quot;Liberation Mono&quot;, &quot;Courier New&quot;, monospace; font-size: 14.4px; color: rgb(51, 51, 51); word-break: inherit; margin: 0px 0.1875rem; padding: 0.125rem 0.25rem; border-radius: 0.25rem; background-color: rgb(239, 240, 241); white-space: pre-line;">url</code><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);"> 地址即可实现需求。</span></p><pre class="brush:as3;toolbar:false">{
&nbsp;&nbsp;images:&nbsp;[
	&nbsp;&nbsp;{
		startdate:&nbsp;&quot;20180315&quot;,
		fullstartdate:&nbsp;&quot;201803151600&quot;,
		enddate:&nbsp;&quot;20180316&quot;,
		url:&nbsp;&quot;/az/hprichbg/rb/WolongPanda_ZH-CN10957042976_1920x1080.jpg&quot;,
		urlbase:&nbsp;&quot;/az/hprichbg/rb/WolongPanda_ZH-CN10957042976&quot;,
		copyright:&nbsp;&quot;卧龙国家级自然保护区的大熊猫，中国四川&nbsp;(©&nbsp;Lynn&nbsp;M.&nbsp;Stone/Minden&nbsp;Pictures)&quot;,
		copyrightlink:&nbsp;&quot;http://www.bing.com/search?q=%E5%A4%A7%E7%86%8A%E7%8C%AB&amp;form=hpcapt&amp;mkt=zh-cn&quot;,
		quiz:&nbsp;&quot;/search?q=Bing+homepage+quiz&amp;filters=WQOskey:%22HPQuiz_20180315_WolongPanda%22&amp;FORM=HPQUIZ&quot;,
		wp:&nbsp;false,
		hsh:&nbsp;&quot;6fa3921773323a6e7f0f45447e548f91&quot;,
		drk:&nbsp;1,
		top:&nbsp;1,
		bot:&nbsp;1,
		hs:&nbsp;[&nbsp;]
		}
	],
&nbsp;&nbsp;tooltips:&nbsp;{
		loading:&nbsp;&quot;正在加载...&quot;,
		previous:&nbsp;&quot;上一个图像&quot;,
		next:&nbsp;&quot;下一个图像&quot;,
		walle:&nbsp;&quot;此图片不能下载用作壁纸。&quot;,
		walls:&nbsp;&quot;下载今日美图。仅限用作桌面壁纸。&quot;
	}
}</pre><p><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);"></span><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);">2、bing-wallpaper 是一个可以从 Bing 下载当天最新图片并将其保存到目录的脚本。</span></p><pre class="brush:as3;toolbar:false">#!/usr/bin/env&nbsp;bash
#&nbsp;shellcheck&nbsp;disable=SC1117
&nbsp;
readonly&nbsp;SCRIPT=$(basename&nbsp;&quot;$0&quot;)
readonly&nbsp;VERSION=&#39;0.4.0&#39;
readonly&nbsp;RESOLUTIONS=(1920x1200&nbsp;1920x1080&nbsp;800x480&nbsp;400x240)
&nbsp;
usage()&nbsp;{
cat&nbsp;&lt;&lt;EOF
Usage:
&nbsp;&nbsp;$SCRIPT&nbsp;[options]
&nbsp;&nbsp;$SCRIPT&nbsp;-h&nbsp;|&nbsp;--help
&nbsp;&nbsp;$SCRIPT&nbsp;--version
Options:
&nbsp;&nbsp;-f&nbsp;--force&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Force&nbsp;download&nbsp;of&nbsp;picture.&nbsp;This&nbsp;will&nbsp;overwrite
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;the&nbsp;picture&nbsp;if&nbsp;the&nbsp;filename&nbsp;already&nbsp;exists.
&nbsp;&nbsp;-s&nbsp;--ssl&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Communicate&nbsp;with&nbsp;bing.com&nbsp;over&nbsp;SSL.
&nbsp;&nbsp;-q&nbsp;--quiet&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Do&nbsp;not&nbsp;display&nbsp;log&nbsp;messages.
&nbsp;&nbsp;-n&nbsp;--filename&nbsp;&lt;file&nbsp;name&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;The&nbsp;name&nbsp;of&nbsp;the&nbsp;downloaded&nbsp;picture.&nbsp;Defaults&nbsp;to
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;the&nbsp;upstream&nbsp;name.
&nbsp;&nbsp;-p&nbsp;--picturedir&nbsp;&lt;picture&nbsp;dir&gt;&nbsp;&nbsp;The&nbsp;full&nbsp;path&nbsp;to&nbsp;the&nbsp;picture&nbsp;download&nbsp;dir.
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Will&nbsp;be&nbsp;created&nbsp;if&nbsp;it&nbsp;does&nbsp;not&nbsp;exist.
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;[default:&nbsp;$HOME/Pictures/bing-wallpapers/]
&nbsp;&nbsp;-r&nbsp;--resolution&nbsp;&lt;resolution&gt;&nbsp;&nbsp;&nbsp;The&nbsp;resolution&nbsp;of&nbsp;the&nbsp;image&nbsp;to&nbsp;retrieve.
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Supported&nbsp;resolutions:&nbsp;${RESOLUTIONS[*]}
&nbsp;&nbsp;-w&nbsp;--set-wallpaper&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Set&nbsp;downloaded&nbsp;picture&nbsp;as&nbsp;wallpaper&nbsp;(Only&nbsp;mac&nbsp;support&nbsp;for&nbsp;now).
&nbsp;&nbsp;-h&nbsp;--help&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Show&nbsp;this&nbsp;screen.
&nbsp;&nbsp;--version&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Show&nbsp;version.
EOF
}
&nbsp;
print_message()&nbsp;{
&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;[&nbsp;!&nbsp;&quot;$QUIET&quot;&nbsp;];&nbsp;then
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;printf&nbsp;&quot;%s\n&quot;&nbsp;&quot;${1}&quot;
&nbsp;&nbsp;&nbsp;&nbsp;fi
}
&nbsp;
#&nbsp;Defaults
PICTURE_DIR=&quot;$HOME/Pictures/bing-wallpapers/&quot;
RESOLUTION=&quot;1920x1080&quot;
&nbsp;
#&nbsp;Option&nbsp;parsing
while&nbsp;[[&nbsp;$#&nbsp;-gt&nbsp;0&nbsp;]];&nbsp;do
&nbsp;&nbsp;&nbsp;&nbsp;key=&quot;$1&quot;
&nbsp;
&nbsp;&nbsp;&nbsp;&nbsp;case&nbsp;$key&nbsp;in
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-r|--resolution)
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;RESOLUTION=&quot;$2&quot;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;shift
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;;;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-p|--picturedir)
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;PICTURE_DIR=&quot;$2&quot;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;shift
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;;;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-n|--filename)
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;FILENAME=&quot;$2&quot;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;shift
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;;;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-f|--force)
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;FORCE=true
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;;;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-s|--ssl)
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;SSL=true
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;;;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-q|--quiet)
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;QUIET=true
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;;;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-h|--help)
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;usage
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;exit&nbsp;0
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;;;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-w|--set-wallpaper)
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;SET_WALLPAPER=true
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;;;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;--version)
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;printf&nbsp;&quot;%s\n&quot;&nbsp;$VERSION
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;exit&nbsp;0
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;;;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*)
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(&gt;&amp;2&nbsp;printf&nbsp;&quot;Unknown&nbsp;parameter:&nbsp;%s\n&quot;&nbsp;&quot;$1&quot;)
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;usage
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;exit&nbsp;1
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;;;
&nbsp;&nbsp;&nbsp;&nbsp;esac
&nbsp;&nbsp;&nbsp;&nbsp;shift
done
&nbsp;
#&nbsp;Set&nbsp;options
[&nbsp;$QUIET&nbsp;]&nbsp;&amp;&amp;&nbsp;CURL_QUIET=&#39;-s&#39;
[&nbsp;$SSL&nbsp;]&nbsp;&nbsp;&nbsp;&amp;&amp;&nbsp;PROTO=&#39;https&#39;&nbsp;&nbsp;&nbsp;||&nbsp;PROTO=&#39;http&#39;
&nbsp;
#&nbsp;Create&nbsp;picture&nbsp;directory&nbsp;if&nbsp;it&nbsp;doesn&#39;t&nbsp;already&nbsp;exist
mkdir&nbsp;-p&nbsp;&quot;${PICTURE_DIR}&quot;
&nbsp;
#&nbsp;Parse&nbsp;bing.com&nbsp;and&nbsp;acquire&nbsp;picture&nbsp;URL(s)
read&nbsp;-ra&nbsp;urls&nbsp;&lt;&nbsp;&lt;(curl&nbsp;-sL&nbsp;$PROTO://www.bing.com&nbsp;|&nbsp;\
&nbsp;&nbsp;&nbsp;&nbsp;grep&nbsp;-Eo&nbsp;&quot;url:&#39;.*?&#39;&quot;&nbsp;|&nbsp;\
&nbsp;&nbsp;&nbsp;&nbsp;sed&nbsp;-e&nbsp;&quot;s/url:&#39;\([^&#39;]*\)&#39;.*/$PROTO:\/\/bing.com\1/&quot;&nbsp;|&nbsp;\
&nbsp;&nbsp;&nbsp;&nbsp;sed&nbsp;-e&nbsp;&quot;s/\\\//g&quot;&nbsp;|&nbsp;\
&nbsp;&nbsp;&nbsp;&nbsp;sed&nbsp;-e&nbsp;&quot;s/\([[:digit:]]*x[[:digit:]]*\)/$RESOLUTION/&quot;)
&nbsp;
for&nbsp;p&nbsp;in&nbsp;&quot;${urls[@]}&quot;;&nbsp;do
&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;[&nbsp;-z&nbsp;&quot;$FILENAME&quot;&nbsp;];&nbsp;then
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;filename=$(echo&nbsp;&quot;$p&quot;|sed&nbsp;-e&nbsp;&quot;s/.*\/\(.*\)/\1/&quot;)
&nbsp;&nbsp;&nbsp;&nbsp;else
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;filename=&quot;$FILENAME&quot;
&nbsp;&nbsp;&nbsp;&nbsp;fi
&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;[&nbsp;$FORCE&nbsp;]&nbsp;||&nbsp;[&nbsp;!&nbsp;-f&nbsp;&quot;$PICTURE_DIR/$filename&quot;&nbsp;];&nbsp;then
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;print_message&nbsp;&quot;Downloading:&nbsp;$filename...&quot;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;curl&nbsp;$CURL_QUIET&nbsp;-Lo&nbsp;&quot;$PICTURE_DIR/$filename&quot;&nbsp;&quot;$p&quot;
&nbsp;&nbsp;&nbsp;&nbsp;else
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;print_message&nbsp;&quot;Skipping:&nbsp;$filename...&quot;
&nbsp;&nbsp;&nbsp;&nbsp;fi
done
&nbsp;
if&nbsp;[&nbsp;$SET_WALLPAPER&nbsp;];&nbsp;then
&nbsp;&nbsp;&nbsp;&nbsp;/usr/bin/osascript&lt;&lt;END
tell&nbsp;application&nbsp;&quot;System&nbsp;Events&quot;&nbsp;to&nbsp;set&nbsp;picture&nbsp;of&nbsp;every&nbsp;desktop&nbsp;to&nbsp;(&quot;$PICTURE_DIR/$filename&quot;&nbsp;as&nbsp;POSIX&nbsp;file&nbsp;as&nbsp;alias)
END
fi</pre><p><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);"><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);">3、在脚本的第 39-40 行，是设置图片的保存位置以及图片的像素。这里默认的是最高像素，保存的位置为 </span><code style="box-sizing: border-box; font-family: SFMono-Regular, Menlo, Monaco, Consolas, &quot;Liberation Mono&quot;, &quot;Courier New&quot;, monospace; font-size: 14.4px; color: rgb(51, 51, 51); word-break: inherit; margin: 0px 0.1875rem; padding: 0.125rem 0.25rem; border-radius: 0.25rem; background-color: rgb(239, 240, 241); white-space: pre-line;">/Pictures/bing-wallpapers/</code><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);"> 这个文件夹中，下图是这段时间我电脑里拉到的一些图片。</span></span><br/></p><p><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);"><img src="https://www.vtrois.com/wp-content/uploads/bing-wallpaper-1.png" width="700" height="427" title="" alt=""/></span></p><p><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);"><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);">4、将修改好的脚本添加到 Automator 的工作流程中，左边选择“运行 Shell 脚本”</span></span></p><p><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);"><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);"><img src="https://www.vtrois.com/wp-content/uploads/bing-wallpaper-2.png" width="700"/></span></span></p><p><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);"><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);"><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);">5、将保存好的工作流程添加到“登录项”中，从而实现开机自动运行</span></span></span></p><p><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);"><img src="https://www.vtrois.com/wp-content/uploads/bing-wallpaper-3.png" width="699" height="537" title="" alt=""/></span></p><p><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);"><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);">6、最后，我们设置桌面背景，添加壁纸所在的文件夹，然后再勾选更改图片的频率为每天</span></span></p><p><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);"><span style="color: rgb(74, 74, 74); font-family: Gotham-Book, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Ubuntu, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft Yahei UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Source Han Sans CN&quot;, sans-serif; white-space: pre-line; background-color: rgb(255, 255, 255);"><img src="https://www.vtrois.com/wp-content/uploads/bing-wallpaper-4.png" width="700"/></span></span></p>','','网络','1','0','0','1523518461','510','0','127.0.0.1');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('9','10','决别辞(组诗)','八月的风，刀光剑影 狂吹向蔽处的森林，我遍体鳞伤 将破碎的心，浸入水底 一再沉潜，直至寒冰彻骨 水之湄，有人在唱歌……','http://china.kids21.cn/sxwqn/q/201105/W020110530429095306030.jpg','◆八月，穷途末路 <br /><br /> <p> 八月的风，刀光剑影<br /> 狂吹向蔽处的森林，我遍体鳞伤 </p> <br /> <p> 将破碎的心，浸入水底<br /> 一再沉潜，直至寒冰彻骨 </p> <br /> <p> 水之湄，有人在唱歌<br /> “虞兮虞兮，奈若何？”<br /> 噫！这可是乌江 </p> <br /> <p> 虞姬虞姬，那夜你流下的清泪里<br /> 我听到了大鹏断翅的声音 </p> <br /> <p> 八月，穷途末路 </p> <br /> ◆远走天涯 <br /><br /> <p> 我的草原，绿意匮乏，牛羊失散<br /> 在八月的河床，我听见月光在尖叫 </p> <br /> <p> 日早落，路犹远<br /> 你说，你还是喜欢那个沾满露水的清晨<br /> 而我，早已垂垂老矣 </p> <br /> <p> 上帝的马车来去匆匆，灯盏一路向北<br /> 我只好携上自己的影子，攥紧言语<br /> 赶在黎明到来之前<br /> ---远走天涯 </p> <br /> ◆诀别辞 <br /><br /> <p> 在黎明到来之前，我们不说诀别<br /> 此时，我手捧茶杯<br /> 将莫名的心绪连同茶水独自饮尽 </p> <br /> <p> 星空下，一曲离歌走向湖面<br /> 湖水澄清，波澜不惊<br /> 这样也好，少了一些落寞的脚步 </p> <br /> <p> 远方将一行足迹点燃，一切万事俱备<br /> 只欠你将“诀别”的东风吹起 </p> <br /> <p> 在露台上，将诀别二字捻了三捻 </p> <br /> <p> 八月的心湖，瞬间崩堤<br /> 一勾残月挂在柳枝上，跟着晃了三晃 </p>','诗歌','王赤清','0','0','0','1457884270','881','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('10','10','江南的雨，总是忧郁的','江南的雨，总是忧郁的 淅淅沥沥，从晚唐起身一路走到今天，似乎不把内心 掏空，他就不会放弃  窗外的风景，如在画纸上走笔 泼墨的山水、烟波、寺院、小桥、雨伞和著旗袍的女子 一如此刻，也是……','http://img2.zol.com.cn/product/91_940x705/332/ceZ5vi8PAJ9Zo.jpg','<p><img width="90%" src="http://img2.zol.com.cn/product/91_940x705/332/ceZ5vi8PAJ9Zo.jpg"/> </p><p>	江南的雨，总是忧郁的<br/>淅淅沥沥，从晚唐起身一路走到今天，似乎不把内心<br/>掏空，他就不会放弃<br/><br/>窗外的风景，如在画纸上走笔<br/>泼墨的山水、烟波、寺院、小桥、雨伞和著旗袍的女子<br/>一如此刻，也是潮湿的<br/><br/>笛音在诗行里走了一半，便沉陷湖底<br/>燕子在房檐下，啁啾一声，与我<br/>对视一眼，然后撇下无限的空旷、寂寥和暗灰色<br/><br/>一季江湖，在江南的雨中，还来不及叙述<br/>便已草草结局<br/>在江南的雨中行走，犹未及的抽身<br/>便淋得湿漉漉的。心事在书案上渐渐发霉<br/><br/>江南的雨，空空濛濛，总是忧郁的<br/>包括居住在江南的人<br/>也是忧郁的</p>','诗歌','王赤清','0','0','0','1458120032','743','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('11','11','转-[面试官说] 大公司HR最爱10个问题','1、请介绍一下你自己 这是外企常问的问题。一般人回答这个问题过于平常，只说姓名、年龄、爱好、工作经验，这些在简历上都有，其实，外企最希望知道的是求职者能否胜任工作，包括：最强的技能、最深入研究的知识……','/uploads/20180416\e9199ba09f49c1fdcd247ada8b099cfd.jpg','<p><img src="/uploads/20180416/e9199ba09f49c1fdcd247ada8b099cfd.jpg" width="450" height="300" alt="e9199ba09f49c1fdcd247ada8b099cfd.jpg"/><br/><strong>1、请介绍一下你自己</strong><br/>　　这是外企常问的问题。一般人回答这个问题过于平常，只说姓名、年龄、爱好、工作经验，这些在简历上都有，其实，外企最希望知道的是求职者能否胜任工作，包括：最强的技能、最深入研究的知识领域、个性中最积极的部分、做过的最成功的事、主要的成就等。这些都可以和学习无关，也可以和学习有关，但要突出积极的个性和做事的能力，说的合情合理外企才会相信。外企很重视一个人的礼貌，求职者要尊重考官，在回答每个问题之后都说一句“谢谢”。外企喜欢有礼貌的求职者。<br/><strong>2、在学校你最不喜欢的课程是什么？为什么？</strong><br/>　　这个问题外企不希望求职者直接回答“数学”、“体育”之类的具体课程，如果直接回答还说明了理由，不仅代表求职者对这个学科不感兴趣，可能还代表将来也回对要完成的某些工作没有兴趣。这个问题外企招聘者最想从求职者口中听到：我可能对个别科目不是特别感兴趣，但是正因为这样，我会花更多的时间去学习这门课程，通过学习对原本不感兴趣的科目也开始有了兴趣，对于本来就有兴趣的科目我自然学习得更认真，所以各门课的成绩较为平衡。通过这样的问题，外企可以找到对任何事情都很感兴趣的求职者。<br/><strong>3、说你最大的优缺点？</strong><br/>　　这个问题外戚问的问题的概率很大，通常不希望听到直接回答的缺点是什么等莫如国求职者说自己小心眼、爱忌妒人非常懒、脾气大、工作效率低，外企肯定不会录用你。外企喜欢求职者从自己的优点说起，中间加一些小缺点，最后再把问题转回到优点上，突出优点的部分。<br/><strong>4、你认为你在学校属于好学生吗？</strong><br/>　　外企的招聘者很精明，问这个问题可以试探出很多问题：如果求职者学习成绩好，就会说：“是的，我的成绩很好，所有的成绩都很优异。当然，其他方面包括实践经验、团队精神、沟通能力我也做的很好，应该说我是一个全面发展的学生。”如果求职者成绩不尽理想，便会说：“我认为是不是一个好学生的标准是多元化的……”有经验的招聘者一听就会明白，外企喜欢诚实的求职者。<br/><strong>5、说说你的家庭</strong><br/>　　外企面试时询问家庭问题不是非要知道求职者家庭的情况，探究隐私，外企不喜欢探究个人隐私，而是要了解家庭背景对求职者的塑造和影响。外企希望听到的重点也在于家庭对求职者的积极影响。外企相信，和睦的家庭关系对一个人的成长有潜移默化的影响。<br/><strong>6、说说你对行业、技术发展趋势的看法？</strong><br/>　　外企对这个问题和感兴趣，只有有备而来的求职者能够过关。求职者可以直接在网上查找你所申请的行业部门的信息，只有深入了解才能产生独特的见解。外企认为日最聪明的求职者是对所面试的公司预先了解很多，包括公司各个部门，发展情况，在面试回答问题的时候可以提到所了解的情况，外企欢迎进入企业的人是“知己”，而不是“盲人”。<br/><strong>7、就你申请的这个职位，你认为你还欠缺什么？</strong><br/>　　外企喜欢问求职者弱点，但精明的求职者一般不直接回答。他们希望看到这样的求职者：继续重复自己的优势，然后说：“对于这个职位和我的能力来说，我相信自己是可以胜任的，只是缺乏经验，这个问题我想我可以进入公司以后以最短的时间来解决，我的学习能力很强，我相信可以很快融入公司的企业文化，进入工作状态。”外企喜欢能够巧妙地躲过难题的求职者。<br/><strong>8、你期望的工资是多少？</strong><br/>　　外企的工资水平是很灵活的，何种能力拿何种工资。外企喜欢直率的人，但这个问题却不能正面回答，外企希望听到：“以我的能力和我的优势，我完全可以胜任这个职位，我相信我可以做的很好。但是贵公司对这个职位的描述不是很具体，我想还可以延后再讨论”外企欢迎求职者给其定薪的自由度，而不是咬准一个价码。<br/><strong>9、你能给公司带来什么？</strong><br/>　　外企很想知道未来的员工能为企业做什么，求职者应再次重复自己的优势，然后说：“就我的能力，我可以做一个优秀的员工在组织中发挥能力，给组织带来高效率和更多的收益”。外企喜欢求职者就申请的职位表明自己的能力，比如申请营销之类的职位，可以说：“我可以开发大量的新客户，同时，对老客户做更全面周全的服务，开发老客户的新需求和消费。”等等。<br/><strong>10、你还有什么问题吗？</strong><br/>　　外企的这个问题看上去可有可无，其实很关键，外企不喜欢说“没有问题”的人，因为其很重视员工的个性和创新能力。外企不喜欢求职者问个人福利之类的问题，如果有人这样问：贵公司对新入公司的员工有没有什么培训项目，我可以参加吗？或者说贵公司的晋升机制是什么样的？外企将很欢迎，因为体现出你对学习的热情和对公司的忠诚度以及你的上进心。<br/></p>','面试，web','网络 - kwooshung','1','0','0','1462339718','403','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('12','10','二胡里的时光','二胡一声幽咽，落叶满山川 岁月的骨骼铿锵作响，三千里的秋月 隐去了星空的暗疾…………','http://www.musicdu.com/Upload/2015New/11/10/6358274577005881762713411.jpg','<img src="http://www.musicdu.com/Upload/2015New/11/10/6358274577005881762713411.jpg" width="308" height="220" /><br /><br />二胡一声幽咽，落叶满山川<br />岁月的骨骼铿锵作响，三千里的秋月<br />隐去了星空的暗疾<br /><br />端坐于二胡的弦上，你送走了一个一个的魂灵<br />一棵树见证了春秋代序<br />可你并不知道，一棵树也见证了你的逃逸<br /><br />数年后，你在墓地凝望来路<br />也只仅凭一只孤独的鸟，拾起你遗落的二胡<br />将无限空间坐小。为谁<br />抒情、落款<br /><br />2011·08·22','诗歌','王赤清','0','0','0','1462326638','282','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('13','9','如何快速定位页面中复杂 CSS BUG 问题','对于快速定位，个人的经验处理一般如下（基本可以定位到我在 淘宝 遇到的 90% 以上的……','http://img10.baike.epwk.com/Uploads/20150303/54f54a3c6dad4.jpg','<img src="http://img10.baike.epwk.com/Uploads/20150303/54f54a3c6dad4.jpg" style="max-width:690px;" width="690" height="351" id="image_operate_60611462386157349"><br>　　对于快速定位，个人的经验处理一般如下（基本可以定位到我在 淘宝 遇到的 90% 以上的复杂 CSS BUG问题）：<br />&nbsp;&nbsp;1、检查页面的标签是否闭合不要小看这条，也许折腾了你两天都没有解决的 CSS BUG问题，却仅仅源于这里。毕竟页面的模板一般都是由开发来嵌套的，而他们很容易犯此类问题。快捷提示：可以用 Dreamweaver打开文件检查，一般没有闭合的标签，会黄色背景高亮。<br />&nbsp;&nbsp;2、样式排除法有些复杂的页面也许加载了 N 个外链 CSS 文件，那么逐个删除 CSS 文件，找到 BUG 触发的具体CSS 文件，缩小锁定的范围。对于刚才锁定的问题 CSS样式文件，逐行删除具体的样式定义，定位到具体的触发样式定义，甚至是具体的触发样式属性。<br />&nbsp;&nbsp;3、模块确认法有时候我们也可以从页面的 HTML 元素出发。删除页面中不同的 HTML 模块，寻找到触发问题的 HTML模块。<br />&nbsp;&nbsp;4、检查是否清除浮动其实有不少的 CSS BUG 问题是因为没有清除浮动造成的。养成良好的清除浮动的习惯是必要的，推荐使用无额外 HTML 标签的清除浮动的方法（尽量避免使用 overflow:hidden;zoom:1的类似方法来清除浮动，会有太多的限制性）。<br />&nbsp;&nbsp;5、检查 IE 下是否触发 haslayout很多的 IE 下复杂 CSS BUG 都与 IE 特有的 haslayout息息相关。熟悉和理解 haslayout 对于处理复杂的 CSS BUG 会事半功倍。推荐阅读 old9 翻译的 《On havinglayout》（如果无法翻越穿越伟大的 GFW，可阅读 蓝色上的转帖 ）快捷提示：如果触发了 haslayout，IE 的调试工具IE Developer Toolbar 中的属性中将会显示 haslayout 值为 -1。<br />&nbsp;&nbsp;6、边框背景调试法故名思议就是给元素设置显眼的边框或者背景（一般黑色或红色），进行调试。此方法是最常用的调试 CSS BUG的方法之一，对于复杂 BUG 依旧适用。经济实惠还环保.<br />','分享，web','清蝎子','0','0','0','1462414270','235','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('14','10','故事新编二则','“……却惊讶地发现水中的自己竟然是一只天鹅!”某天，妈妈给儿子讲丑小鸭的故事。儿子马上截口地问道：“妈妈，妈妈，这只丑小鸭是不是……','/uploads/20180416\5d47feccf89d20d635da042c1a3942b1.jpg','<p><img src="/uploads/20180416/5d47feccf89d20d635da042c1a3942b1.jpg" width="499" height="354" id="image_operate_10031462412398047" alt="5d47feccf89d20d635da042c1a3942b1.jpg"/><br/></p><p><span style="font-family: 宋体; line-height: 24px; background-color: rgb(253, 255, 248);">（一）</span><br/><span style="font-family: 宋体; line-height: 24px; background-color: rgb(253, 255, 248);">　　“……却惊讶地发现水中的自己竟然是一只天鹅!”某天，妈妈给儿子讲丑小鸭的故事。</span><br/><span style="font-family: 宋体; line-height: 24px; background-color: rgb(253, 255, 248);">　　儿子马上截口地问道：“妈妈，妈妈，这只丑小鸭是不是去整容了？”</span><br/><span style="font-family: 宋体; line-height: 24px; background-color: rgb(253, 255, 248);">　　“……”</span><br/><span style="font-family: 宋体; line-height: 24px; background-color: rgb(253, 255, 248);">（二）</span><br/><span style="font-family: 宋体; line-height: 24px; background-color: rgb(253, 255, 248);">　　北山愚公长叹说：“你的思想真顽固，顽固得无法改变，连孤儿寡妇都比不上。即使我死了，还有儿子在呀；儿子又生孙子，孙子又生儿子；儿子又有儿子，儿子又有孙子；子子孙孙无穷无尽，可是山却不会增高，还怕挖不平吗？”</span><br/><span style="font-family: 宋体; line-height: 24px; background-color: rgb(253, 255, 248);">　　春去秋来，年复一年，某天，穷尽愚公三十六代子孙，终于将太行、王屋挖开了一条小路。</span><br/><span style="font-family: 宋体; line-height: 24px; background-color: rgb(253, 255, 248);">　　一阵轰鸣声响，只见一位身穿西装的青年，睁着双眼，一脸不可思议之色。愚公三十六代孙也好奇的盯着面前青年，问道：“你谁啊？”接着又指了指青年座的铁疙瘩，道：“这是什么？”</span><br/><span style="font-family: 宋体; line-height: 24px; background-color: rgb(253, 255, 248);">　　青年半响才回过神，又仔细瞅了瞅眼前几位蓬首垢面活脱脱的原始人，回答说：“我是当年住在河湾智叟的后人，至于这铁疙瘩，不是有句话这样说：挖掘机学校哪家强,中国山东找蓝翔！”</span></p>','琐碎','王赤清','0','0','0','1462458068','236','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('15','11','一位投资老手告诉你熊市长什么样[转载]','熊市就是没有人谈股票。交易所只有打牌的老头老太。身边有股票的都是长套。炒股票的人被认为是败家的傻瓜。不是跌个不停，而是横盘几个月，再跌一次，横盘几个月，再跌一次。91年就有过熊市啦。那是你们想象……','http://img.imaibo.net/data/uploads/2016/0316/18/56e933b9d3dc9.jpg','<div class="article-body fr-view">           <p><img class="fr-dib fr-draggable" src="http://img.imaibo.net/data/uploads/2016/0316/18/56e933b9d3dc9.jpg" style="width: 363px;" data-status="1"></p><p>熊市就是没有人谈股票。交易所只有打牌的老头老太。身边有股票的都是长套。炒股票的人被认为是败家的傻瓜。不是跌个不停，而是横盘几个月，再跌一次，横盘几个月，再跌一次。<br><br>91年就有过熊市啦。那是你们想象不到的，股票天天跌，那时股票不多，发展（注：应该是深发展，现在的<a target="_blank" href="http://www.imaibo.net/stock/1000001 " data-stock-code="000001" data-stock-name="平安银行" data-stock-id="96" data-stock-market="2" class="data_stock_class">平安银行</a>）有一天一个小时跌了46%还多。有的股票在一周内的成交量为零。来了100个贼，被消灭了95个，剩下的成了精，死不了，活下来了。<br><br>熊市的初级阶段：经历股价的大幅上涨以后，很多股票长了几倍甚至几十倍，这时候市场上该炒的股票都炒了，该长得都长了，股价透支了未来几年的几十年的成长，100元50元的股票到处都是。</p><p>市场再也找不到做多的理由。股价开始下跌，而很多人还沉浸在赚钱的喜悦当中，认为这只不过是牛市的回调，可是赚钱却变得越来越困难。最后很多人终于忍无可忍，开始抛售股票，股价开始大幅下跌，进入熊市的中级阶段。<br><br>我只记得我2000年拿出自己存的3000元和家人给的7000元共一万大元入市，当时正值科网热潮，我全部买了<a target="_blank" href="http://www.imaibo.net/stock/1000909 " data-stock-code="000909" data-stock-name="数源科技" data-stock-id="944" data-stock-market="2" class="data_stock_class">000909</a><a target="_blank" href="http://www.imaibo.net/stock/1000909 " data-stock-code="000909" data-stock-name="数源科技" data-stock-id="944" data-stock-market="2" class="data_stock_class">数源科技</a>。刚开始赚了点，后来一直跌一直跌，我也懒得管了。直到2005年尾，我再查那个账户，发现只剩下两千多元的市值了。我现在还庆幸当时刚毕业出来做事没多久，没什么钱，不然的话可就亏大了!<br><br>最熊市的时候是交易所倒闭，炒股的人跳楼，市场很多人发誓只要解套就永不炒股。没有人愿意讨论股票，谁说自己炒股，就会被人当作生活无以为继，失业的赌徒、也像说自己在48元买了中石油一样，会被别人当作是傻子。很多人亏损超过70%，投资者天天骂政府。<br><br>熊市一句话---30元的跌到5块没人买。流通盘好几亿的票，每天成交10几万算多的，天天阴跌不反弹，一周波动还不够手续费，除非高买低卖。 熊市时，股民说的最多的一句话：“还是要靠勤劳致富啊”。<br><br>每次大牛市行情总会深套一批稀里糊涂的新股民，当这些新股民熬到下次大牛市时解套了（这时已变成老股民）又会再套一批新股民，牛熊交替，周而复始，牛很凶狠，牛长熊长。<br><br>2001年熊市开始的那段日子，那几天,大盘也是天天跌,后来一天比一天跌的快,跌的急,等你反应过来,自己的钱已经亏近3成。稳定一段后，大家都认为可能要有反弹了,可是几天内又跌了10%,后来大家都失去了信心,人人都出货,机构出,散户出,一连跌了两周。</p><p>不过当时波动还是比较小,两周也没有现在一天跌的多,这时大盘已经从2245点跌到了1500。后来国家看跌的太多,出了很多利好（股市长期的走势不是国家出利好这么简单，要是国家政策有用，后来就不会跌的那么多了）,股市开始反弹了,只用了几天就涨到了1700点。</p><p>这时大家习惯性的开始大量抄底,认为回到前期高点是理所当然的事。后来几年证明，这些抄底的人基本上是亏的最多的。那些从高位跌下来的人因为短期跌幅巨大，大部出局,虽然跌了不少,但比起那帮抄底家伙只是小巫见大巫了。</p><p>大盘反弹在1700点后,横了一个月,一个冲高的假突破后，大盘再次杀跌，当时没有人再敢抄底了，因为谁也不敢想底在哪里。</p><p>十几天下来，大盘已经到了１３００。后来大盘出现了又一次的反弹，最后进入了最恐怖的阴跌时代：各股进一退二，平时各股波动只有２％。大盘股的日线就像心电图，涨一分跌一分，有时一天就出现２个价，不死不话的，就象冬天一样。</p><p>突然有几天大涨，这时千万不能追，后面的大跌一般是涨幅的三倍以上。然后又进入不死不话的行情，每周各股只跌个１%－２％，你一点不往心里去，一年下来你稀里糊涂就把钱亏了一半。</p><p>不过一年有一、两波行情，一般都是一个月内完成，快进快出的多，不然死的会很惨。一年下来，你会发现，大家都在亏，而且亏的很多，亏的在５０％以上的人占股民的８０％。</p><p>这时你只有死扛，因为卖了剩下的钱已经没有什么意义了。几年下来，你的钱只剩下了零头．这是典型的中国式大熊市，从２００１年到２００５年初的这个时期我几乎陪了９０％。<br><br>我记得03年有个股票一天就成交了两手。还一次看到说一支股票怕没有成交量，庄家派了两个操盘手自买自卖，如果其中一个操盘手有天感冒没上班，这支股票就没有成交量了。<br><br>熊市就是原来5000平米的营业部变成500平米。无数的券商失踪。国泰和君安，申银和万国，南方证券，唐氏兄弟，国氏兄弟，朱耀明，太多了。<br><br>把您的电脑屏幕翻转180度看2006、2007年的走势图就知道什么叫大熊市了。<br><br>当没人愿意说自己是个股民的时候就是深度熊市了。<br><br>满仓15块的成本最后跌成了4块钱，去年准备重操就业，可翻遍了整个屋子都找不到交易卡，电话委托的号码也变成了空号，其实就算打通了我也想不起密码来，跑到以前的交易所居然变成了卖橱柜的，原来的证券公司搬到了卖橱柜那栋楼背后的居民区的二楼小平台上，就只有两个工作人员在那里办公。<br><br>那时还在证券公司上班，印象最深的就是成交量缩到不能缩，许多票分时基本是一条直线，交易大厅基本无人，资金基本为零，工作基本无事，整天业务培训，要不就是打扫卫生，周围无人谈股票。<br><br>熊市就是你做高抛低吸时，如果是先高抛，那99％以上能低吸回来；如果是先低吸，那90％以上就被套住了。<br><br>还忘了一点，股民都是深度套牢，腰斩很正常，更惨的20-30跌到几块。现在许多人说被套牢，其实也就是10-20％，背点的30％多，套是套了，但绝对没牢。<br><br>熊市最初让你快速套牢20%，之后就是漫漫阴跌! 股票不会轻易跌停，但每天1%-3%的跌幅让你感觉生不如死!利好出台当利空!你看不见希望!<br><br>01年跌到1800我让朋友快离场，他说要反弹了再看看。后来我急了把他账户要过来直接全部砍仓。到1300的时候他还专门谢谢我来着。<br><br>熊市从刚开始到结束：以一只10元的股票为例，跌三元反弹一元，再跌三元再反弹一元，然后直接跌到三元，最后一元....这时老熊快睡了，但十分之九的人已经牺牲了。</p><p>熊市证券公司倒闭很常见呀，南方证券那么大的公司不是倒了吗，我开户的大鹏证券就倒闭了。我是在报纸上看到消息的，赶紧联系证券公司，好在<a target="_blank" href="http://www.imaibo.net/stock/1000783 " data-stock-code="000783" data-stock-name="长江证券" data-stock-id="107" data-stock-market="2" class="data_stock_class">长江证券</a>接手，资金没有损失，不过要重新过户。<br><br>印象不深了，开始跌的急，大家都不愿撤退,后来阴跌了，没办法，都不怎么关注了。再后来，开始猜测是不是真的到底了，开始抄底，我妈亏就亏在抄底抄在了熊市的山腰上。<br><br>证券公司大厅空无一人，除了工作人员已经没人。报纸的证券版越来越小，电视的证券节目全部都在深夜，新闻已经不再报道行情，因为天天都是跌。<br><br>每天出现的股评高手，说的都是如何杀跌换股，短线操作而不是持有。<br><br>每隔几周，都会出现新底，而且每次有人叫着抄底，接下来就继续地下跌，跌到最后没人说抄底为止。国家出台超过3个以上的利好，但是每次利好出现都会创新低，所有人对与消息都看成利空。<br><br>报纸出现“创多少多少年新低”“单日跌幅超过1929年大萧条美国股市”。。。<br>上证每天成交量只有50---100亿左右，不过当时200亿以上算天量了。呵呵，现在应该不容易见到了，并且国家不断出台一系列利好消息，可股市就不见涨。64的<a target="_blank" href="http://www.imaibo.net/stock/1600839 " data-stock-code="600839" data-stock-name="四川长虹" data-stock-id="948" data-stock-market="1" class="data_stock_class">四川长虹</a>跌到6.3（最低4元多）都没人接盘。<br><br>北京那个西北证券，物美超市边上，四环边上那个，有次我去，大厅里面有：椅子几排，屏幕一个，笤帚一个，保安一名，女营业员一名。见我来了，追上来问我你开户吗？开户吗？多少资金什么的。给我拿笔啊搬凳子啊，很热情。</p><p>下午人逐渐多了起来，有些老人拿着螃蟹蔬菜什么的和别人讨论超市里蔬菜的价格，有人打毛衣，有人讨论在国外的子女生活，还有人慢慢的就睡着了被保安叫醒了说关门下班了。</p><p>现在手里只有一张西北证券的卡，听说倒闭了还是怎么地被南京证券怎么地了。不过当时那个证券门口停着一辆红色的宝马，经常停在那里，朋友们都说，瞧，那是大户的，有钱人，90万才能上大户室。我的股票从10万到1万。<br><br>就拿最近的一次来说把：2000年到2005年，这期间，赠券公司倒闭了一半！股市日成交金额很多天低于40亿，<a target="_blank" href="http://www.imaibo.net/stock/1000878 " data-stock-code="000878" data-stock-name="云南铜业" data-stock-id="522" data-stock-market="2" class="data_stock_class">云南铜业</a>最低不到4块钱，那个时候，谁在证券公司上班，都不好意思跟别人说！<br><br>在熊市，经历快一半时，大部分人以为是正常调整，只有当熊市快结束时，大家才醒悟，妈的，老子遇到熊市了。</p><p>出差到外地，当时看行情很难，主要看报纸，看到路上一家营业部，心想正好去看看行情，推门进来，厅里没人，柜台里有两个小姐，用奇怪的眼光看我，我很疑惑，还以为走错地方，抬头一开屏幕，没错，能看行情，为着省电还是怎么的，营业部只放上海行情。<br><br>曾经有天见自己的票一个小时都没有成交，自己给它对冲一手弄出开盘价。也就是玩玩。后来知道这样做的人不止我一个，有一个玩得过火，一手就拉出个惊人升幅（具体多少不记得了），结果上了报纸，给交易所查了，后来也不知道怎么处理了。</p><p>我有一个科长，当时拿了5万元，买了2000股“达尔曼”，差不多25块钱一股。她一直没打理，就放那。结果，股票慢慢的阴跌，最后跌倒2快钱。5万变四千。最后，就这四千也没拿出来。“达尔曼”的董事长卷钱跑路了。“达尔曼”随后就退市了。现在在三板市场上也不知道怎么样了？？<br><br>我经历过两次熊市，现在还远远没有到底，但罗马不是一天建成的，熊市也不是一下就到的，熊市有个作为股民群体特征就是：等到有人开始用菜刀剁自己的手指头诅咒发誓说一辈子不碰股票了，那么那个时候熊市就进行了一半了。还有就是谁也不谈股票了，谈股票的都是败家子的象征，是脑子进水了。<br><br>我2001年25元买的<a target="_blank" href="http://www.imaibo.net/stock/1600776 " data-stock-code="600776" data-stock-name="东方通信" data-stock-id="957" data-stock-market="1" class="data_stock_class">东方通信</a>，最后一次看的时候是1.97元。现在大家还沉浸在牛市习惯性思维中，再过二个月，估计没人敢抄底了，新股民资金腰斩的人数会越来越庞大。</p><p>股票天天跌！股评家就是衰神的带名词！交易所里个个面如死灰。不炒股的朋友和亲人都把你看成败家子、赌徒。所谓技术派人士分析出来的铁底一个接一个被打穿炒股的朋友越来越少，这个离婚了。那个有点神经了。我清楚的记得当时的希望之星出现后。好多老股民买票时手指在操作机上不停的发抖！<br><br>2006年重新回来，第一次买股，真的是手抖发颤。买过之后还心有余悸。<br><br>唉。一把辛酸泪。<br><br>我记得那时候经常开盘半小时后我的股票还没出来开盘价，以为是停牌，上交易所网站查半天没发现停牌公告，才知道是没有委托下单不是停牌，很多人从大户室被踢到中户市，再从中户室一脚踢出来。熊市是过后的牛市对之前的提法，熊市过程中当时提的都是‘平衡市’，‘猴市’、‘中级调整’之类，很少直接说是熊市。<br><br>我01年进的，一开始还看看，后来不看了。再后来，我已经忘了还有股票了。<br><br>92年那时候的大跌我没有经历，我的老师倒是抗住了，按照他的说法,“每个昨天都是新高”。<br><br>熊市还有一个特征，就是政府不断出利好，市场只当是放屁。那时侯券商每天佣金不够交电费的。<br><br>熊市开始的一个标志就是政府开始减印花税了。<br><br>熊市走法如同现在的<a target="_blank" href="http://www.imaibo.net/stock/1000002 " data-stock-code="000002" data-stock-name="万科A" data-stock-id="1468" data-stock-market="2" class="data_stock_class">000002</a>万科，<a target="_blank" href="http://www.imaibo.net/stock/1601600 " data-stock-code="601600" data-stock-name="中国铝业" data-stock-id="359" data-stock-market="1" class="data_stock_class">601600</a>中铝。一个台阶接着一个台阶下挫，近期的底部变成了暴跌之后下次再反弹的顶部。</p><p>某日偶然在报纸上看到有关股票的说法，就想学着抄股，而且还将资金分成3批，每下一个台阶抄底买进一批。在下第三个台阶之后我以为见真低了，第三批就满仓抄底了，没想到一开始就抄在熊腰上，短短几个月就跌了40%，全部套牢。我接手我妈的账户时，2个股票缩水80%，一个退市。<br><br>卖的时候基本都是一手一手的卖，还要喘半天气。没办法，一下挂出10来手，就怕盘口形成打压，股价就嗖嗖往下掉。我们的交易所在行情最低那天只来过两个人，一个是无业的股民躺在椅子上睡觉，还有一人前天刚从海南回老家(我们那里)，听说是搞了点钱一心想炒股。那天拎了三十万想入市，但看到交易所空无一人，吓得不敢买，跑回去了。后来不多久政府救市股市大涨，此人后悔莫及。<br><br>评注：在2007年牛市结束时，本人还是个菜鸟，对那次牛转熊没有经验，所以总是抱着幻想。直到有一天看到上面这篇没有署名的网络文章，才深深受到震撼，猛然醒悟，于是逃离熊市。从此，我就把该文留在身边，凡是当我春风得意、忘乎所以的时候，我就读读这篇文章，以提醒自己注意风险。今天，同样是面临熊市，我把这篇文章拿出来，献给大家，希望对大家认清熊市有帮助。另外，该文不知道最初的作者是谁，我没法署名，在此向该原作者表示感谢！</p><p><br></p><p>来源：雪球&nbsp;作者：拈花成佛        </div>','web,财经，投资','网络','1','0','0','1462461052','280','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('16','10','时间与异次元空间之狂想曲','在我们中国古代，流传着各种各样的传说。相传彭殇活了800多岁，老子活了200余岁。而在西方神话中，亚当活了930年，而亚当的儿……','http://image.bitauto.com/dealer/news/100050600/1f6866ce-c8ea-4437-bdda-cf57e55ddaf3.jpg','<p><img src="http://pic9.nipic.com/20100902/3320946_174440259795_2.jpg" width="650" height="454" title="" alt=""/></p><p>　　在我们中国古代，流传着各种各样的传说。相传彭殇活了800多岁，老子活了200余岁。而在西方神话中，亚当活了930年，而亚当的儿子塞特活了912年。无论那个民族，总流传着许许多多关于长生之说的美丽故事。</p><p>　　当时间一天天流逝，人们也一天天变老，从四肢在地上爬，到后来拄着拐杖，人们终于意识到时间的可怕。他们渴望人生得以永驻，生命可以与天地长存。然而到得最后，他们还是永归尘土。就连佛教中的佛也不例外，在《地藏菩萨本愿经》中有记：一切智成就如来，其佛寿命六万劫。对于生、对于死，有人豁达，有人悲观，也有转生之说。也有那极狂之人，想求长生，其如李·克斯特伯爵夫人，她用纯洁少女的鲜血沐浴，每次洗澡前，她还要饮下血液，她洗一次澡，至少要杀掉两个少女。这就是欧洲著名的“血腥玛丽”的故事。而在中国，也有很多名皇帝想要长生，最后中毒身死。而造成这一切的——就是时间。</p><p>　　时间，看不见，摸不着，但他确然存在。何谓时间？维基百科中对时间的定义是这样的：时间是指宏观一切具有不停止的持续性和不可逆性的物质状态的各种变化过程，其有共同性质的连续事件的度量衡的总称。在著名物理学家阿尔伯特·爱因斯坦眼中，时间、与空间构成了宇宙的四维。他以此为据点，认为时间与空间相交的点，是异次元的入口。物理学家们认为，宇宙存在着“平行宇宙”。也有物理学家认为人是可以穿越过去未来。他们的立论就是——时间。</p><p>　　霍金认为，时光之旅在理论上是可行的。为此，据说他做了一个实验，邀请未来人参加他2009年6月28日的派对。但结果是，那日并没有未来人来参加他的派对。</p><p>　　那么，时间，是否是构成宇宙四维中的一个？异次元空间真的存在？时光之旅，真的如某些人说的，是因为因果论，而无法成行？</p><p>　　我们知道，宇宙，是从大爆炸开始的。有科学家认为，宇宙刚开始时是很小的，经过N年，N亿年后，宇宙从爆炸后，开始慢慢膨胀，而且行星之间的距离也是越来越远。银河系是宇宙中的一角，也许还不足一粒尘埃。我们人类生活在太阳系内，我们是一种生命，我们人类，对于整个宇宙来说，也许渺小的还不足原子大小。那么我们人类所谓的时间，对这个宇宙来说，是否正确呢？就像蚂蚁的观念，也许在他们蚂蚁的世界观中，是正确的。但于我们人类来说，他的世界观也是正观的吗？</p><p>　　我们人类的时间观，是从日起日落、人生人死、花谢花开中确立的，并且深信不凝。因为我们有参照物，当太阳从东边升起到西边落下，代表着一日；当月圆月缺，代表着一月过去了；当百花争妍到飞雪连天，代表着一年过去了。那么，假如有一天，这一切的参照物没了呢？假如有一天，地球停止转动呢？我们所参照物得来到时间还正确吗？</p><p>　　我们的一生，大约是几十年。但对于有的动物，却只有一日。他的早晨是童年，中午是壮年，自然，日暮就是晚年。他们对我们的时间，自然也就嗤之以鼻。但对于生命体来说，不管是人类，还是其它生命物种，时间，还是存在着的。区别也就在于所参照的各异。</p><p>　　但对于恒星，石头来说，时间就是可笑的了。他们不知生，不知死，只是在这个居大的宇宙空间里毫无意识的运动。时间对他们来说，只是某时，他在A点，到另一时间段，他在B点而已。宇宙只是一个爆炸体，而后不断的膨胀、膨胀再膨胀。如果没有膨胀，一切都是静止的，时间，对于恒星就毫无意义。说是时间，不如说是宇宙膨胀的过程。</p><p>　　那么他膨胀的过程，是如何产生的呢？没有人知道。但不可否认，他是在发生大爆炸后产生的。也许是热胀产生的物理反应，也许是别的，谁知道呢！但此时我们是否可以得出一个结论，无论生命体，还是静物体，时间，也只是宇宙膨胀的过程。假如有一天，宇宙停止膨胀了，时间是否会停止？又或许，宇宙只是存在一物体的盒子里，有一天，宇宙膨胀，最后膨胀到挤爆了这个盒子，我们宇宙中的物，会是一个什么样呢？</p><p>　　我们下面做一个实验。拿二块磁铁，一块为1，一块为2。1磁铁放在A点，2磁铁放在别的地方，当两端N极靠近，2磁铁推动1磁铁，磁铁1从A点就会被推动，我们掌控2磁铁推动1磁铁，将1磁铁推动至B点。磁铁1从A点，移动到B点，就产生了过程，叫做移动过程。磁铁本是静止物，他自己不会产生时间，也不会产生移动过程，当外物影响了他，他才产生了移动过程。对于1磁铁自身来说，他无法从A点移动到B点。假如磁铁里面有个生命体，他生活在1磁铁里面，就像我们生活在宇宙中，那么那个生命体无认如何，他也无法作用于这个移动过程。</p><p>　　我们人类，能做用于宇宙膨胀的过程吗？答案肯定是否定的。我们无法作用于宇宙膨胀，也就无法作用于宇宙时间。宇宙膨胀就膨胀了，他不可能回到过去，就像实验1磁铁那样，他自身无法从B点回到A点。也就是说时间是往前走的，而且时间对于宇宙来说，也就像他膨胀的过程，不断发生，但他自已无法控制。同一个宇宙，时间的过去、现在、未来，也不是宇宙可控制的。而且宇宙只存在膨胀不断发生过程，说明时间线只有一条。那么自然，也就没有说什么过去来到现在，未来来到现在，跟本不存在时光旅行。</p><p>　　既然宇宙自身无法作用于这个宇宙膨胀过程，那么时间，很可能并不是宇宙的。就像上面实验，磁铁从A点到B点，这个移动过程，是外力作用下发生的。我们见过果实的腐烂过程，果实自己并没有时间，但他腐烂的过程是依据我们这个世界的时间而慢慢腐烂的。果实腐烂自己并没有时间，但时间作用于了这个腐烂的过程。宇宙也许自己没有时间，但大爆炸后，他的膨胀过程，宇宙外的时间作用于他了。而这个膨胀的过程，我们人类误以为是宇宙的的时间。果实腐烂后生出生命体，生命体以果实腐烂的过程为果实时间，这是错误的。</p><p>　　宇宙自身并没有时间，宇宙外的时间与宇宙内的空间相交的点，存在吗？所以异次元空间理念并不成立。也许这个宇宙有异次元空间，这个谁也不知道，但这个理念绝对错误。就像The
Cow in the field（空地上的奶牛）实验一样。也许奶牛还在，但农民看见牛奶在场地，并不是对的。</p><p>　　想要长生不老，就是与时间搏斗。现在我们还没办法从时间上着手来改变生命。那只好从其他方面着手，但我们还是无法做到与时间同存，长生不老，从现在看来，只好是个美丽的传说了。我们可以用科学来延长长命，但不是永无止境。听说现在医院，可以帮患者换很多的器官，这让我想起了另一个实验，The
Ship of Theseus（特修斯之船）。</p><p>　　这个实验讲：一艘在海上航行的船，只要一块木板腐烂了，它就会被替换掉，以此类推，直到所有的功能部件都不是最开始的那些了。问题是，最终产生的这艘船是否还是原来的那艘特修斯之船，还是一艘完全不同的船？</p><p>　　那么作为人类的你，当科技发达到可以换人体任何器官，那么，在漫长的岁月后，当你的器官全部被置换后，最后的你，还是你吗？</p>','时间,异次元,长生不老','清蝎子','0','0','0','1462517532','328','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('17','10','七绝·生日（新韵）','春去春来秋复秋，二十三载梦中流……','/uploads/20180416\1fb5cbbbfc22c17da17a76de9841e772.jpg','<p><img src="/uploads/20180416/1fb5cbbbfc22c17da17a76de9841e772.jpg" alt="1fb5cbbbfc22c17da17a76de9841e772.jpg"/></p><p>　　春去春来秋复秋，<br/>　　二十三载梦中流。<br/>　　千杯佳日饮须醉，<br/>　　喜鹊枝头语未休。<br/>　　<br/>　&nbsp;&nbsp;&nbsp;&nbsp;2008-11-6<span style="color: rgb(34, 34, 34); font-family: Consolas, ">&nbsp;·</span>于泉州</p>','诗歌','王赤清','0','0','0','1478618172','837','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('18','11','友情链接交换','网站经后可能不再变了，麻烦。现在开始接收友情连接互换，最好是个人博客类网站，如要……','/uploads/20180416/483d5bb71bf40c7435a09607deca3f7b.jpg','<p><img src="/uploads/20180416/483d5bb71bf40c7435a09607deca3f7b.jpg" alt="483d5bb71bf40c7435a09607deca3f7b.jpg"/></p><p>&nbsp;&nbsp;&nbsp;&nbsp;网站经后可能不再变了，麻烦。现在开始接收友情连接互换，最好是个人博客类网站，如果有愿意的，可以在下面留言，谢谢！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;另：有须要的也可以加我QQ群，大家互相交流学习，群号是：585285315</p>','琐碎','清蝎子','0','0','0','1478604359','966','9','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('19','9','Linux神奇漏洞：长按回车键70秒 Root权限到手','一般来说获取系统Root权限是很困难的，尤其是加密系统中，但西班牙安全研究员Hector Marco、Ismael RIPOll发现，Linux系统下只需按住回车键70秒钟，就能轻松绕过……','http://img.jrjimg.cn/2016/11/20161117220400032.jpg','<p>　　一般来说获取系统Root权限是很困难的，尤其是加密系统中，但西班牙安全研究员Hector Marco、Ismael RIPOll发现，Linux系统下只需按住回车键70秒钟，就能轻松绕过系统认证，拿到Root权限，进而远程控制经过加密的Linux系统。</p><p style="text-align:center;"><img alt="Linux神奇漏洞：长按回车键70秒 Root权限到手" src="http://img.jrjimg.cn/2016/11/20161117220400032.jpg"/></p><p>　　<strong>这个问题来自Linux标准磁盘加密程序Cryptsetup的一个漏洞（CVE-2016-4484），它处理密码输入错误的时候，会允许用户多次重试输入，直到错误超过93次，程序就会给用户一个带Root权限的shell（busybox）。</strong></p><p>　　也就是说，<strong>如果你重复93次输错密码，或者持续按回车键大概70秒，就能够获得root initramfs （initial RAM filesystem） shell。</strong></p><p>　　之后，你就可以复制、修改或者破坏整个硬盘，也可以使用网络传输数据。加密磁盘内容不会泄露，但是攻击者可以提升权限、盗取信息、发起DoS攻击等等。</p><p>　　关键是，<strong>这个漏洞攻击的成功率非常高</strong>，因为它不依赖特定系统或者配置。在图书馆、ATM机、机场、实验室等场景下，它特别有用，因为在这些场景下开机的过程受到加密保护，而对外交互只有键盘/鼠标。</p><p>　　如果你使用了基于Linux的云服务，还可以在没有物理接触的情况下远程处罚漏洞。</p><p style="text-align:center;"><img alt="Linux神奇漏洞：长按回车键70秒 Root权限到手" src="http://img.jrjimg.cn/2016/11/20161117220400261.png"/></p><p>　　<strong>这个漏洞影响范围很广，覆盖几乎所有的Linux发行版，包括Debian、Ubuntu、Fedora、Red Hat Enterprise Linux （RHEL）、SUSE，免疫的则有Arch Linux、Solus。</strong></p><p>　　如果你的系统存在此漏洞，官方还没有发布补丁，可以自行修改cryptroot文件：</p><p>　　# sed -i &#39;s/GRUB_CMDLINE_LINUX_DEFAULT=&quot;/GRUB_CMDLINE_LINUX_DEFAULT=&quot;panic=5 /&#39; /etc/default/grub</p><p>　　# grub-install</p><p style="text-align: center;"><img alt="Linux神奇漏洞：长按回车键70秒 Root权限到手" src="http://img.jrjimg.cn/2016/11/20161117220400427.png"/></p>','Linux','网络','1','0','0','1479715676','777','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('20','11','我所认识的历史','看到吧里有很多人说儒家怎样怎样了，其实是你没看明白历史。　先说三皇时代，当时是部落，没有国家。你的部……','/uploads/20180416\631c77e54f8cee658b4a34d89c7af794.jpg','<p><span style="font-family:宋体;line-height:24px;background-color:#FDFFF8;">看到吧里有很多人说儒家怎样怎样了，其实是你没看明白历史。</span><br/>先说三皇时代，当时是部落，没有国家。你的部落住东，我住西，老死不相往来。后来有个人，带着自己的部落走出了住地，到别的部落来，看到你家有好东西，于是说你应该每年给我多少物资，人家不给，于是打起来了。<br/>后来打赢了，发现打别人，比自己围猎更有嫌头，尝了甜头，于是他不停的打别人，时间长了，他就是大部落长。<br/>他死了，他儿子接了他的位置，仍是部落长。一代一代相传，后来偶然一次，部落长的女婿发觉当部落长也有意思，还比别人吃的好，睡的好，于是他自己做了部落长。别人都不服他啊，好了，又打。打的时间长了，于是出现了专门打架的，这就是军队的出现。<br/>有了军队，就出现了国家。那时还不叫国家，还是部落，各部落都须每年送物资给大部落长。一代一代的，成了习惯，后来因为有个大部落长太贪了，他还想更多，有人给不起，大部落长又来打，谁知他打输了，自己死了。于是人们猛然发觉，大部落长也不过如此，好，那我也当当这个大部落长。这样就出现了朝代更替。<br/>后来又更替了一次大部落长，大部落长觉得，这不安全，如果将来又有别的部落将我部落杀了，他来做大部落长，怎么办呢？于是出现了分封，他把跟着自己打架的能人，分封到别的部落去坐镇，叫封王。你替我看着他，别叫他将来带领着部落将我部落灭了。<br/>时间长了，他分封出去的后代，不听他后代的话了，凭什么我给你看着别人？好，那时交通不发达，我也来尝尝当部落长的滋味，要交物资，我给，你又不知道我这边情况，于是各各部落长如雨后春笋，这就是春秋。<br/>后来为了利益，各部落长互相打架，顾不得真正的大部落长了，是为战国。<br/>春秋战国，儒家跳了出来。因为他是支持真正大部落长的，看到天下大乱，于是怀念大部落长时的礼、乐、平和。为了支持真正的大部落长，他将古代各代更替美化了。但真正的大部落长因为弱小，没人理会。<br/>秦始皇一统天下，将别的所有部落长打败了，将六大部落长养在咸阳，不许回封地，他怕还有人不服他，于是他不停的带着军队巡视天下，震慑各部落长的亲戚。<br/>秦是代替了真正的大部落长，他并不像儒家美化说的那样，是禅让，而是通过武力。这就与政治不合了，于是秦皇杀了儒家许多人，这也是几千年后儒家得势，为报当日之仇，黑化了许多秦国。<br/>可惜秦的天下没坐几年，秦始皇死后，各部落长的亲戚不服，又起来反秦，自立为王。<br/>秦国亡后，项羽为了当部落长，仍旧分封各部落长剩下的亲戚。人心不足，亲戚大多，没分封到的，不服项羽的分配，后来被农民出身的刘氏得了天下。<br/>刘氏可不想后来人又有人来杀他后代，于是想出了一个办法，非刘姓不得封王。于是高祖大封刘氏为王，吕后不答应了，又改封吕氏为王。经过八王之乱，刘氏皇帝又想，不能这么来，于是封王仍是帝姓，但没有多少权力了，不许拥兵，不许建国。兵与权就落入别姓之手。自此，非刘姓不得封王变成了空壳。<br/>刘氏不是禅让得天下，所以他们对儒家也没兴趣，《史记》记载当年他们是以老子无为治天下。某一天，儒家眼看就要日暮西山，于是开始对刘氏说，我承认你的合法性，也帮你牧万民。这其间，刘氏发觉了禅让好啊，我不禅让，他就不是真正的部落长了，是非法的。于是刘氏提高了儒家的地位，后来曹丕不得不玩禅让的游戏。因为曹丕是禅让的，刘备是刘姓，于是儒家多以魏蜀为正统，而没东吴为正统之说。<br/>游戏一直这么走下去，朝代更替，兵权也是今天你家，明天我家，但儒家一直帮着统治者牧万民。直到宋朝，赵姓当了皇帝，儒家发展到了巅峰。赵氏看到唐代与周代何其相似，一个是封王，一个是节度使，把周和唐的天下给分了。他也想他赵家千秋万代啊，怎么办呢，赵氏盯上了儒家。他让儒家替他看着及打理宋，儒家果然有几把刷子，将宋打理的井井有条，蒸蒸日上，发了大财。外族人民看到宋有钱，眼红了，于是与宋打架了，可惜，儒家不是兵家，打架是他的弱项。打输了架，外族人民嚷着给钱，给钱。儒家及赵氏只能捏着鼻子认了。<br/>后来外族人民辽及金也跟着宋，学儒家治国，想发大财，也还行，国力上升了。可惜，后来他们都把自己文化弄没了，儒家在外族还没站稳，他们变成了无根之木，骑兵一到，辽金几乎是灰飞烟灭。大宋还能挡挡，没有立刻就死，这都是儒家的功劳。<br/>到了明代，朱也是用儒家治国，但朱祖不放心儒家打架能力，于是设置了卫制。因为宋明都是用儒家治国，国力都还行，但打架变成了弱项，所以宋明不像前朝，被内部势力刮分了，而是被外部势力攻破。<br/>到了清朝，儒家已经发展了几千年了，清仍是用儒治国，但他不放心儒家，于是抽掉了儒家的骨头，剩下了个皮囊，带着辫子。只帮着他治国，不须要他干别的了，军国大事，还是由他自己人办。儒家没骨头了，只好把眼睛转向了自己人。儒家所重视的正统也没处说了，只好变成了哑巴，这就是“避席畏闻文字狱，著书都为稻粱谋”，儒家变成了哑巴，没了骨头，这就是奴才相了嘛。主人说什么，就是什么，没了自己主意了，腰也弯了。<br/>所以那些骂儒家的童鞋们，儒家不是没骨头，不是奴才，而是自清后才这样的。你看看汉唐宋明，儒家不是都是没骨头的奴才，他们在外族铁蹄前，也曾面不变色。自汉用儒，中国多为世界经济、文化、军事之中心。而有意思的是，清还是被外国文化攻破了，而不是被儒家攻破的。<br/></p><p>		各朝各代谁都想着自己千秋万代，秦皇汉武，唐宗宋祖，为了千秋万代，他们想尽办法，但历史仍是前进，宋前中国多为内部所分而国崩，自宋后，多为外族而至国灭。	</p><p><img src="http://chiqinga.com/uploads/20180416/631c77e54f8cee658b4a34d89c7af794.jpg" alt="631c77e54f8cee658b4a34d89c7af794.jpg"/></p><p><img src="http://img.blog.163.com/photo/s0pSz24AdooiKo62rD-RSQ==/4587197695454096535.jpg" alt=""/></p>','历史','王赤清','0','0','0','1462577493','288','1','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('21','10','人间八月天','大风过后，人间已是八月　大地上的草木，有着浅秋的味道　但岭南不在此例　炎炎的风每天都滚过生活的窗口……','http://cdn103.img.lizhi.fm/audio_cover/2016/07/31/30333166014961543_320x320.jpg','<p>	<img src="http://cdn103.img.lizhi.fm/audio_cover/2016/07/31/30333166014961543_320x320.jpg" alt="" /> </p>大风过后，人间已是八月<br />大地上的草木，有着浅秋的味道<br />但岭南不在此例<br />炎炎的风每天都滚过生活的窗口<br /><br />八月，我在南方看书、写字、画隐去的容颜<br />一个人平淡的生活在陌生的城市<br />从此拒谈诗歌，拒谈热烈与是非<br />而之于一场雨，来或者不来<br />鱼仍旧在水中游，我在岸边走过<br /><br />在人间八月天<br />给远方的朋友发短信<br />“我看到一朵栀子花，将前朝的风起云涌<br />开成了淡淡的香。”','诗歌','王赤清','0','0','0','1463660398','336','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('22','10','秋去冬来',' 天空倾斜出一角，泻出一场白雨 覆水难收，秋天说去就去了　那时，我正端起茶杯小啜一口　三十六级佛塔，凉了苔藓……','/uploads/20180416\613a82340a917b9191a39596317109d7.jpg','<p><img src="/uploads/20180416/613a82340a917b9191a39596317109d7.jpg" alt="613a82340a917b9191a39596317109d7.jpg"/></p><p><span style="color:#444444;font-family:Tahoma, Helvetica, simsun, sans-serif;background-color:#FFFFFF;"> 天空倾斜出一角，泻出一场白雨</span><br/><span style="color:#444444;font-family:Tahoma, Helvetica, simsun, sans-serif;background-color:#FFFFFF;"> 覆水难收，秋天说去就去了</span><br/><span style="color:#444444;font-family:Tahoma, Helvetica, simsun, sans-serif;background-color:#FFFFFF;"> 那时，我正端起茶杯小啜一口</span><br/><br/><span style="color:#444444;font-family:Tahoma, Helvetica, simsun, sans-serif;background-color:#FFFFFF;"> 三十六级佛塔，凉了苔藓</span><br/><span style="color:#444444;font-family:Tahoma, Helvetica, simsun, sans-serif;background-color:#FFFFFF;"> 宝剑高系墓树。挥戈退日的故事终随茶水失了温度</span><br/><span style="color:#444444;font-family:Tahoma, Helvetica, simsun, sans-serif;background-color:#FFFFFF;"> 鸟雀还在杯沿打捞失足的笛音</span><br/><br/><span style="color:#444444;font-family:Tahoma, Helvetica, simsun, sans-serif;background-color:#FFFFFF;"> 三千里的辽阔，一草一木穿梭在唐宋的晨钟暮鼓</span><br/><span style="color:#444444;font-family:Tahoma, Helvetica, simsun, sans-serif;background-color:#FFFFFF;"> 你我的名字，终被落叶仔细地掩埋</span><br/><span style="color:#444444;font-family:Tahoma, Helvetica, simsun, sans-serif;background-color:#FFFFFF;"> 秋去冬来，无非是一些沸腾的日子</span><br/><span style="color:#444444;font-family:Tahoma, Helvetica, simsun, sans-serif;background-color:#FFFFFF;"> 沦陷进岁月的陶罐，然后</span><br/><span style="color:#444444;font-family:Tahoma, Helvetica, simsun, sans-serif;background-color:#FFFFFF;"> 任其荒芜、滋漫寂寥</span><br/><span style="color:#444444;font-family:Tahoma, Helvetica, simsun, sans-serif;background-color:#FFFFFF;"> 11·18</span></p>','诗歌','王赤清','0','0','0','1462693407','306','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('26','10','小白站长的生存法则——献给绝望的小白站长们','前言：有人的地方就有江湖，有江湖的地方就有纷争，网络也不例外，现在在网络上淘金的人越来越多了，有着一群小……','http://pic.baike.soso.com/p/20091004/bki-20091004041420-1839094966.jpg','　　前言：有人的地方就有江湖，有江湖的地方就有纷争，网络也不例外，现在在网络上淘金的人越来越多了，有着一群小白们前仆后继就犹如飞蛾扑火，只是最终在某个夜深人静的夜晚孤寂的放弃了自己的网站，落魄的不得不去转行。（我想这也是我不敢专职做站的原因吧）。虽然如此，可是又有着这样的一批新人揣着激动的心情，踌躇满志要在互联网上有一番作为，只是成功的能有几人？小白也想成功，也在努力的去成功，虽然距离成功还是有着很远的距离，但是我相信“走到尽头的人只要你回头看一眼，你就不后悔。”为了“不后悔”这三个字，什么宝典秘籍啥的小白可是看了一堆，现如今就向各位小白站长们分享下我目前掌握的心得。<br />&nbsp; &nbsp;小白站长你要忍得住寂寞<br />&nbsp; &nbsp;寂寞？没错，就是寂寞。站长要夜深人静的时候做数据，站长要通宵达旦的弄代码、推广什么的，这期间最直接面对的就是寂寞。寂寞，不仅仅是环境上的，更是心灵上的，因为一个网站的发展是一个过程，这样的一个过程中会用很多快乐以及多数的痛苦和悲伤。比如早上起来开电脑一看，网站的权重降了，排名降了，IP少了，流量减了......等等，作为一名标准的小白站长，你需要淡定，你的这份淡定就是寂寞演变出来的。只是，很多人一旦看到这些不好的数据就烦躁，烦躁了后就失去了建站的动力，没有动力的日子是悲哀的，网站没有方向，没有前进的动力，没有新的内容，恶性循环下就成了一个没有盼头的日子，于是乎，又一个小白站长还没有脱白就已经离去，退出了站长行业。<br />&nbsp; &nbsp;建站4年，身边这样的例子太多了，小白真心的希望来到小白基地的你能够有所收获，能够或多或少的学到些东西，而小白基地能给你带来一些小小的帮助，这对于小白来说，真的很欣慰！这里，我所说的“忍得住寂寞”就是想告诉各位小白站长对于网站的那些起起伏伏的数据要淡定，要习惯，要看得开。你要相信，只要你的方法是对的，那么你的方向就对了，成功对于你来说只是时间早晚的问题！少点抱怨，少点纠结，安安静静踏踏实实做站才是王道，百度不是某一家开的，为了使其利益最大化，它肯定是向着绝大多数人，所以不要想着去作弊，只有安静、踏实，好好的去做站才是王道！因为路还长着呢！<br />&nbsp; &nbsp;小白站长你要有一个健康的身体<br />&nbsp; &nbsp;在讲解之前，请各位站长将你的右手从鼠标上离开，去摸摸你的鼻子，感觉有油么？有过没有，那好吧，你继续。如果有，大哥，你该是时候休息，平时面对着电脑要小心辐射啊，身体的健康是第一位，谁都输不起！那我自己来说，我有很严重的头痛，关注我的微博的人都知道，我每个月有那么几天要出差，同时又有那么几天头痛的感觉要炸掉。说白了，身体差，如果让我超过15个小时对着电脑，我会直接喷鼻血，头痛的上楼梯都是一步步向上挪，生怕晃动让头更痛。如果现在你有一天15个小时是坐在电脑面前，眼睛是时常看着你的显示器，那小白友情提醒你，眼睛、颈椎、腰，还有最严重的辐射，这些你要时刻记住，身体健康高于一切数据财富！路很长，慢慢的一步步向前走，不要想着用身体的压榨去换取更多的发展，这不值得，一场持久战才刚刚开始，谁笑到最后才笑的最甜，别钱还没有赚到手就把身体弄跨了，那真的是得不偿失啊。每天吃完饭后晃荡晃荡，平时抽空和朋友一起玩玩，像我没事就往庐山跑，就当是锻炼身体了。<br />&nbsp; &nbsp;小白站长你要保持好好学习的习惯<br />&nbsp; &nbsp;互联网的高速发展，变幻莫测的搜索引擎，作为一名站长，特别是一名小白站长，你要保持一个好好学习的良好习惯。你要时刻关注SEO你的变化，只有这样你才不会被时代淘汰。<br />&nbsp; &nbsp;一个不想当将军的士兵不是一个好士兵，同样我认为一个不知道去学习的站长，不是一个好站长！<br />&nbsp; &nbsp;互联网的知识在不停的更新，而如果总是守着曾经学到的那点微薄的知识，就自恋到一位自己是多么的牛B的人，迟早是会被淘汰掉的，优胜劣汰的生存法则不仅仅是生活中实用，网络是尤其更加的明显。作为一名小白站长，你要时刻关注着别人怎么做，从别人身上学习他的优点，同时记住对方的缺点，时刻提醒着自己这些是不能去碰的。平时没事的时候多去逛逛站长论坛，看看近期站长们都在干嘛， 和同行们交流一下经验， 这样时刻掌握着最新的动态信息，鞭笞着自己时刻前进！记住，千万不要去做一名可恶的伸手党......<br />&nbsp; &nbsp;小白站长要懂得去分享，一个不知道去分享的站长，绝对不是一个好的站长<br />&nbsp; &nbsp;分享，不只是狭隘的小圈子里，自己玩自己的，而是希望分享的东西对更多的人有所帮助，在付出的同时给自己带来无形的财富，一个有良好品德的站长是大家都愿意打交道接触的，这样的分享才更有意义。 互联网的圈子很大，这块蛋糕很大，没有人能一口气吃完，也没有人能独自一人吃完。分享你自己的经验，换取别人的经验，一个人的孤军奋战远远比不上一群人，要耐得住寂寞，更要懂得“分享”！<br />&nbsp; &nbsp;小白站长你要有一个明确的目标<br />&nbsp; &nbsp;小白这里说的目标，并不是那些所谓的“我要把我的站作为第一户外网站”、“我要把我的站发展成中国***的站”，这些都是太空泛了，小白这里明确的目标指的是自己网站以后发展的方向和计划，有了方向和计划那才有前进的动力。我发现身边总是有许许多多的小白站长今天做电影站，明天看到了小说站有钱途，于是乎一头扎进了小说站的洪流之中。只是成功有这么简单？如果如此轻易的复制成功，那互联网会是今天的模样？这样的人只会越做约迷茫，做到了最后是一事无成，默默收场。一个明确的目标很重要，这是站长生存的根本，有目标，有计划，一步一个印，成功就在眼前！<br /><br />PS:后言：<br />&nbsp; &nbsp;目前来网上淘金的你是否入不敷出？你的网站是否被百度拔毛？草根站长的路在哪里？不要紧！因为绝望的小白站长不只是你一个，还有很多朋友跟你一样绝望着！小白也一样绝望着！只是这又能怎样？如果有一天小白基地走到了尽头，我想我不会后悔！加油吧，绝望的小白站长，好好的生存，在绝望中找希望！小白会默默的支持你们！<br /><div style="text-align:right;">	<span style="line-height:1.5;">本文由小白基地原创：http://www.xbai.net/thread-26-1-1.html 转载请注明出处！</span><span style="line-height:1.5;"></span> </div>','web','小白基地','1','0','0','1469201288','534','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('24','10','大风歌','海水咆哮的夜里，北方有鹰死去 大风里 你的檐角落雨，他的楼台笛飞  而岭南的天气阴睛不定　 三二异乡人　 在夏夜的地图里穿行……','/uploads/20180416\a702becb677a54cf763332879046b2a4.jpg','<p><img src="http://image72.360doc.com/DownloadImg/2014/05/1417/41603613_1.jpg" alt=""/></p><p><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;">海水咆哮的夜里，北方有鹰死去</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 大风里</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 你的檐角落雨，他的楼台笛飞</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> </span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 而岭南的天气阴睛不定</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 三二异乡人</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 在夏夜的地图里穿行</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 大风过后</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 他们的背影消失在历史的卷册之外</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> </span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 此刻，我在天际下抬头</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 抑望璀璨的星星</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 闪烁的光芒，在几百光年外</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 他们也曾点亮古人的杯盏</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> </span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 河水滔滔而下</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 火焰与雨水在时间的国度</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> ——大动干戈</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 胸间的山川，草木一枯一荣</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 那是证明有风吹过我的沧海桑田</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 尘世里</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 我们都是被困在黑夜里的小兽</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 你道不出我的孤独</span><br/><span style="font-family:宋体;font-size:14px;line-height:24px;background-color:#FDFFF8;"> 我也说不出你的荒芜</span></p>','诗歌','王赤清','0','0','0','1462749974','938','3','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('25','9','吐槽：产品狗是如何逼死强迫症的','老板说，要有用户量，于是产品狗们加班加点做产品，想方设法圈用户。如果是土豪团队，基本上就是真金白银往出扔，比如打车类App、O2O类App，不就是烧钱嘛，小爷有钱就是任性，不就是用户吗?买!买!买……','http://img04.hc360.com/security/201511/201511090939542401.jpg','<div> 	<p style="text-align:center;"> 		<img width="50%" src="http://img04.hc360.com/security/201511/201511090939542401.jpg" />  	</p> 	<p>　　老板说，要有用户量，于是产品狗们加班加点做产品，想方设法圈用户。如果是土豪团队，基本上就是真金白银往出扔，比如打车类App、O2O类App，不就是烧钱嘛，小爷有钱就是任性，不就是用户吗?买!买!买! 	</p> 	<p>　　然后，我的脑子里总会出现小马哥拿出一把美金点烟的画面，然后乐得屁颠屁颠地赶紧下载。自从我用了某度的外卖，单单减8元，把自己吃成了一个胖子，真没出息! 	</p> 	<p>　　如果是创业团队，没那么多钱怎么办?这年头，大家都转行进互联网了，连我家门口卖烧饼的都学会用互联网思维卖烧饼，扫一扫二维码关注微信买十送一。 	</p> 	<p>　　竞争太激烈了，把用户吊得胃口大开，个个无利不下载。创业公司又特别苦逼，拿点投资天天被投资人屁股后面追着要用户量，动不动就问，你们的产品怎么还没过千万呢!现在市面上获取一个用户的最低成本都3块钱了，过千万!要不要我给你刷个3千万啊! 	</p> 	<p>　　说到这里我得跑个题，不愿看的可以自动跳过。 	</p> 	<p>　　某些App你压根都没听过名字，到应用商店一看，下载量动辄过千万。我之前的一个公司，老板是传统行业出身，被人忽悠互联网遍地是金矿，凑了5000万做App，结果一年过去了，App终于上线了，界面不忍直视，各种BUG，最后产品经理自觉跑了…… 	</p> 	<p>　　这种产品给钱也没人用，并且老板也亏得没多少钱了。老板寻思着5000万也不能打水漂呀，怎么办，干脆刷个漂亮的数据，做个好看点的财务报表，看看能不能上个市……尼玛。 	</p> 	<p>　　产品推广说白了就是拼钱、拼渠道，产品做得好，也得砸钱推呀。只有渠道做好了，营销跟上了，用户需求满足了，产品才有可能一炮而红。 	</p> 	<p>　　产品推广，目前主要是围绕传统媒体、社会化媒体、应用商店等进行。在社会化媒体方面，许多老板都有误区，互联网公司还理性点，一些往互联网转变的传统行业却把微博、微信营销奉为神明，有一次和一个老板谈，老板问，你负责微信、微博，每个月都带来多少业绩? 	</p> 	<p>　　我说，这得看你每个月给出的预算多少了?老板眼睛睁大了，我要你来就是搞这个的，你还让我另外给你预算?你看人家杜蕾斯官网发一条微博，带来多少业绩的提升啊，还是免费的好吧!我当时差点都哭出来了，老板，你怎么能这样天(kou)真(men)啊! 	</p> 	<p>　　跑题结束。 	</p> 	<p>　　某些公司为了吸引下载量，各种奇招(没节操)倍出，首先各种送礼品，用的最烂的就是下载就送苹果，从苹果4S一直到苹果6，从iPad 2一直到iPad  Air，这是给苹果做广告呢，还是给自己推广产品呢? 	</p> 	<p>　　至于奖品送没送出去，说句没节操的话，解释权在我手里，我爱给谁给谁。反正，我知道基本都是内部消化了，有的公司太省，老板的手机都客串了好几回奖品。 	</p> 	<p>　　其次，各种擦边球营销，谁让咱穷呢。妈蛋，谁都知道买用户是最省时省力的，问题是，咱高吗?富吗?帅吗?既然没有就想方设法地拉用户下水。 	</p> 	<p>　　这在社交类App上简直是重灾区。打点色情擦边球大家都心照不宣，但是之前被下架的某加就太过火了，现在是什么时候，大家都夹着尾巴做人，你风风火火地一路陪睡游全国，真当社会主义价值观不存在啊! 	</p> 	<p>　　当然高级点的就是各位老总亲自出面搏出位、抢头条，各种江湖恩怨，各种创业伙伴反目成仇，各种揪心各种励志，比娱乐圈还热闹，尤其是越来越多的女性同胞，巾帼不让须眉，充分发挥自己独特的优势，比如马佳佳同学，靠着一对大胸抢了不少头条。 	</p> 	<p>　　说到这里，可能有客官就会问了，你这和题目有什么关系。稍安勿躁，马上到重点了，小弟从不玩什么标题党，但我就是玩了，你能怎样，你来咬我啊! 	</p> 	<p>　　反正，大家千辛万苦的终于把用户忽悠过来了，却发现，用户活跃度上不去，怎么办?老板们开始对产品狗说：我们要活跃度! 	</p> 	<p>　　投资人又追在屁股喊：我要活跃度，我揍是要活跃度!没有活跃度，你让我怎么到纳斯达克上市，怎么获得高估值，怎么和马云抢首富! 	</p> 	<p>　　于是，这帮杀千刀的产品狗们把主意打到我们强迫症身上，你知道我有多累吗?每天各种消灭小红点，都成了App的标配了!这尼玛到底是谁先想出的这个主意，还让不让人活了!有的小红点关都关不掉，明明把消息都看了一遍，竟然还有，还能不能让人愉快地玩耍了! 	</p> 	<p>　　更可耻的是朋友圈的某些人，竟然把头像也换成带小红点的，我在这里宣布要和你们断交! 	</p> 	<p>　　更悲惨的是，即使我把小红点全部点完，心里才能舒坦，但是过一会，我就想拿起手机，看各种App里有没有小红点继续出现!完全挺不住下来啊!! 	</p> 	<p>　　所以，我要在这里为我们强迫症患者大声疾呼：产品狗们!不要为了你们的活跃度而不顾我们强迫症患者的身心健康，你们这种卑鄙的行为已经严重影响到我们的正常生活! 	</p> 	<p>　　没有买卖，就没有杀害!我可以滚了，继续消灭小红点。 	</p> </div>','web','网络','1','0','0','1462944438','333','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('27','10','中国码农生存现状调查','程序员一直都是一个备受人们关注的群体。2014年，据IDC统计，全球约有1850万名程序员，中国占10%。随着近年全国互联网创业热潮的兴起，“互联网+”、“云计算”以及“智能硬件”等领域……','/uploads/20180416\2f9f1f05de756c2b67c2bc97965fd56f.jpg','<p>程序员一直都是一个备受人们关注的群体。2014年，据IDC统计，全球约有1850万名程序员，中国占10%。随着近年全国互联网创业热潮的兴起，“互联网+”、“云计算”以及“智能硬件”等领域发展迅速，市场对程序员的需求更为旺盛。</p><p>由程序员客栈联合稀土掘金通过对北京、广东、浙江、上海等全国28个省、直辖市及特别行政区的10W+优秀程开发者进行了一次调查。调查报告里对程序员的年龄组成、性别比例、擅长的计算机语言、工作时间以及薪资等进行了统计和分析。&nbsp;</p><p><strong>地理分布：</strong></p><p><img alt="" src="http://img.jiemian.com/jiemian/original/20160830/147254316417014700_a580xH.png"/></p><p><em>数据来源：程序员客栈/稀土掘金</em></p><p>从调查结果来看，有一半以上的程序员来自于北京（23.55%）、广东（16.53%）、浙江（12.81%）和上海（12.40%）。北上广作为中国经济和科技最为发达的地区，是程序员的主要聚集地。浙江杭州目前正重金打造成为“中国硅谷”，吸引了一大批创业者，为程序员的就业和发展提供了优质条件。</p><p><strong>年龄组成：</strong></p><p><img alt="" src="http://img.jiemian.com/jiemian/original/20160830/147254318429681800_a580xH.png"/></p><p><em>数据来源：程序员客栈/稀土掘金</em></p><p>结果显示，绝大部分程序员年龄都不到35岁。超过一半的程序员年龄在23-30岁之间。当然程序员中间的“天才少年”的比例也不低。</p><p><strong>性别比例：</strong></p><p><img alt="" src="http://img.jiemian.com/jiemian/original/20160830/147254319899664200_a580xH.png"/></p><p><em>数据来源：程序员客栈/稀土掘金</em></p><p>一直以来，程序员这一群体主要是男性为主。在本次调查中发现，程序员群体中男女比例超过了12:1。如此“畸形”的性别组成，也解释了为什么很多程序员自嘲“单身狗”的原因。男程序员单身比例（52.46%）略高于女程序员，但女程序员的单身比例也达到了46.90%。</p><p><strong>擅长语言：</strong></p><p><img alt="" src="http://img.jiemian.com/jiemian/original/20160830/147254321514290500_a580xH.png"/></p><p><em>数据来源：程序员客栈/稀土掘金</em></p><p>图表显示，在程序员群体中，最擅长的语言为Java （43.03%）、JavaScript（32.38%）、HTML5（32.38%）、PHP（25.41%）。另外C语言（15.98%）、Python（15.57%）、Objective-c（12.70%）、C++（11.89%）和Node.js（11.07%）等也是使用较多的计算机语言。</p><p><strong>薪资状况：</strong></p><p><img alt="" src="http://img.jiemian.com/jiemian/original/20160830/147254323228335500_a580xH.png"/></p><p><em>数据来源：程序员客栈/稀土掘金</em></p><p>普遍认为，程序员是一份高薪的职业。从调查结果来看，工作3年内，1/5的程序员群体年收入在6万以下。1/5的程序员在3年内年薪就达到了20-30万的水平。大多数程序员年收入在10-20万之间，相比于其它一些行业，月薪过万已经是高收入水平。</p><p><img alt="" src="http://img.jiemian.com/jiemian/original/20160830/147254324296284400_a580xH.png"/></p><p><em>数据来源：程序员客栈/稀土掘金</em></p><p>在工作3-5年后，90%以上的程序员达到了月薪1万+的水平，只有大约10%的群体年收入低于10万。超过1/6的群体甚至年薪达到了30-50万的水平。</p><p><img alt="" src="http://img.jiemian.com/jiemian/original/20160830/147254325396989000_a580xH.png"/></p><p><em>数据来源：程序员客栈/稀土掘金</em></p><p>在工作5-10年后，只有2.7%的程序员年收入低于6万。年收入在15-20万的程序员占21.62%，1/3的程序员年收入在20-30万之间，超过1/5的程序员年收入在30-50万之间。年收入在50-70万的程序员占5.41%。</p><p><strong>星座运势：</strong></p><p><img alt="" src="http://img.jiemian.com/jiemian/original/20160830/147254326760657400_a580xH.png"/></p><p><em>数据来源：程序员客栈/稀土掘金</em></p><p>有趣的是，在程序员群体中，巨蟹座最少，只占4.10%。而白羊座（12.30%）、天秤座（12.30%）和天蝎座（11.07%）最多。</p><p><strong>关爱码农，解放生产力：</strong></p><p>国内一线城市依然是程序员的主要聚集体，一些经济发达，科技公司密集的二线城市也聚集了大量的程序员。</p><p>在计算机语言方面，懂得使用Java 、JavaScript、HTML5和PHP等编程语言的程序员最多。</p><p>男程序员的单身问题一直是社会关注的焦点，虽然女程序员的数量相对要少很多，但是其单身比例也很高，个人问题不容忽视。</p><p>从工作状态来看，目前越来越多的程序员希望能够远程自由工作，但从收入来看，如果一名程序员能够坚持工作3年，特别是对于刚毕业不久的大学生，其收入水平会有明显的提高。考虑到程序员年龄整体比较年轻，工作到5年的时候会到一个瓶颈期：竞争压力增大，事业上升后劲不足，同时面临家庭生活的压力。如果熬过这一阶段，提升自身价值，收入有可能获得突破性增长。</p>','web','网络','1','0','0','1473545312','1972','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('28','17','PHP为什么会被认为是草根语言？','从PHP诞生之日起，PHP就开始在Web应用方面为广大的程序员服务。同时，作为针对Web开发量身定制的脚本语言，PHP一直秉承简单、开源的思想……','http://www.aseoe.com/uploadfile/2014/1013/20141013102550923.jpg','<p style="text-indent:2em;">	以下文字并没有非常多的技术词汇，所以只要对PHP感兴趣的人都可以看看。</p><h4>	PHPer是草根吗？</h4><p style="text-indent:2em;">	从PHP诞生之日起，PHP就开始在Web应用方面为广大的程序员服务。同时，作为针对Web开发量身定制的脚本语言，PHP一直秉承简单、开源的思想，这也使得PHP得以快速的发展，并且大力地推动Web2.0的出现与发展。但是，长期以来，PHPer(PHP Programmers)被认为是处于草根阶层的程序员，被认为是技术含量少，层次低的程序员。这点在国内尤其突出。</p><p style="text-indent:2em;">	记得一个技术主管说过这样一个事情。他给一个程序员分配了PHP的开发任务，没想到那个程序员居然说：“我是学Java出身的，你让我去写 PHP，你这不是在贬低我吗？”。这件事情给我印象很深、触动也很大。虽然这不能代表大部分程序员的看法，但是这么认为的人应该不少。还有人说，现在如果是大型的政府项目，PHP是肯定不会被列入考虑的范围之内的。</p><p style="text-indent:2em;">	那么为什么PHPer会被认为是草根阶层，是因为它很简单，人人都可以学会，所以没什么难度吗？我以前也是这么认为。PHP入门很快，处理文件，数据，远程连接，网络编程都非常方便，官方也有这样的说法：PHP学习的成本很低，所以你容易去使用它。这个想法也是普遍的，甚至大部分的PHPer 自己都这样认为。</p><p style="text-indent:2em;">	说到这里，我想大家就会想到我为什么要写这些文字。因为一年多的PHP推广工作让我了解到许许多多的使用PHP的公司的大概情况。在这些过程中我慢慢体会到其中的根本原因。这里我说是根本原因虽然是个人的看法，但是我觉得事实就是如此。</p><p style="text-indent:2em;">	那么为什么PHPer会被看成草根阶层，根本原因是PHPer所作的事情(通过代码实现)的绝大部分都是表现层的东西，这个熟悉PHP的人都知道。当然也会有PHP会说他用MVC结构编写的某某框架具备的如何如何的功能。但是这些还是表现层。所以只会处理表现层的程序员就被看成草根阶层了。事实上也是如此，因为这种情况下PHP确实很难构造大型的应用。</p><p style="text-indent:2em;">	这就找到原因了，不是的。为什么PHPer总是在负责表现层的东西呢。答案是底层的数据处理(Web应用就是数据存储和查找)我们一般不去触及。好，那么说到这里有些人可能已经想到了，那不就是数据库吗？对，就是数据库。让PHPer一直当草根的元凶就是数据库。为什么？</p><h4 style="text-indent:2em;">	PHPer对MySQL的依赖过大</h4><p style="text-indent:2em;">	因为目前流行的web架构中，前端是负载均衡系统，中间是web服务器，后面是数据库服务器。所以，大部分PHPer工作在Web服务器层面。因为数据库已经很好地为我们组织数据了。所以PHP中没有太多的算法，而且大家潜意识下也觉得不需要，更何况会影响性能。</p><p style="text-indent:2em;">	这种情况下，PHPer就成为了数据库使用者，他总是在操作数据库。而不是在做程序。一个最简单的PHP脚本就是，连接数据库，把数据取出来，然后用命令输出到浏览器。整个过程不超过10行代码。给人的感觉就是太简单了。没有任何技术含量。为什么了，因为数据处理部分都已经被数据库做完了。尤其是MySQL的使用。MySQL是免费的，所以大多数程序员可以自由地使用它，另外MySQL的速度够快了，所以做个PHP应用程序非常的简单。这就相当于给你枪以后你觉得没有必要学习武功一样。当然，我不是说枪没有武功好。而是说，枪的出现，小孩都可以轻松便捷地杀人了。</p><p style="text-indent:2em;">	我们再详细说说为什么是数据库。这里我说一个例子。我去过北京一家非常著名的网站，当时我们还有一个比较资深的PHP程序员在那说些系统架构的事情。我记得当时那个程序员问大家一个数据结构中的算法问题的时候，全场没有一个人能答得出来(包括我)。然后那个程序员就开始给大家讲些很基础的数据结构的东西了。让我一下子回想到大学时候学的数据结构课。而这些基础的数据排序、查找、传递的问题在其他高级语言(比如C)是非常普遍的。但是在PHP没有。PHPchina.com的论坛也有个板块叫PHP的数据结构和算法。这个板块的帖子也是寥寥无几。</p><p style="text-indent:2em;">	说到这里，大家明白了吧？大部分PHPer仅仅处理表现层的东西，而在MySQL的便捷使用下，PHPer几乎不用触及任何数据结构与算法的情况下完成大部分开发任务，所以一个才有上面的，没有一个PHP程序员能够回答出那道数据结构的问题，换成是C等语言，情况可能就大不相同了。是PHPer草根，才让PHP显得草根。</p><p style="text-indent:2em;">	仔细回想下，目前网络上大家讨论的最多的是两个方面的问题。一个是PHP的类的使用(处理过程的封装)，还有一个是开发框架问题。但是我们仔细分析的话，发现这些所谓的PHP中比较复杂的概念里面没有数据处理。为什么，有数据库。用一个Adodb或者PHP5的PDO就可以搞定了。真的搞定了吗？不是，这些无非是在连接数据库，没有数据处理。所以PHPer似乎就没有什么可以拿出台面上的东西。</p><p style="text-indent:2em;">	再说一个具体的代码问题，无级分类。这个概念我想大家都不会陌生了吧。我见过两种处理方式。第一个是地道的PHPer的处理方式，也是目前比较流行的。就是用数据库来处理。而且字段很少，只需要加个父类的字段并加以判断就行了。而且这个方法很实用。效率也高。但是这个不是数据处理的范畴了，而是数据库的查找。</p><p style="text-indent:2em;">	第二个是C程序员用PHP写出来的，他把所有的分类信息都从数据库取出来，然后用数据结构算法进行排列分布，然后输出。</p><p style="text-indent:2em;">	这里我们不对这两种方式的效率进行对比，我想大家都有各自的想法。但是我想说明一个问题，就是这两种做法的本质的区别。PHPer习惯性地用数据库来处理，而且有很巧的处理方式，效率也很高。这种方式就是数据库查询。而第二种方法是比较有特点的。他认为数据库就是存放数据的地方，具体的逻辑处理还要靠自己的逻辑。</p><p style="text-indent:2em;">	因此，结论是第二种方法的使用者觉得自己强些，因为数据的逻辑是他组织的。并且觉得PHPer的那种做法无非就是会查询数据库罢了。所以他认为PHPer是草根级的，只懂得操作数据库和排列页面(smarty搞搞那种)。</p><h4>	让数据库回归本职工作</h4><p style="text-indent:2em;">	说到这里，我想大家都已经回忆了不少自己平时用PHP做开发的经历了吧，是否发现大家确实都在操作数据库呢。</p><p style="text-indent:2em;">	那么我们来讨论下这个问题。数据库不好吗？为什么我一直用数据库处理数据都没有问题。我要说的是数据库是有问题的，而且有很大的问题。当然这里我并不是说不能用数据库，也不是在贬低数据库的性能。而是，我们没有充分认识到数据库所起到的作用。</p><p style="text-indent:2em;">	我的想法源起于这样一个事情，有一次一个网站的技术总监问我，为什么他们的网站那么慢，要怎么办。当时，我的MSN里Zend总部的工程师正好在线，我就问他PHP响应比较慢了，怎么办？他当时直接告诉我，是数据库问题。肯定是数据库没有优化设计好。所以，我没有给那个技术总监确切的答案了，因为他们的数据库设计我们是不能涉及的。所以就给了大概的数据库优化的建议。这样的事情屡次发生，我就开始怀疑，为什么Zend总部的工程师每次都跟我说是数据库的问题呢，难道我们不能从PHP层面来解决这个问题吗？答案是不能。因为PHP目前的运行速度已经是很快了，通过Zend的性能分析也能看到一个用户的点击，PHP的运行时间只有10%不到，那PHP在干吗？它在等。等数据库的查询结果。这个方面在目前的PHP产品中有了很大的提高，那就是 Caching和网页静态化两个方案。</p><p style="text-indent:2em;">	Caching可能大家会比较陌生，但是网页静态化现在连PHP产品的用户都非常清楚了。速度快、容易被搜索到等等，好处不言而喻。开玩笑地说，现在网站的主页实现网页静态化只需要硬盘足够大。J至于Caching就比较复杂些，也是大多数PHPer感到头疼的地方。甚至于有些人会用C来实现。因为Caching中的数据有效期验证、查找、提取、更新等等都是比较难处理。当然，也有人会用数据库来处理 Caching问题。</p><p style="text-indent:2em;">	所以，当访问量激增的时候，PHP架构的网站会出现的很多问题都因数据库而起。数据库的同步问题还不算什么。关键是数据库的响应速度会有指数级的降低。这个问题我在10月23号LAMP发布会的时候问过MySQL的副总裁。他当时也没有给我比较完美的答案(这也我的意料之中)，因为数据库总会有瓶颈的，除非是神仙数据库，哈哈!</p><p style="text-indent:2em;">	这里有个题外话，LAMP大会的时候我跟Yahoo的一个技术高管聊的时候，我问他Yahoo在选择MySQL还是Oracle的时候是怎么考虑，他的答案令我非常惊讶。他说大部分的时候我们是会用MySQL的，因为它的性能已经达到我们的要求。但是什么时候我们会选用Oracle呢，就是当我们需要存储收费用户的数据的时候。我就问为什么，难道Oracle比MySQL稳定吗？他说，这个倒没有特别考虑。关键是如果使用Oracle的话，当出现问题的时候我们可以找到负责人，Oracle会负责事故的处理，但是如果用MySQL的话，我们找谁去?</p><p style="text-indent:2em;">	所以，我们对数据库的看法应该纠正过来，就是说数据库不是万能的。如果有实力的话自己开发数据库。听说Google就是那样的。</p><p style="text-indent:2em;">	那么我们怎么看待数据库呢？我个人的理解是数据库只是用来降低开发成本的手段。因为采用数据库以后我们不需要考虑数据的存储，尤其是排序和查找。但是这会带来什么问题呢？就是当业务膨胀的时候，数据库就成为瓶颈了。这个时候问题就会非常棘手。因为这个是底层的数据处理。牵一发而动全身。</p><p style="text-indent:2em;">	所以我认为正确的观点是，数据库是一个数据备份机。怎么理解，我们只需要保证数据的存储有效性就行了。而这本来就是数据库的核心功能，只不过因为数据库的方便的排序等功能让大家把过多的处理都交给数据库来操作了。一个用户的点击PHP就把一大堆的任务交给数据库，然后把结果排列下给用户就完事了。这对数据库是不公平的。也是因此大家开始抱怨数据库的性能了。</p><p style="text-indent:2em;">	针对这个观点，我们再举个例子，有一次我去拜访一个大型的网络公司(基本上国内只要上过互联网的都知道)，他们使用PHP很少，但是我了解到他们其它业务是怎么使用数据库。他们自豪地跟我介绍说他们在数据库的外围有个第二数据库(我这里起名叫第二数据库)。为什么叫第二数据库呢，原来它是一个缓存系统。那么开发工程师怎么去这个缓存系统获取数据呢？那个技术总监自豪地说，他们这个缓存系统有SQL查询语句。我当时很惊讶，但是后来想想确实需要这个。因为当你的缓存系统达到一定量级的时候从缓存获取数据都非常复杂，干脆写个SQL查询语句让缓存系统分析、处理并返回数据。而且他们告诉我，在他们那里，就算是用PHP的话也是让PHP去那个缓存系统读取数据。</p><p style="text-indent:2em;">	所以说，如果你能处理好这样的问题的话，把数据存放在数据库，然后数据库只起到备份的作用。然后你用自己的中间层来处理分析数据，效果是90% 以上的用户不访问数据库。有人就会说了，这不就类似连接池的东西吗？是的，因为数据库的瓶颈是无法解决的，我们只能在Web服务器和数据库中间加个中间层来做缓冲。</p><p style="text-indent:2em;">	可能大家会说了，切，这个我们早就知道了。那好，这里我要说的是它引发的两点思考：</p><p style="text-indent:2em;">	第一，有些语言已经有连接池技术的基础上，那些程序员可以方便地使用连接池而构建大型应用。那么如果他们认为 PHPer只会是用数据库，那么我们是不是可以说他们只会是用连接池呢？连接池和数据库在这个概念上有何区别？</p><p style="text-indent:2em;">	第二，当PHPer开始构建自己的缓存系统的时候，他是不是突破了PHPer只会是用数据库的层次？因为他参与了数据逻辑的处理工作。那么他还是草根吗？</p><p style="text-indent:2em;">	最后，懂得独立思考的PHPer不是草根，什么事情都丢给数据库去做的才是。</p>','php,web','网络','1','0','0','1473673975','1637','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('29','11','关于我的网站','很久以前就想有个自己的网站了，去年学php，10月份做了第一版，那时刚学php，还没学框架，不会逻辑和模块分离，采用的是混编……','/uploads/20180416\76ed86f0567add36a2581fa5d90c5c6b.jpg','<p><img src="/uploads/20180416/76ed86f0567add36a2581fa5d90c5c6b.jpg" alt="76ed86f0567add36a2581fa5d90c5c6b.jpg"/>　</p><p>　很久以前就想有个自己的网站了，去年学php，10月份做了第一版，那时刚学php，还没学框架，不会逻辑和模块分离，采用的是混编。今年刚好域名也到期了，就关了，后来学了thinkphp，本着学以致用，试着用thinkphp写了这个博客。</p><p>&nbsp;　因为阿里备案还没下来，于是我又跑去注册TK域名，关于ＴＫ域名的注册，好像以前就说要写篇介绍，但后来关站了，加上网上也有许多这方面的介绍，也就没写了，今天想着介绍下这个网站，于是一起连带着说说吧！</p><p>&nbsp;　ＴＫ域名注册是免费的，可以打开http://www.freenom.com/zh/index.html网址进行注册，这个其实也没什么好说的，你只要按提示一步步操作就可以得到一个免费的域名，免费域名最长时间是一年，免费时间过后，如果你想要继续使用的话，就得购买了。免费域名的优点，可能就是可以不用备案，不用掏钱，对于网站搭建初学者，我认为是个不错的选择，但是渡过了初学者阶层，就不太适用了，还是自己购买一个域名吧，因为免费的东西是有局限性的，各种搜索引擎过滤收录等限制了.tk域名的良好发展。也就是说像google、百度、360这些，会过滤这种域名。</p><p>&nbsp;　ＴＫ域名的注册，让国内网友望而却步的不是域名的注册，而是帐户的注册，freenom帐户注册目前提供了邮箱注册和google、facebook及live社交帐户注册，像国内的163邮箱，我记得我注册的时候是不能用的，现在情况就不得而知了。我自己忘了是什么时候注册的，那时我是用ＱＱ邮箱注册的，不知现在还能不能注册了，就不太清楚。反正对于不会翻*墙的朋友们来说，总会遇到各种困难就是了，这个也没什么好说的，支持郭嘉吧！</p><p>&nbsp;　好了，注册到了一个域名，下面就讲讲空间吧。我在阿里也有空间，因为域名没备下来，我关了，走流量，闹心。于是又跑去找空间。目前我这个网站空间也是免费的，时间没看到他说什么限制，可能是无限的时间的吧，。因为我是用php写的网站，所以找的空间也是支持php的空间，我是在hostinger这个网站注册的空间，为什么选择这个空间，我想当时我选择他的最主要原因，可能就是无广告吧，以前也在别的空间注册过，太多数就是在你网站挂广告，这是不能忍的，你想，你做好的美美哒网页，被他一个广告一挂，整个美观度就被破坏了嘛，就像绝世倾颜，被无情的一刀划过，对于站长，肯定心中是不爽的。</p><p>&nbsp;　当然了，这个空间除了无广告与免费外也有一些缺点，因为他是英国的空间商，国内防问速度有时不尽人意，有时等个半天都还没缓冲完，不过有时又特别快。碰到这种半天还没缓冲完的，大多数手动刷新一次，就好了。</p><p>&nbsp;　说了半天，可能没用过hostinger的人，还不知道网址，http://www.hostinger.com.hk/中国用户可以打开这个网址，就可以注册到一个200M免费PHP的空间啦。hostinger空间分三个版本，我是用的免费版，200Ｍ空间，100Ｇ月流量，还附送一个邮箱，mysql数据库，很好吧！关于如何注册问题，相信会用百度的朋友，也知道如何注册了。</p><p>&nbsp;　注册空间后，当然就剩下域名绑定了，网页上传了，数据库数据导入了这些基本事情了，做完这些后，你就拥有了一个完全免费的网站。对于初学者，这是真正的给心。</p><p>&nbsp;　好了，其实也没什么东西，但不知不觉就写下了这么多字，嘿！说明偶还是有水的天赋的！好了，介绍就到这里吧！</p>','分享，web','清蝎','0','0','0','1473782461','570','2','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('30','11','时间在流逝','流光容易把人抛，红了樱桃，绿了芭蕉。<br>——宋·蒋捷','http://img.51tietu.net/upload/www.51tietu.net/2014-12/201412100337233619.jpg','<div id="divcontent">　　流光容易把人抛，红了樱桃，绿了芭蕉。——宋·蒋捷<br><br>　　下午的时间，严翰正坐在一间小咖啡馆里啜着一杯咖啡，轻音乐舒缓地流泻，咖啡杯沿的热气静静地在半空溃散。此时小咖啡馆里只有严翰这一位客人，一位中年女服务员正无聊地盯着柜台墙上一面玻璃钟，她似乎要在钟里看到什么？或者找寻什么，但事实是她什么也没看到。秒针嘀嗒嘀嗒地仍转个不停，严翰就在这嘀嗒声中陷入了一种自我沉思之境。<br>　　“喂！你咖啡里要不要加糖？”一个年轻的女子声音突然问道。严翰不假思索地应声道：“不用，谢谢。”抬起头，却什么人也没看到，更别说什么年轻女子。那中年女服务员听到严翰说话声，回过头来看了眼严翰，莫名其妙地盯着他，以为自己听错了，最后还是走上前来，询问道：“先生，请问你须要什么帮助吗？”严翰歉意一笑，冲那中年女服务员道：“谢谢，不用，抱歉！”<br>　　中年女服务员走后，严翰又闭上了眼睛。我这到底是怎么了？他在心底反复地扣问着自己。一条绿色波浪形的花边裙不知不觉在他脑海里随风飘舞。对，就是如此。这是严翰给自己唯一的答案。沿着绿色波浪形的花边裙，他时常像一条在小溪里游泳的鱼，在从前的岸边，他是贪婪于海洛因的瘾君子。<br>　　哦！这多么可笑，都过去十多年了，为什么我总是放不下这一片时光的碎叶呢？严翰轻敲着咖啡桌面，自嘲地自问。你为什么总是沉于幻想之中呢？哦！不，娜娜，这不是幻想，我相信总有一天，我会成功的。你要相……好了，我相信你，但对不起，我不能陪你枯守着一口井，等着雨水将他覆盖。<br>　　这几句话又在严翰脑海里时隐时现，对，雨水永远无法将一口井覆盖，但他——严翰成功了，可当初说这话的人儿呢！那说话总是先将上唇微微向上翘起的女子呢？严翰逊轻抿了一口咖啡，苦苦的，涩涩的，这就是他爱的味道。<br>　　中年女服务员见他脸色阴睛不定，于是上前，关切地问道：“先生，你……不舒服吗？”“哦！不，我很好。”严翰觉得他应该找点别的事来转移自己的注意力，于是抬头问女服务员“你在这工作几年了？”大概因了这时咖啡店没什么顾客，女服务员也是无聊，于是回说：“有三四年了吧。”<br>　　三四年不算很长，但也不算很短。严翰在心里说。门被推开，严翰的思路被打断，向门口望去，一个七八岁的小女孩背着卡通粉红色的书包朝女服务员招了招手，甜甜地叫了声“妈妈”，小女孩后面跟着走进一个中年男子。女服务员迎了上去，对小女孩说道：“上楼去做作业。”<br>　　中年男子对女服务员问道：“娜娜，今天生意如何？”女服务员苦涩一笑，回道：“整个下午也就这么一位先生，我让其他服务员回去休息了。”说着指了指16号桌上的严翰。中年男子顺着娜娜的手指看了眼严翰，也是无奈地叹道：“这年头的生意是越来越难做了。”<br>　　严翰听了中年男子称女服务员为娜娜，脑袋嗡地声，有如炸开。细细审视女服务员，长相与当年的娜娜确有几份相似，只是当年那微微上翘的上唇被生活磨平了，那条绿色波浪形的花边裙也暗暗换成了普通服务员所穿的大众衣服，她也无法逃脱生活车轮的辗压。<br>　　当年的娜娜也许不复存在了，现在的娜娜也不是当年的娜娜；当然，现在的严翰也不是当年的严翰了。严翰看了眼腕表，指针指向18：26，呵！时间过得真快，严翰在心里颇有感触地说道。站起身来，严翰出了咖啡馆，一缕夕阳刺入他的双眼，他几乎无法睁开眼来，这时他想起蒋捷的那首《一剪梅》，其中有句“流光容易把人抛，红了樱桃，绿了芭蕉。”<br>　　睁开眼，发觉自己昨晚又做了这几年来所做的同一个的梦，当清晨的阳光射进他的卧室，严翰蹒跚地起床、穿衣。卧室里靠东一面落地大镜，照着他满头的白发。<br >2011-06-28</div>','琐碎，小说','王赤清','0','0','0','1473880427','670','1','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('31','11','仲秋日偶记','　　又到了一年一度的中秋了，今年也没什么特别的，做为异乡人，早就误把他乡做故乡了。现在的中秋，再也没有古人的相乡之情，几千里外也是朝发夕至……','/uploads/20180416\be1b07007ab1dd7bb2fff20a8b348d7b.jpg','<p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　又到了一年一度的中秋了，今年也没什么特别的，做为异乡人，早就误把他乡做故乡了。现在的中秋，再也没有古人的相乡之情，几千里外也是朝发夕至。情感的也变的慢慢麻木了。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　说到仲秋，就不得不提月饼。从小就讨厌吃月饼——太干，无论什么味，什么馅的，所以我从小就不吃月饼。更没有拜月亮的习惯了，什么赏桂花啊，放河灯啊，烧塔之类的，更与之无缘。大概现代人更比古人冷酷吧，想一想，古人娱乐业不发达，每逢到节日，也是挖空心思的玩，放到现代，唯有一笑耳。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　中秋节，也是古代诗人表达情思之日吧，说到关于仲秋的诗词，多不枚举，其中诗歌里面，写的好的，当推王建的《十五夜望月》：全诗云：</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　中庭地白树栖鸦，冷露无声湿桂花。<br/>　　今夜月明人尽望，不知秋思落谁家？</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">而在词里，却苏东坡的《水调歌头》莫属。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　中秋无事，自晨而晚，落雨一天，唯翻诗词渡之……</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: "><br/></p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: "><img src="/uploads/20180416/be1b07007ab1dd7bb2fff20a8b348d7b.jpg" alt="be1b07007ab1dd7bb2fff20a8b348d7b.jpg"/></p><p><br/></p>','琐碎','王赤清','0','0','0','1474104016','210','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('32','11','漫谈史卷','人生不如意之事，十有八九，遇到不平的事，难免会发发牢骚。这是自古恒之，谁也免不了的事。但是中国历史上，曾经发生了件因为发句牢骚，引起了另一个国家的皇帝死亡的故事……','/uploads/20180416\a9c28077f62ca9b04737a0f8e937599b.jpg','<p><span style="color: rgb(51, 51, 51); font-family: "><img src="https://timgsa.baidu.com/timg?image&quality=80&size=b9999_10000&sec=1523876994201&di=bc0a1e8bbd60124a986b6becfde78ac9&imgtype=0&src=http%3A%2F%2Fs16.sinaimg.cn%2Fbmiddle%2F537ef173g6cfcdab021cf%26690" width="500"/></span></p><p><span style="color: rgb(51, 51, 51); font-family: ">　　人生不如意之事，十有八九，遇到不平的事，难免会发发牢骚。这是自古恒之，谁也免不了的事。但是中国历史上，曾经发生了件因为发句牢骚，引起了另一个国家的皇帝死亡的故事。</span><br/></p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　话说宋太祖死于烛影斧声，太宗赵光义继位，对，说的就是强奸小周后还让别人在旁边作画的那位。太宗赵光义继位后，将他胞兄（即太祖）之子德昭和德芳逼死，又贬黜廷美到房州，两年后廷美死于谪所。太宗长子元佐因为同情廷美被废，另一子元僖暴死，最后襄王元侃被立为太子，改名恒。太宗驾崩后，李皇后和宦官王继恩等企图立元佐为帝，但宰相吕端手腕老辣，将真宗扶上了帝位。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　翻开书籍，人们可以发现，世人对真宗多有溢美之词，史学家更是对他称道，他在位其间，史称咸平之治。然而真实的真宗如何呢？公元1004年辽国举兵来犯，宋朝大多数大臣建议不抵抗，但以宰相寇准为首的少数人极力主张抵抗，最后他们说服宋真宗御驾亲征，勉强北上。好嘛！皇帝亲征，宋军士气大振，与辽军相持在澶州城下相交，宋胜。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　但是真宗决定就此罢兵，以每年向辽纳白银十万两、绢二十万匹来换取与辽之间的和平，定澶渊之盟。这是宋朝以岁币换取和平的开始。当初真宗派人出使辽议和，说了句：“迫不得已，虽百万亦可!”宰相寇准知道后，指着曹利用怒道“超过30万两，提人头来见”。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　是不是有种熟悉的感觉，对，说的就是慈禧，打了胜杖却赔款，真宗根本没法与太祖太宗比，他胸无大志，无进取之心，像个女人。澶渊之盟后，寇准渐渐失宠，真宗以为天下太平，开始感到无聊，当皇帝感到无聊，自然有悦上的人，后来真宗就玩脱了。常以天书符瑞之说，荧惑朝野，真宗也沉溺于封禅之事，广建宫观，劳民伤财，致使社会矛盾加深，使得北宋的“内忧外患”问题日趋严重。元朝史官脱脱评说：“及澶洲既盟，封禅事作，祥瑞沓臻，天书屡降，导迎奠安，一国君臣如病狂然，吁，可怪也。”一国君臣就像得了狂疯病一样，真是怪事。朱元璋曾评说：“……致使言祥瑞者相继于途，献芝草者三万余本”。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　反正真宗后来就是粉饰太平，好事夸大了说，坏事往小了说，或者坏事直接变好事，不图强国抗辽，全国沉浸在意淫之中，歌舞升平。真宗还特意写的篇《励学篇》，“书中自有黄金屋，书中自有颜如玉，书中自有千株栗，书中车马多如簇“就是出自他手，当皇帝鼓励学子功名富贵但向书中求，就会变成潮流。就如《聊斋》里，宫中尚促织之戏，民间就必然有跟风的潮流，这时跟本没有清醒的人，就如现代，当上有侈风，下必跟之，蔚成风气。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　而故事主人公的童年，就生活在这个年代的背景里。当整个社会以功名富贵但向书中求时，谁也免不了俗，这就是大势。他们以科举登第为一生之志，穷经皓首。可以这么说，宋朝，就是从真宗起，由强转弱，当然，他的表面，是盛世。真宗逝世后，就是仁宗继位。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　仁宗十三岁即位，皇后刘氏垂帘听政历时十一年，也就是仁宗在位时，夏朝向宋称臣，你以为宋朝当大哥了吧，未必，你大哥反而要向小弟纳币。这就是庆历和议。庆历和议后，辽国因为种种原因，重兵压境，于是又增加了辽国的岁银十万两，绢十万匹。当时是大宋内部财用匮乏，盗贼蠭起。外部方面，宋与西夏爆发长达数年的战争，屡战屡败。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　这时社会精英也看出了宋朝的表面繁华，实则羸弱，社会矛盾日益尖锐，当然，表面上，他还是那个繁华大宋，号称仁宗盛治。世人皆醉，几人独醒？读过《岳阳楼记》的同学，都有所了解，范仲淹就是这时的精英之一，庆历新政就是范仲淹、富弼、韩琦三人在庆历三年实施的新政，希望能解决自真宗遗留下来的问题。当然，由于仁宗宽仁少断，新政以失败而告终。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　而故事的主人——柳永，就生活在真宗、仁宗二朝。柳永，福建崇安人，北宋著名词人。柳永出生在官宦世家，有功名用世之志。也就是说，他是真正的官二代呀，而且还是全家都是当官的，他爸，他五位叔父，他哥，他弟都有官位，好幸福有木有！所以，咱柳永也是自小立志要当官的，功名富贵绊身。真宗大中祥符年间（公元1009年），柳永第一次参与科举，他可是踌躇满志，直奔魁甲而去。就像唐伯虎，还没发榜，直呼状元非我其谁？</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　然而，真宗是要面子的人，自己好浮夸之风，却见不得别人浮夸。就像小偷碰到别人偷东西，必然第一个站在道德高点，跳出来指责对方。柳永将文章作的辞藻流采，花团锦簇似的。真宗拂然不悦，严厉谴责柳永“属辞浮糜”，落第。柳永傻眼了。柳永在京城那时候可是名动汴京的，很是自负的，一下子被打落深渊，心理肯定不服气。于是写了首词，叫《鹤冲天》。这个词牌名就是柳永创出的，名字起的那是相当霸气。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　黄金榜上，偶失龙头望。明代暂遗贤，如何向。未遂风云便，争不恣游狂荡。何须论得丧？才子词人，自是白衣卿相。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　烟花巷陌，依约丹青屏障。幸有意中人，堪寻访。且恁偎红倚翠，风流事，平生畅。青春都一饷。忍把浮名，换了浅斟低唱！</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　这首词可谓是满纸牢骚，落榜，他说是偶失。还自称自己是遗贤，我落榜只是风云不及，也就是说运气不好罢了。何必说什么得失（其实这首词就是柳永对落榜的牢骚，一边说不说得失，一边发着牢骚），我虽是白衣（古人无仕之人），但必是卿相之姿。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　下阙更是变本加历，说青春都一饷，有点像现代说的人不风流枉少年。将功名视为浮名，这就是打了所有求功名人的脸了。还说不如将浮名，换了浅斟低唱。这句说的委婉，所谓浅斟低唱，其实就是偎红倚翠，把嫖宿说的这么文雅，没办法，人家就是有这个本事。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　这下子可谓把整个官场及皇帝的面子全驳了。柳永虽然发着牢骚，但他自小家庭环境及社会环境影响，还是准备着富贵书中求。但因为这首词的关系，后来三次科举皆名落孙山，柳永不得不离开汴京。另有一说，不是真宗，而是受到仁宗的黜落。可以说柳永的命运，就因为这首词而变的多舛。而北宋的命运，也因为这首词，将柳永黜落，从而导致他的灭亡。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　如果柳永离开汴京，对官场失了热情，就没有后来的事，但个人又如何能与大势相抗？在所有人都说再多的钱生不带来，死不带去时，但当整个社会都被金钱所迷醉时，谁也不能独善其身。柳永虽然离开了汴京，但他还是希望挤进大宋的官场。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　据南宋罗大经《鹤林玉露》所载：柳永离开汴京后，便到处飘泊流浪，寻找晋升的途径，迫切希望得到他人的提拔。后来他到杭州，听到孙何任两浙转运使，便想去拜会孙何。但柳永是布衣百姓，如何随随便便见到转运使。于是他写下了《望海潮》这首词，希望孙何能赏识自己，得到举荐。这个没什么，在古代这个叫干谒，李白、杜甫、王维、白居易都写过这种干谒。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　《望海潮》主要是描绘杭州的繁荣、壮丽景象，及小小的拍了记孙何的马屁。全词云：</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　东南形胜，三吴都会，钱塘自古繁华，烟柳画桥，风帘翠幕，参差十万人家。云树绕堤沙，怒涛卷霜雪，天堑无涯。市列珠玑，户盈罗绮，竞豪奢。<br/>　　重湖叠巘清嘉。有三秋桂子，十里荷花。羌管弄晴，菱歌泛夜，嬉嬉钓叟莲娃。千骑拥高牙。乘醉听箫鼓，吟赏烟霞。异日图将好景，归去凤池夸。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　据传，孙何听了后也是很赏识这首词，谁都喜欢被拍马屁。但孙何知道是柳永所作后，只是请他吃了顿饭，就没下文了。你想，你都得罪皇帝了，皇帝可是记着你号人呢！谁敢逆了皇帝，举荐你呀！</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　柳永不得不认清现实，公元1034年，柳永约48岁时，改了个名，穿着马甲，第三次直奔汴京。柳永这次终于如愿，科举及第。但是他仕途不得意，一直做小官。他政绩颇佳，但上面不给他升职，他希望能升官，甚至还曾向宰相晏殊自荐，但被晏殊讥讽。甚至请求宦官加以推荐，有人向仁宗举荐他，仁宗说：“得非填词柳三变乎？……”他曾作《醉蓬莱》词，歌颂皇帝，献给宋仁宗，希望得到擢用，但词中有几句话得罪了宋仁宗，宋仁宗把词掷在地上，柳永自此再也没有得到升职重用。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　柳永虽然一生不得志，但他的词非常出名，甚至传出了大宋国门。曾有凡有井水饮处皆能歌柳词！就是说有井水的地方，也就是有村落，就有能唱柳永词的人。自然，柳永的那首《望海潮》也一起传出了大宋国门。大宋仍然还是那个大宋，表面繁华似锦，但有识之士已经看到了大宋的危机，在内忧外困的局面下，淹仲淹等人的变法也无法挽救大宋走向没落，新法也只是推行了一年左右，就被迫停止，随之全面废止。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　仁宗后就是英宗，但英宗多病，只做了四年皇帝，由于多病，政治方面没什么建树。英宗逝世后就是神宗即位。这时变法已经是刻不容缓了，土地兼并严重，达官贵人占田无数，一眼望去，看不到田的尽头；而贫穷的人却没有立足的田地，他们只能去耕种富户人家的田地。公卿大臣大都占地千顷以上，神宗即位后，对北宋的积贫深感忧心，在王安石的主持下，推行新法，但遭到全面倒的反对。朝野皆哗，苏东坡的老爸苏洵还曾写过《辩奸论》的文章，从王安石的衣食住行，外貌表情全面诋毁王安石进而反对新法。就连王安石的亦师亦友的欧阳修更是在自己治理境内全面停止新法。比较有意思的是苏东坡，他刚开始也是全面反对新法，跟新法对骂，还写《日辩》，嘲笑王安石，被贬到地方上，才看清了新法。后来王安石倒台，司马光上台，把苏东坡招回，司马光全面废止新法，苏东坡又跳出来反对，他认为新法里面也有可取之处，并骂司马光为司马牛。从而被司马光等人认为苏东坡是第二个王安石又贬出汴京，就这样，苏东坡被新法旧法所不容，越贬越远。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　社会舆论全面抵制新法，王安石两次推行新法均以失败告终。神宗时期两次与西夏暴发了战争，皆以惨败告终，神宗于1085年忧忿而死。神宗死后，十岁的哲宗即位。哲宗在位15年，但受高太皇太后牵制，哲宗根本没有发言权。高太皇太后执政8年，对新党多有抵制，互为攻讦，高太皇太后死后，哲宗及新党展开对了旧党的打击报复。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　哲宗恢复王安石新法，减轻农民负担，停止与西夏谈判，出兵讨伐西夏，西夏被迫向宋朝乞和并谢罪。哲宗在北宋是较有作为的皇帝，但因为他对旧党的怨怼，激化了新党旧党的党争，造成朝局动荡。哲宗时本看到了北宋的兴起，但哲宗仅24岁就病逝了，彻底灭了兴起的希望。哲宗后就是宋徽宗，宋徽宗专好享乐，对朝政了无兴趣，生活糜烂，好逛青楼楚馆。而这时，北方少数民族女真族终于成长起来了。宋徽宗没什么特别本事，却好大喜功，听说女真在攻打辽国，于是派人去跟金国结盟。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　北宋朝这时已有灭亡之兆，农民已经没有生路，宋徽宗搜刮东南奇石，引起民怨沸腾，公元1119年，宋江聚众三十六人纵横山东、河北一带，方腊攻占杭州，建立起横跨两江、江浙及安徽等地的政权。当金国灭了辽国后，大宋已经看到了风雨欲来之势，连强大的辽国都被他灭了，自己还不是人家吹吹气就灰飞烟灭，但朝庭权贵还在忙着争权夺利，其中以蔡京、童贯等六奸为最，公开买官卖官。当然，这里面徽宗也跑不了，他还在飘飘然地地陶醉在众臣祝贺收复燕云之中，实际却是在金国手中买来的空城。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　金兵压境，北宋根本无力抵抗，二围汴梁，宋微宗及钦宗被掳北上。北宋就这样灭亡了。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　柳永的词，经久不衰，后来传到了金国第四位皇帝耳中。当完颜亮读到柳永的《观海潮》时，看到有“三秋桂子，十里荷花”，不禁由然向往，十分羡慕南宋的繁华。加之完颜亮有三志，国家大事，皆我所出，一也；帅师伐远，执其君长而问罪于前，二也；无论亲疏，尽得天下绝色而妻之，三也。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　其中完颜亮这第二志愿就是帅师伐远，执其君长而问罪于前，就是指的南宋。他的祖父纵横天下，执鞭所指，就是兵戈所向。他完颜亮肯定不能说自己不如祖父吧，这其二志可以说他的执念所在。当读到“三秋桂子，十里荷花”时，好战的因子一下子被激发出来了，他激动的停不下来，当即题诗云：万里车书一混同，江南岂有别疆封？提兵百万西湖侧，立马吴山第一峰。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51); font-family: ">　　终于在公元1161年10月，完颜亮兵分四路，对南宋发动全面进攻，完颜亮更是亲自率三十二总管兵，但完颜雍乘金帝在外领兵，于沈阳自立为帝，是为金世宗。而完颜亮伐宋大业，在采石矶（位于马鞍山）受阻，退兵瓜州渡，加之完颜亮受金世宗自立为帝所激怒，强下军令，三日内渡江，激起兵变。金国第四位皇帝，在兵变中被部下所杀。</p><p style="box-sizing: content-box; padding: 0px; color: rgb(51, 51, 51);">　　从此，金宋之局才变，金国自完颜亮后再也无力南征，直到金国末年，被蒙古人欺负惨了，又恨南宋不再纳币，于是又发动了一次侵宋，但这次南宋跟蒙古人联手，金国再不复金太祖之雄，现在也只是如丧家之犬。不过，等金国灭了，南宋也走上了北宋的老路，被昔日的盟军所灭。</p>','历史','王赤清','0','0','0','1474191517','382','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('33','10','五律·冬夜咏','苍穹连大海，暝色隐青山。树杪稀星挂，粼波月影……','/uploads/20180416\7b97aee503d1d779bb04807ea702eb72.jpg','<p><img src="/uploads/20180416/7b97aee503d1d779bb04807ea702eb72.jpg" alt="7b97aee503d1d779bb04807ea702eb72.jpg"/></p><p>苍穹连大海，暝色隐青山。</p><p>树杪稀星挂，粼波月影环。</p><p>冬风吹寂寂，独步意闲闲。</p><p>忽忆莲峰雪，游人几岁还！</p>','诗歌','王赤清','0','0','0','1474687095','1606','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('34','11','关于网站经营的一些零碎心得','网站内容是一个网站的立站之本。关于网站内容，有以下心得。 网站需要有好的内容。 如何获得优质内容？ 用户贡献内容是第一步……','/uploads/20180416\695352a0b5b27c5a1d01653129c1bb72.jpg','<p>网站内容是一个网站的立站之本。关于网站内容，有以下心得。</p><ol class=" list-paddingleft-2"><li><p>网站需要有好的内容。</p></li><li><p>如何获得优质内容？</p></li><ul class=" list-paddingleft-2"><li><p>用户贡献内容是第一步。</p></li><li><p>用好的方式找到有价值的内容是第二步。</p></li><li><p>为找到的好内容找到合适的，感兴趣的读者是第三步。</p></li><li><p>让用户互动起来是第四步。</p></li></ul><li><p>以好的方式找到有价值的内容的方法有两种。</p></li><ul class=" list-paddingleft-2"><li><p>一种是事先过滤，是预测性的，过滤什么，不过滤什么，靠的是少数编辑；</p></li><li><p>另一种是事后过滤，我们不知道什么东西是好的，什么东西是流行的，我们只懂得监测，观察，分析，靠的是大量的用户数据。</p></li></ul><li><p>事先过滤。</p></li><p>靠的是少数编辑对于大众口味，流行趋势的把握，他们最乐于的事情是根据自己的判断与理解调整所推荐的内容和位置，并从事后的Pageview的反映来检测自己的价值判断，通过这样的反复检测，最终形成自己对于流行，对于热点的理解，以此为指导思想来承担过滤的工作。事实上，经过一系列的正反馈的锻炼之后，成就了一个优秀的编辑，我们这个世界的热点就是由这样一群人所制造出来的。他们的价值不可否认。</p><li><p>事后过滤。</p></li><p>靠的科学的对大量的用户数据进行挖掘，无论是是根据Pagerank来判断一个网页的价值，还是根据浏览者的喜好趋势推荐相关物品，又或者根据一系列小众群体的收藏，评论，digg行为找出优质内容，这样的手段都是2.0的核心。</p></ol><p>网站运营之前我们还需要更深入的了解下我们的网站：</p><ol class=" list-paddingleft-2"><li><p>我们的网站为谁服务？目的是什么？</p></li><li><p>正在做什么？如何做的更好？</p></li><li><p>互联网的100：9：1法则同样适用我们的网站--大部分用户不做任何事情</p></li><li><p>给用户足够的提示或演示，让用户明白他们正在做什么</p></li><li><p>核心用户最为重要，我们必须花精力培养核心用户，并维护好他们，特别是在早期。</p></li><li><p>同时要引导新鲜的血液。欢迎他们，引导他们，满足他们。</p></li><li><p>寻找提供给用户可玩性的功能，满足游戏心态。如，提供排行榜让用户更积极。</p></li><li><p>请记住我们不是网站的“主人”。记住：用户只是在我们这开party，而我们并不是他们的“老板”</p></li><li><p>信誉重于生命。对人如此，对网站同样如此。</p></li></ol><p>现实一些，别好高骛远，目标庞大常常导致失败。</p><ul class=" list-paddingleft-2"><li><p>少就是多，把产品做得尽可能的简单，小巧。</p></li><li><p>注重前期的设计，尤其是界面的。</p></li><li><p>宁可做一点点好东西，也不要一大堆垃圾。也就是宁缺勿滥，这个用于项目目标，用于选人，也用于项目本身的进度。</p><p><img src="http://chiqinga.com/uploads/20180416/695352a0b5b27c5a1d01653129c1bb72.jpg" alt="695352a0b5b27c5a1d01653129c1bb72.jpg"/></p><p><br/></p></li></ul>','分享，web','网络','1','0','0','1474711848','1209','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('35','18','浅谈 JavaScript 编程语言的编码规范','JavaScript 编程语言作为最流行的客户端脚本语言，早已被众多 Web 开发人员所熟悉。随着 Web2.0 时代的到来和 ……','/uploads/20180416\11d54bea6477d577b8e7ee986d64424f.jpg','<p style="margin-top: 0px; margin-bottom: 0px; padding: 6px 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: Arial, sans-serif; color: rgb(34, 34, 34); line-height: 1.5em; white-space: normal; font-size: 1.166em !important;"><img src="/uploads/20180416/11d54bea6477d577b8e7ee986d64424f.jpg" alt="11d54bea6477d577b8e7ee986d64424f.jpg"/></p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">对于熟悉 C/C++ 或 Java 语言的工程师来说，JavaScript 显得灵活，简单易懂，对代码的格式的要求也相对松散。很容易学习，并运用到自己的代码中。也正因为这样，JavaScript 的编码规范也往往被轻视，开发过程中修修补补，最终也就演变成为后续维护人员的恶梦。软件存在的长期价值直接与编码的质量成比例。编码规范能帮助我们降低编程中不必要的麻烦。而 JavaScript 代码是直接发送给客户浏览器的，直接与客户见面，编码的质量更应该受到关注。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">本文浅谈 JavaScript 编程中关于编码规范的问题，分析其中缘由。希望引起更多 Web 开发人员对 JavaScript 编码规范问题的关注和对软件产品质量问题的重视。</p><h2 style="font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; margin: 8px 0px 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-size: 24px; line-height: 32px; white-space: normal; background-color: rgb(255, 255, 255);"><a style="color: rgb(78, 161, 219); outline: 0px; margin: 0px; padding: 0px; font-weight: 400; box-sizing: border-box;"></a>前言</h2><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">提及 C/C++ 和 Java 编码规范，相信许多工程师并不生疏。但说到 JavaScript 语言的编码规范，也许您会忍俊不禁。JavaScript 不是语法很灵活吗？变量随时用随时可以声明；语句结束符可以不要；字符串和数字也可以相加；参数多一个少一个也不会报错。没错，当您从 C/C++ 和 Java 严格的语法规定之下，转向 JavaScript 语言，会觉得自由了很多，轻松了很多。语法松散是 JavaScript 重要的特征。它灵活易懂，给开发人员带来了很多方便，但如果编写过程中不注意，代码的调试成本和维护成本则会无形地增加。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">JavaScript 编码会随应被直接发送到客户端的浏览器，代码规范不只是代码质量的保证，也影响到产品的长期信誉。希望 JavaScript 编程语言的规范问题也能同样引起更多朋友的关注。</p><h2 style="font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; margin: 8px 0px 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-size: 24px; line-height: 32px; white-space: normal; background-color: rgb(255, 255, 255);"><a style="color: rgb(78, 161, 219); outline: 0px; margin: 0px; padding: 0px; font-weight: 400; box-sizing: border-box;"></a>JavaScript 编码规范建议</h2><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">本文就 JavaScript 编码过程中涉及的排版、命名、声明、作用域、及一些特殊符号的使用等方面，根据个人在学习工作中的总结，给出自己的一些建议，并分析其中缘由，以供参考。</p><h3 style="font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; margin: 8px 0px 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-size: 22px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);"><a style="color: rgb(78, 161, 219); outline: 0px; margin: 0px; padding: 0px; font-weight: 400; box-sizing: border-box;"></a>JavaScript 文件引用</h3><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">JavaScript 程序应该尽量放在 .js 的文件中，需要调用的时候在 HTML 中以 &lt;script src=&quot;filename.js&quot;&gt; 的形式包含进来。JavaScript 代码若不是该 HTML 文件所专用的，则应尽量避免在 HTML 文件中直接编写 JavaScript 代码。因为这样会大大增加 HTML 文件的大小，无益于代码的压缩和缓存的使用。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">另外，&lt;script src=&quot;filename.js&quot;&gt; 标签应尽量放在文件的后面。这样会降低因加载 JavaScript 代码而影响页面中其它组件的加载时间。</p><h3 style="font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; margin: 8px 0px 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-size: 22px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);"><a style="color: rgb(78, 161, 219); outline: 0px; margin: 0px; padding: 0px; font-weight: 400; box-sizing: border-box;"></a>代码排版</h3><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);"><span style="box-sizing: border-box; font-weight: 700;">行长度</span></p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);"><span style="box-sizing: border-box; font-weight: 700;">每行代码应小于 80 个字符。</span>如果代码较长，应尽量选择换行，下一行代码应缩进 8 个空格。这样可以使代码排版整齐，减轻阅读代码的疲劳感。换行缩进 8 个空格可以和代码段的缩进 4 个空格区分开，以增强代码的可阅读性。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);"><span style="box-sizing: border-box; font-weight: 700;">行结束</span></p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">JavaScript 语句应该以分号结束。但大多数浏览器允许不写分号，只要在本应是分号的地方有一个换行符就行。但是如果代码行较长需要换行的时候，有哪些注意事项呢？换行应选择在操作符和标点符号之后，最好是在逗号&#39;,&#39;之后，而不要在变量名、字符串、数字、或&#39;)&#39; &#39;]&#39; &#39;++&#39; &#39;--&#39;等符号之后换行。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">这样可以有效的防止拷贝、粘贴而引起的错误，并可有效地增强代码的可阅读性。请见清单 1，代码的输出符合我们的期望。但就写法而言，对 valueB 的赋值语句是在变量 valueA 之后进行的换行，这很容易被误解为 valueB=ValueA，给阅读造成障碍。而对 valueC 的复制语句是在&#39;+&#39;之后进行的换行，就容易理解的多。这也是本文所提倡的换行方式。</p><h5 style="font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; margin: 8px 0px 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-size: 18px; line-height: 26px; white-space: normal; background-color: rgb(255, 255, 255);">清单 1. 行结束的位置</h5><pre class="displaycode" style="margin-top: 0px; margin-bottom: 24px; padding: 0px; box-sizing: border-box; background-color: rgb(240, 240, 240); overflow-x: auto; font-family: Consolas, Inconsolata, Courier, monospace; font-size: 14px; line-height: 22px; color: rgb(0, 0, 0);">&nbsp;&lt;script&nbsp;language=&quot;javascript&quot;&gt;&nbsp;
&nbsp;var&nbsp;valueA&nbsp;=&nbsp;1;&nbsp;
&nbsp;var&nbsp;valueB&nbsp;=&nbsp;valueA&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;///bad&nbsp;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;+1;&nbsp;
&nbsp;var&nbsp;valueC&nbsp;=&nbsp;valueB&nbsp;+&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;///good&nbsp;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;valueA;&nbsp;
&nbsp;alert(valueB);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//output:&nbsp;valueB=2&nbsp;
&nbsp;alert(valueC);//output:&nbsp;valueC=3&nbsp;
&nbsp;&lt;/script&gt;</pre><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);"><span style="box-sizing: border-box; font-weight: 700;">缩进</span></p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">关于缩进的问题，不只是 JavaScript，几乎所有的语言编写的时候，都会提及缩进的问题。缩进几乎是代码编写规范的第一课，是代码可阅读性判断的直接因素。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">代码缩进的好处是不言而喻的，但是对于如何缩进，则没有标准而言。最受欢迎的是方便使用 TAB 键缩进，也有些喜欢用 2 个、4 个、8 个空格进行缩进。这样缩进风格不一，也同样给代码的阅读带来障碍。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">本文提倡用&nbsp;<span style="box-sizing: border-box; font-weight: 700;">4 个空格</span>来进行缩进，并在同一产品中采用同一种缩进标准。不支持用 TAB 键进行缩进。这是因为直到现在还没有统一的标准来定义 TAB 键所代替的空白大小，有些编辑器解析为 4 个空格大小，有些则解析为 8 个。因而用不同的编辑器查看代码，可能造成格式混乱。当然 TAB 简单易用，为解决这个问题，建议在设置开发环境时，将编辑器里的 TAB 快捷键重新设置为 4 个空格。据了解 Eclipse, Vi, Nodepad++，Editplus, UltraEdit 等流行的编辑器，均提供了此功能。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);"><span style="box-sizing: border-box; font-weight: 700;">注释</span></p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">代码中的注释很重要，自然也是毋庸置疑的。通常我们会强调代码中注释数量的多少，而轻视了对注释质量的提高。编码是及时添加注释，会给后续代码的维护人员带来很大的便利。但是如果注释不注意更新，或者由于拷贝、粘贴引起的错误的注释，则会误导阅读人员，反而给阅读带来障碍。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">除了注释要&nbsp;<span style="box-sizing: border-box; font-weight: 700;">及时更新</span>外，我们还应对注释的内容要特别关注。注释要尽量简单、清晰明了，避免使用含混晦涩的语言，同时着重&nbsp;<span style="box-sizing: border-box; font-weight: 700;">注释的意义</span>，对不太直观的部分进行注解。请见清单 2。</p><h5 style="font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; margin: 8px 0px 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-size: 18px; line-height: 26px; white-space: normal; background-color: rgb(255, 255, 255);">清单 2. 有意义的注释</h5><pre class="displaycode" style="margin-top: 0px; margin-bottom: 24px; padding: 0px; box-sizing: border-box; background-color: rgb(240, 240, 240); overflow-x: auto; font-family: Consolas, Inconsolata, Courier, monospace; font-size: 14px; line-height: 22px; color: rgb(0, 0, 0);">&nbsp;&lt;script&nbsp;language=&quot;javascript&quot;&gt;&nbsp;
&nbsp;//following&nbsp;section&nbsp;is&nbsp;used&nbsp;to&nbsp;initialize&nbsp;golbal&nbsp;variables&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(good)&nbsp;
&nbsp;var&nbsp;valueA&nbsp;=&nbsp;0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//initialize&nbsp;&nbsp;valueA&nbsp;to&nbsp;be&nbsp;sero&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(bad)&nbsp;
&nbsp;var&nbsp;valueB&nbsp;=&nbsp;1;&nbsp;
&nbsp;...&nbsp;
&nbsp;//call&nbsp;f1&nbsp;function&nbsp;after&nbsp;waiting&nbsp;for&nbsp;50&nbsp;seconds.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(good)&nbsp;
&nbsp;setTimeout(f1,50000);&nbsp;//set&nbsp;timeout&nbsp;to&nbsp;be&nbsp;20s&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(copy&nbsp;error)&nbsp;
&nbsp;...&nbsp;
&nbsp;&lt;/script&gt;</pre><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">这样的注释方式在 JavaScript 代码中经常见到。&quot;initialize valueA to be sero&quot; 这样的注释有什么用呢？难道阅读程序的工程师从&quot;var valueA = 0;&quot;复制语句中看不出来么？&quot;set timeout to be 20s&quot;这条注释，不只是因拷贝、粘贴引起的时间大小的错误，同时也误导了程序员对这条语句的理解。setTimeout() 函数的作用并非是设置函数执行的超时时间，而是等待一定时间后执行所调用的函数，害人匪浅呀。这样的注释内容宁可删掉。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">此外，JavaScript 的注释有两种&quot;//&quot; 和&quot;/* .... */&quot;，建议&quot;//&quot;用作代码行注释，&quot;/* .... */&quot;形式用作对整个代码段的注销，或较正式的声明中，如函数参数、功能、文件功能等的描述中。</p><h3 style="font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; margin: 8px 0px 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-size: 22px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);"><a style="color: rgb(78, 161, 219); outline: 0px; margin: 0px; padding: 0px; font-weight: 400; box-sizing: border-box;"></a>标识符命名</h3><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">JavaScript 中的标识符的命名规则：</p><ul class="ibm-bullet-list list-paddingleft-2" style="list-style-type: none;"><li><p>以字母、下划线&#39;_&#39;或美元符号&#39;$&#39;开头</p></li><li><p>允许名称中包含字母，数字，下划线&#39;_&#39;和美元符号&#39;$&#39;</p></li><li><p>区分大小写</p></li></ul><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">变量、参数、成员变量、函数等名称均以小写字母开头，构造器的名称以大写字母开头。下划线&#39;_&#39;开头的变量一般习惯于标识私有 / 局部成员。而美元符号&#39;$&#39;开头的变量习惯于标识系统相关，比如系统进程等。应避免用下划线&#39;_&#39;或美元符号&#39;$&#39;来命名标识符。尽可能地降低代码的阅读负担。</p><h3 style="font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; margin: 8px 0px 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-size: 22px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);"><a style="color: rgb(78, 161, 219); outline: 0px; margin: 0px; padding: 0px; font-weight: 400; box-sizing: border-box;"></a>声明</h3><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);"><span style="box-sizing: border-box; font-weight: 700;">变量的声明</span></p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">尽管 JavaScript 语言并不要求在变量使用前先对变量进行声明。但我们还是应该养成这个好习惯。这样可以比较容易的检测出那些未经声明的变量，避免其变为隐藏的全局变量，造成隐患。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">在函数的开始应先用 var 关键字声明函数中要使用的局部变量，注释变量的功能及代表的含义，且应以字母顺序排序。每个变量单独占一行，以便添加注释。这是因为 JavaScript 中只有函数的 {} 表明作用域，用 var 关键字声明的局部变量只在函数内有效，而未经 var 声明的变量则被视为全局变量。我们来看下清单 3。</p><h5 style="font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; margin: 8px 0px 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-size: 18px; line-height: 26px; white-space: normal; background-color: rgb(255, 255, 255);">清单 3. 局部变量声明</h5><pre class="displaycode" style="margin-top: 0px; margin-bottom: 24px; padding: 0px; box-sizing: border-box; background-color: rgb(240, 240, 240); overflow-x: auto; font-family: Consolas, Inconsolata, Courier, monospace; font-size: 14px; line-height: 22px; color: rgb(0, 0, 0);">			&nbsp;&lt;script&nbsp;language=&quot;javascript&quot;&gt;&nbsp;
&nbsp;var&nbsp;valueA&nbsp;&nbsp;=&nbsp;&quot;a&quot;;&nbsp;
&nbsp;var&nbsp;valueB&nbsp;&nbsp;=&nbsp;&quot;b&quot;;&nbsp;
&nbsp;function&nbsp;f1()&nbsp;{&nbsp;
&nbsp;&nbsp;&nbsp;var&nbsp;valueA&nbsp;=&nbsp;&quot;c&quot;;&nbsp;
&nbsp;&nbsp;&nbsp;alert(&quot;valueA=&quot;+valueA);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//output:&nbsp;valueA=c&nbsp;
&nbsp;&nbsp;&nbsp;valueB&nbsp;=&nbsp;&quot;d&quot;;&nbsp;
&nbsp;&nbsp;&nbsp;alert(&quot;valueB=&quot;+valueB);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//output:&nbsp;valueB=d&nbsp;
&nbsp;}&nbsp;
&nbsp;f1();&nbsp;
&nbsp;alert(&quot;valueA=&quot;+valueA);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//output:&nbsp;valueA=a&nbsp;
&nbsp;alert(&quot;valueB=&quot;+valueB);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//output:&nbsp;valueB=d&nbsp;
&nbsp;&lt;/script&gt;</pre><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">从上例的输出惊奇地发现，用 var 声明过的变量 valueA 和没有声明的变量 valueB 是有区别的。特别需要注意的是，在函数内部用 var 声明的变量为局部变量，这样可以有效地避免因局部变量和全局变量同名而产生的错误。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);"><span style="box-sizing: border-box; font-weight: 700;">函数的声明</span></p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">函数也应在调用前进行声明，内部函数应在 var 声明内部变量的语句之后声明，可以清晰地表明内部变量和内部函数的作用域。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">此外，函数名紧接左括号&#39;(&#39;之间，而右括号&#39;)&#39;和后面的&#39;{&#39;之间要有个空格，以清楚地显示函数名以其参数部分，和函数体的开始。若函数为匿名 / 无名函数，则 function 关键字和左括号&#39;(&#39;之间要留空格，否则可能误认为该函数的函数名为 function。</p><h5 style="font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; margin: 8px 0px 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-size: 18px; line-height: 26px; white-space: normal; background-color: rgb(255, 255, 255);">清单 4. 内部函数声明</h5><pre class="displaycode" style="margin-top: 0px; margin-bottom: 24px; padding: 0px; box-sizing: border-box; background-color: rgb(240, 240, 240); overflow-x: auto; font-family: Consolas, Inconsolata, Courier, monospace; font-size: 14px; line-height: 22px; color: rgb(0, 0, 0);">			&nbsp;&lt;script&nbsp;language=&quot;javascript&quot;&gt;&nbsp;
&nbsp;var&nbsp;innerA&nbsp;=&nbsp;1;&nbsp;
&nbsp;function&nbsp;outF()&nbsp;{&nbsp;
&nbsp;&nbsp;&nbsp;var&nbsp;innerA&nbsp;=&nbsp;2;&nbsp;
&nbsp;&nbsp;&nbsp;function&nbsp;_inF()&nbsp;{&nbsp;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;valueA=&quot;+innerA);&nbsp;
&nbsp;&nbsp;&nbsp;}&nbsp;
&nbsp;&nbsp;&nbsp;_inF();&nbsp;
&nbsp;}&nbsp;
&nbsp;outF();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//output:&nbsp;valueA=2&nbsp;
&nbsp;_inF();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//error:&nbsp;innerF&nbsp;is&nbsp;not&nbsp;defined&nbsp;
&nbsp;&lt;/script&gt;</pre><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">从清单 4 的输出可以看出，inF() 函数仅在 outF() 函数的内部生效，局部变量 innerA 对内部函数的作用域生效。这样的编码方式使得变量和函数的作用域变得清晰。</p><h3 style="font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; margin: 8px 0px 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-size: 22px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);"><a style="color: rgb(78, 161, 219); outline: 0px; margin: 0px; padding: 0px; font-weight: 400; box-sizing: border-box;"></a>语句</h3><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">对于简单语句而言，需要提及的仍然是分号必要性，同时，一行最多有一个语句。如果一个赋值语句是用函数和对象来赋值，可能需要跨多行，一定切记要在赋值语句末加上分号。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">这是因为 JavaScript 中，所有表达式都可以当语句，遇换行符时会解析为表达式的结束，此时不规范的换行和分号的丢失，可能引入新的错误。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">对于复合语句，if, for, while, do, switch, try … catch 等代码体，函数定义的函数体，对象的定义等都需要放在花括号&#39;{}&#39;里面。</p><ul class="ibm-bullet-list list-paddingleft-2" style="list-style-type: none;"><li><p>&#39;{&#39; 应在行末，标志代码块的开始。</p></li><li><p>&#39;}&#39; 应在一行开头，标志代码块的结束，同时需要和&#39;{&#39;所在行的开始对齐，以表明一个完整的复合语句段。这样可以极大地提高代码的可阅读性，控制逻辑能清晰地表现出来。</p></li><li><p>被包含的代码段应该再缩进 4 个空格。</p></li><li><p>即使被包含的代码段只有一句，也应该用花括号&#39;{}&#39;包含。尽管不用花括号代码也不会错，但如若需要增加语句的话，则较容易因花括号遗漏而引起的编译错误或逻辑错误。</p></li></ul><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);"><span style="box-sizing: border-box; font-weight: 700;">return</span>语句在使用时也需慎重，如果用表达式的执行作为返回值，请把表达式和 return 放在同一行中，以免换行符被误解析为语句的结束而引起返回错误。return 关键字后若没有返回表达式，则返回 undefined。构造器的默认返回值为 this。</p><h5 style="font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; margin: 8px 0px 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-size: 18px; line-height: 26px; white-space: normal; background-color: rgb(255, 255, 255);">清单 5. return 表达式</h5><pre class="displaycode" style="margin-top: 0px; margin-bottom: 24px; padding: 0px; box-sizing: border-box; background-color: rgb(240, 240, 240); overflow-x: auto; font-family: Consolas, Inconsolata, Courier, monospace; font-size: 14px; line-height: 22px; color: rgb(0, 0, 0);">			&nbsp;&lt;script&nbsp;language=&quot;javascript&quot;&gt;&nbsp;
&nbsp;function&nbsp;F1()&nbsp;{&nbsp;
&nbsp;&nbsp;&nbsp;var&nbsp;valueA&nbsp;&nbsp;=&nbsp;1;&nbsp;
&nbsp;&nbsp;&nbsp;var&nbsp;valueB&nbsp;&nbsp;=&nbsp;2;&nbsp;
&nbsp;&nbsp;&nbsp;return&nbsp;valueA&nbsp;+&nbsp;valueB;&nbsp;
&nbsp;}&nbsp;
&nbsp;function&nbsp;F2()&nbsp;{&nbsp;
&nbsp;&nbsp;&nbsp;var&nbsp;valueA&nbsp;&nbsp;=&nbsp;1;&nbsp;
&nbsp;&nbsp;&nbsp;var&nbsp;valueB&nbsp;&nbsp;=&nbsp;2;&nbsp;
&nbsp;&nbsp;&nbsp;return&nbsp;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;valueA&nbsp;+&nbsp;valueB;&nbsp;
&nbsp;}&nbsp;
&nbsp;alert(&nbsp;F1()&nbsp;);&nbsp;&nbsp;//output:&nbsp;3&nbsp;
&nbsp;alert(&nbsp;F2()&nbsp;);&nbsp;&nbsp;//ouput:&nbsp;undefined&nbsp;
&nbsp;&lt;/script&gt;</pre><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">在清单 5 中显示了因返回表达式没有和 return 关键字放在同一行而引起的返回错误，需重视。</p><h3 style="font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; margin: 8px 0px 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-size: 22px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);"><a style="color: rgb(78, 161, 219); outline: 0px; margin: 0px; padding: 0px; font-weight: 400; box-sizing: border-box;"></a>特殊符号</h3><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);"><span style="box-sizing: border-box; font-weight: 700;">空白符</span></p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">适当的空白行可以大大提高代码的可阅读性，可以使代码逻辑更清晰易懂。同时，在表达式中适当的留空白，也会给代码的阅读带来方便。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">关键字的后面如有括号，则最好在关键字和左括号&#39;(&#39;之间留空白，如 for, if, while 等。而函数名和括号之间则不宜留空白，但若是匿名函数，则必须在 function 和左括号&#39;(&#39;之间留空白，否则，编辑器会误认为函数名为 function。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">在表达式中，二元运算符 ( 除左括号&#39;(&#39;，左方括号&#39;[&#39;，作用域点&#39;.&#39;) 和两个操作数之间最好留空白。一元运算符（若不是词 typeof 等）和其操作数之间不宜留空白。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">逗号&#39;,&#39;的后面需要留空白，以显示明确的参数间隔，变量间隔等。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">分号&#39;;&#39;之后通常表明表达语句的结束，而应空行。在 for 的条件语句中，分号之后则应该留空白。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);"><span style="box-sizing: border-box; font-weight: 700;">{ } 和 [ ]</span></p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">在 JavaScript 中，如需定义空对象和空数组，通常很自然地想到用 new Object() 和 new Array() 的方法。其实花括号&#39;{}&#39;和方括号&#39;[]&#39;可以直接用来定义一个空对象和一个空数组。这种书写方法可以使代码看起来简单易懂。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);"><span style="box-sizing: border-box; font-weight: 700;">== 和 ===</span></p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">判断&quot;逻辑等&quot;在代码里太平常的不过事情了，但 JavaScript 与其他熟知的编程语言不同的是，除了可以使用两个等号&#39;==&#39;来作判断以为，还可以使用三个等号&#39;===&#39;来进行逻辑等判断。两者的不同是&#39;==&#39;作逻辑等判断时，会先进行类型转换后再进行比较。&#39;===&#39;则不会。因而，&#39;==&#39;进行的判断结果可能产生偏差。&#39;!=&#39;与&#39;!==&#39;的区别亦是如此。本文提倡尽量使用&#39;===&#39;来进行逻辑等的判断，用&#39;!==&#39;进行逻辑不等的判断。</p><h5 style="font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; margin: 8px 0px 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-size: 18px; line-height: 26px; white-space: normal; background-color: rgb(255, 255, 255);">清单 6. === 的使用</h5><pre class="displaycode" style="margin-top: 0px; margin-bottom: 24px; padding: 0px; box-sizing: border-box; background-color: rgb(240, 240, 240); overflow-x: auto; font-family: Consolas, Inconsolata, Courier, monospace; font-size: 14px; line-height: 22px; color: rgb(0, 0, 0);">			&nbsp;&lt;script&nbsp;language=&quot;javascript&quot;&gt;&nbsp;
&nbsp;var&nbsp;valueA&nbsp;=&nbsp;&quot;1&quot;;&nbsp;
&nbsp;var&nbsp;valueB&nbsp;=&nbsp;1;&nbsp;
&nbsp;if&nbsp;(&nbsp;valueA&nbsp;==&nbsp;valueB)&nbsp;{&nbsp;
&nbsp;&nbsp;&nbsp;alert(&quot;Equal&quot;);&nbsp;
&nbsp;}&nbsp;
&nbsp;else&nbsp;{&nbsp;
&nbsp;&nbsp;&nbsp;alert(&quot;Not&nbsp;equal&quot;)&nbsp;
&nbsp;}&nbsp;
&nbsp;//output:&nbsp;&quot;Equal&quot;
&nbsp;if&nbsp;(&nbsp;valueA&nbsp;===&nbsp;valueB)&nbsp;{&nbsp;
&nbsp;&nbsp;&nbsp;alert(&quot;Equal&quot;);&nbsp;
&nbsp;}&nbsp;
&nbsp;else&nbsp;{&nbsp;
&nbsp;&nbsp;&nbsp;alert(&quot;Not&nbsp;equal&quot;)&nbsp;
&nbsp;}&nbsp;
&nbsp;//output:&nbsp;&quot;Not&nbsp;equal&quot;
&nbsp;&lt;/script&gt;</pre><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">清单 6 中，valueA 和 valueB 两个变量的值显然是不相等的，起码 valueA 是个字符串，而 valueB 是一个数字。但用&#39;==&#39;进行判断是，程序却输出相等的字样。这是因为编译器对两个变量进行比较时，因为他们的类型不同，而自动地将 valueB 转换成字符串，而后再和 valueA 进行比较的。用&#39;===&#39;得到的判断结果正和预期的结果相符。</p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);"><span style="box-sizing: border-box; font-weight: 700;">+</span></p><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">加号&#39;+&#39;也同样是程序员所熟知的操作符之一。JavaScript 和其他编程语言不同的是，在 JavaScript 中，&#39;+&#39;除了表示数字值相加，字符串相连接以外，还可以作一元运算符用，把字符串转换为数字。因而如果使用不当，则可能与自增符&#39;++&#39;混淆而引起计算错误。这一点，在清单 7 中可以清楚地看出。</p><h5 style="font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; margin: 8px 0px 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-size: 18px; line-height: 26px; white-space: normal; background-color: rgb(255, 255, 255);">清单 7. 巧用 + 号</h5><pre class="displaycode" style="margin-top: 0px; margin-bottom: 24px; padding: 0px; box-sizing: border-box; background-color: rgb(240, 240, 240); overflow-x: auto; font-family: Consolas, Inconsolata, Courier, monospace; font-size: 14px; line-height: 22px; color: rgb(0, 0, 0);">			&nbsp;&lt;script&nbsp;language=&quot;javascript&quot;&gt;&nbsp;
&nbsp;var&nbsp;valueA&nbsp;=&nbsp;20;&nbsp;
&nbsp;var&nbsp;valueB&nbsp;=&nbsp;&quot;10&quot;;&nbsp;
&nbsp;alert(&nbsp;valueA&nbsp;+&nbsp;valueB);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//ouput:&nbsp;2010&nbsp;
&nbsp;alert(&nbsp;valueA&nbsp;+&nbsp;(+valueB));&nbsp;//output:&nbsp;30&nbsp;
&nbsp;alert(&nbsp;valueA&nbsp;+&nbsp;+valueB);&nbsp;&nbsp;&nbsp;&nbsp;//output:30&nbsp;
&nbsp;alert(&nbsp;valueA&nbsp;++valueB);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//Compile&nbsp;error&nbsp;
&nbsp;&lt;/script&gt;</pre><h2 style="font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; margin: 8px 0px 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-size: 24px; line-height: 32px; white-space: normal; background-color: rgb(255, 255, 255);"><a style="color: rgb(78, 161, 219); outline: 0px; margin: 0px; padding: 0px; font-weight: 400; box-sizing: border-box;"></a>总结</h2><p style="margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; background-color: rgb(255, 255, 255);">本文就 JavaScript 代码的排版、命名、声明、语句、和一些特殊字符的使用等方面，谈了自己对 JavaScript 编程规范的建议。此外，还有许多方面需要深入了解研究，如 with, eval 语句和 this 对象的使用等等。我们在认识其普遍性的同时也需要注意其特殊性，在编写代码时多用心留意，以创造更多更优质的程序代码。</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 6px 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: Arial, sans-serif; color: rgb(34, 34, 34); line-height: 1.5em; white-space: normal; font-size: 1.166em !important;"><br/></p>','web,分享','网络转载','1','0','0','1475791450','1309','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('36','11','通货膨胀对什么人有好处？普通人如何应对或是从中牟利呢？','小时候，很小的时候数学课讲完利息以后，我真的有想过，如果我有 20 万块钱，我存进银行，如果一年 3.65% 利息的话，我一天就能得到 20 块的利……','/uploads/20180416\b591d434c10fe9c672e6e7b08878146c.jpg','<p><img src="/uploads/20180416/b591d434c10fe9c672e6e7b08878146c.jpg" alt="b591d434c10fe9c672e6e7b08878146c.jpg"/></p><p>　　小时候，很小的时候数学课讲完利息以后，我真的有想过，如果我有 20 万块钱，我存进银行，如果一年 3.65% 利息的话，我一天就能得到 20 块的利息（当然按照小学数学教的，要扣除 20% 利息税。）</p><p>我是真的这么想过……小时候觉得，我草，一天能有 20 块钱，我可是土豪啊，当时心里美滋滋的，当时目标就是我长大要赚 20 万然后存银行当土豪……</p><p>　　后来长大了，学到了更高级的知识，到了 07 年，觉得我小时候真是个蠢 B，有钱去炒股多好，三天一倍妥妥的没压力，20 万变 40 万，40 万变 80 万，80 万变 160 万，毫无压力。07 年我身边什么阿猫阿狗都在说炒股能赚钱。当时觉得我要是能有 20 万，一年时间妥妥 1000 万无压力。</p><p>　　再后来，学了尼玛保险学，进了坑，发现小时候的我简直弱爆了！我要有 20 万，买一份终生年金，年轻好好工作赚钱养活自己，老了以后一个月一万多吃香喝辣大保健毫无压力！</p><p>　　到了现在，我发现之前的我都是傻逼，</p><p>　　今年 5 块钱一碗的牛肉拉面到了明年变 8 块，存款不靠谱！</p><p>　　07 年赚的盘满钵满，到了 08 年头一夜回到解放前，炒股不靠谱！</p><p>　　00 年 20 万能在人民广场买套房，到了 30 年估计 20 万在崇明岛上买个厕所都不够，保险不靠谱！</p><p><strong>　　我想，这就是普通人对于「理财既跑不过通胀，也干不过风险」的困扰。</strong></p><p>　　炒股？不靠谱，一个股灾一夜回到解放前。</p><p>　　存款？不靠谱，利息还没通胀跑得快。</p><p>　　保险？更 TM 不靠谱了，20 年后的钱还叫钱？</p><p><strong>　　为什么我的老板钱都花不完，但是我的工资看着在涨，为啥越来越不够花？</strong></p><p><strong>　　这就是资本主义市场经济的邪恶之处，凯恩斯曾经提出一个理论，这个理论是现在经济学必学的一个基础理论：</strong></p><blockquote>货币错觉<br/><br/>劳动者都对直观的金钱敏感，而不对通货膨胀敏感。由于他们对直观的金钱敏感，所以给劳动者降工资万万不可行，所谓「工资曲线的下方硬直性」，也就是说工资只能升不能降。<br/>但是企业家们想要更多的利润来扩大发展，扩充资本怎么办？通过通货膨胀来掩盖工资下降的事实。<br/><br/>例 1：物价下降了 3%，老板给你降了 2% 工资。<br/>你：「抗议！为啥我的工资降了，我辛辛苦苦工作！」<br/>老板：「傻 X，给你涨工资你还叫？」<br/><br/>例 2：物价上涨 10%，老板给你涨了 5% 工资。<br/>你：「谢谢老板给我加工资，以后我会更加努力工作！」<br/>老板：「傻 X，把你卖了你还不知道。」<br/><br/><strong>所以普通劳动者在资本家眼里永远是傻 X。<br/></strong></blockquote><p>　　而领工资的普通劳动者却不能死在通货膨胀面前，他们拿起各种武器来保障自己的财产。</p><p><strong>　　有些人偏好风险，他们想要更多钱，于是他们选择了高风险投资，股市一波流，大力出奇迹，不赌一波谁知道。</strong></p><p><strong>　　有些人厌恶风险，只想守好自己的一亩三分地，于是他们选择了低风险投资，买了保险，存了银行，老了无忧。</strong></p><p><strong>　　有些人求稳，买信托，亏了也亏不多，基本还能赚一点。</strong></p><p><strong>　　大家都在和通货膨胀作斗争。</strong></p><p>　　当然更多人还是选择了大家都知道的捷径：</p><p>　　买房，这种投资风险极大，但是却不知道风险什么时候来。房价一跌，只要手上有房的全完蛋，然而大家总觉得房价稳着呢。于是大家都去买房。</p><p>　　所以我从来不会去指责买房保值的普通人们，我也不能理解为什么有人指责买房保值的人，人家辛辛苦苦有点积蓄买房保值，顶着巨大的风险守着自己的一亩三分地，难道有错吗？</p><p><strong>　　通货膨胀面前，靠劳动的永远玩不过靠资本的，这是一条永恒的真理。</strong></p><p>　　大家只能靠各种工具尽量让自己的钱变慢的少一点，我国一年通胀大约 4%，买个银行理财，买个保险差不多刚好能保值。</p><p>　　而掌握资本的人靠着投资攫取着劳动者创造的价值。</p><p><strong>　　在通货膨胀面前，普通人永远是输家，</strong>不论是 80 年代的日本，90 年代的美国，还是 00 年代的中国。</p><p><strong>　　抱歉，这道题无解。</strong></p><p><strong>　　兴、百姓苦，亡、百姓苦。<br/></strong></p><p>　　我个人建议没有有效投资方式的普通人，尤其是工薪族，对养老、赡养老人抚养孩子有后顾之忧的，还是选取比较有保障的保值方式，银行理财，保险，长期国债，目前都能提供 4% 以上的收益率，是有效对抗通胀的方式。</p><p>股市、楼市，虽然收益可观，但是对于普通人来说，风险太难掌控，因此投资股市楼市需要有很强的自制力，<strong>千万不要贪，见好就走</strong>，一贪毁一生。而我国楼市和股市和政策的关联太强，都是政策市，谁也不敢预测，因此非机构普通人进去基本属于撞撞运气，如果豁得出去，可以考虑。</p><p>　　互联网融资股权众筹，民间借贷，<strong>碰都不要碰。</strong></p><p>　　如果没有确定把握，不要轻易尝试辞职创业，风险太大。</p><p>　　也可以<strong>多者组合</strong>，存点钱，买点保险和国债，<strong>保险和国债是完全没有风险的，银行的保本型理财信托风险也相对比较小。</strong>在有效保障日后生活、老人赡养孩子抚养之后，再把自己的余钱拿去进行高风险投资股票房产，起码有个后路，没有后顾之忧。<strong>切勿把全部身家押进高风险投资，命比钱重要，家庭幸福也比钱重要。</strong></p><p>　　这可能是普通人唯一能对抗通胀的武器了。</p><p>　　……</p><p>　　这位朋友的路子不错 - - 问银行借钱好像确实能有效抵御通胀……</p><p>　　哦，补充一个，美国 NBA 前著名球星阿伦艾弗森，由于挥霍无度，喜爱逛夜店和赌场，还养着一群白眼狼兄弟。数年来败光了赚来的几亿美金。身无分文，孩子的抚养费都给不起，不过当年赞助商知道艾弗森花钱无度的恶习，在签订代言合同的时候，有 3200 万美元是以理财产品支付的（虽然新闻没有透露是什么理财产品，不过我猜测应该是一份固定收益年金保险），但是 55 岁之前，艾弗森无法动这笔钱，在艾弗森到 55 岁以后，能够每个月从这笔理财中获得相当可观的收入，过上衣食无忧的老年生活。</p><p>　　艾弗森现在 41 岁，过个 14 年穷困潦倒的生活，应该会更加珍视来之不易的财富吧，3200 万，多年增值也能过上不错的生活了，应该不会像之前那样挥霍无度了，祝愿这名传奇球星晚年幸福。</p><p>　　我不得不说，该公司真的是美国好赞助商。</p><div></div>','web,财经，投资','网络-知乎','1','0','0','1475833592','1645','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('37','10','web开发电子书籍分享','之前学习网站制作，收集过一些电子书籍，感觉对学习有用，今天特意发到百度云盘，现在拿出来，给大家分……','','<p>　　之前学习网站制作，收集过一些电子书籍，感觉对学习有用，今天特意发到百度云盘，现在拿出来，给大家分享：我们的口号是：分享仍是快乐之本。<br/></p><p>&nbsp; 好了，第一本：<img src="http://filesrv.iyunshu.com/C/01131/97871113362421966738-fm.jpg" width="186" height="242"/></p><p>　 HTML5与CSS3权威指南：下载地址&nbsp;<a href="http://pan.baidu.com/s/1mirYdny" target="_self">http://pan.baidu.com/s/1mirYdny</a> 密码: qwha</p><p><br/></p><p>&nbsp; &nbsp;第二本：<img src="http://a2.att.hudong.com/80/98/01200000026885134376984786410_s.jpg" width="186"/></p><p>　 &nbsp;悟透JavaScript：下载地址　<a href="http://pan.baidu.com/s/1jIoEs8Q" target="_self">http://pan.baidu.com/s/1jIoEs8Q</a> 密码: dac6</p><p><br/></p><p>　　第三本：<img src="http://image-7.verycd.com/191503189ced7a5c79d9f4292ed4443e436911/c1.png" width="183" height="238" title="" alt=""/></p><p>&nbsp;&nbsp;深入PHP 面向对象 模式与实践第3版：下载地址&nbsp;<a href="http://pan.baidu.com/s/1hrY3wFi" target="_self">http://pan.baidu.com/s/1hrY3wFi</a> 密码: xs4t</p><p><br/></p><p>　　第四本：<img src="https://img3.doubanio.com/lpic/s1856795.jpg" width="186" height="236" title="" alt=""/></p><p>　　PHP和MYSQL WEB应用开发核心技术：下载地址&nbsp;<a href="http://pan.baidu.com/s/1c2ytWIG" target="_self">http://pan.baidu.com/s/1c2ytWIG</a> 密码: mcn1</p><p><br/></p><p>　　第五本：<img src="https://img3.doubanio.com/lpic/s1958902.jpg" width="186" height="236" title="" alt=""/></p><p>　　JavaScript+DOM编程艺术：&nbsp;<a href="http://pan.baidu.com/s/1mhGKrws" target="_self">http://pan.baidu.com/s/1mhGKrws</a> 密码: txfu</p><p><br/></p><p>　　第六本：<img src="https://images-cn-8.ssl-images-amazon.com/images/I/41Sd55w2MTL._OU28__BG0,0,0,0_FMpng_AC_UL320_SR240,320_.jpg" width="186" height="239" title="" alt=""/></p><p>　　jQuery权威指南（第2版）、jQuery Mobile权威指南:&nbsp;<a href="http://pan.baidu.com/s/1sl4Fdhj" target="_self">http://pan.baidu.com/s/1sl4Fdhj</a> 密码: pfnk</p><p><br/></p><p>　　第七本：<img src="https://timgsa.baidu.com/timg?image&quality=80&size=b9999_10000&sec=1523872431108&di=ba6b3e90e67d7f159db8a72bfa43795e&imgtype=0&src=http%3A%2F%2Fimg3.doubanio.com%2Flpic%2Fs27371732.jpg" title="" alt="" width="186" height="263"/></p><p>　　AngularJS权威教程：&nbsp;<a href="http://pan.baidu.com/s/1kVNOhiZ" target="_self">http://pan.baidu.com/s/1kVNOhiZ</a> 密码: 8h1x</p>','web，php，分享','清蝎子','0','1','0','1476099136','1671','6','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('38','18','网页自动跳转','我的网站以前是用TK域名，后来用了com域名，为了照顾老用户，不得不为他们做个跳转……','http://s6.51cto.com/wyfs02/M00/6D/76/wKioL1VlI26g_PW2AACuOGjgyHw520.jpg','<p>　　<img src="http://s6.51cto.com/wyfs02/M00/6D/76/wKioL1VlI26g_PW2AACuOGjgyHw520.jpg"/></p><p>　　我的网站以前是用TK域名，后来用了com域名，为了照顾老用户，不得不为他们做个跳转功能。</p><p>　　其实这是一个非常简单的网页跳转功能，老鸟可以自动飘过，有须要学习的小伙伴们，下面我就呈上代码，供小伙伴们参考。</p><pre class="brush:html;toolbar:false">&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;http-equiv=&quot;refresh&quot;&nbsp;content=&quot;10;&nbsp;URL=http://www.chiqinga.com&quot;&gt;</pre><p>　　对，你没看错，非常简单，只要在head之间，插入一行代码就行了。content=10;这里代码代表着什么呢？看过我做的跳转网面的小伙伴们，可能都知道，我那里给了个10秒的提示，也就是说，用户打开你的网页，会出现个提示，告诉用户，10秒后，我们会为用户自动跳到新的页面。这里content就是说多少秒后，进行跳转新的网页，你也可以设置为0。URL=&quot;&quot;就是你要跳转到的地址了。</p><p>　　除了这种方法，当然还有许多方法，也很好用，下面再给小伙伴们看看，另一种跳转方法：</p><pre class="brush:html;toolbar:false">&nbsp;&nbsp;&nbsp;&lt;body&nbsp;onload=&quot;parent.location=&#39;http://www.chiqinga.com&#39;&nbsp;&quot;&gt;</pre><p>　　这是一种通过网页加载时，用onload事件指定跳转的方法。这种方法也很简洁，当然，还有别的跳转方法，这里就不一一而谈了。小伙伴们可以自行研究，看看那种好用，就用那种吧!!!</p>','web，琐碎，分享','清蝎子','0','0','0','1476280037','714','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('39','11','阿里负责人揭秘面试潜规则','最近负责了公司招聘，发现校园招聘这种事情，真是一个奇葩的工作。能做好校园招聘的人，一定是一个矛盾体。这个人一定是思维缜密，因为流程在这里……','http://img.managershare.com/uploads/wp/d3d36e3c569601c7.jpg','<div></div><p><img src="http://img.managershare.com/uploads/wp/d3d36e3c569601c7.jpg"/></p><p>　　阿里巴巴校招负责人揭秘面试中那些你应该知道的“潜规则”。</p><p>　　最近负责了公司招聘，发现校园招聘这种事情，真是一个奇葩的工作。能做好校园招聘的人，一定是一个矛盾体。</p><p>　　这个人一定是思维缜密，因为流程在这里特别重要，宣讲会、笔试、面试，需要不能出乱子；这个人还需要有创新意识，做好了宣讲会、笔试面试，也就是中规中矩，没啥特色，如果不弄点有新意的东东出来，也很难找到最优秀的人才。</p><p>　　有人说，这个不矛盾啊。看上去不矛盾，实际上挺矛盾的。因为如果这个人思维缜密，还有很好的创新意识，太难找了。</p><p>　　看了很多大学生过来面试，感触颇多。感触的是，人和人真的很不一样。今天就给大家分享一下我的见闻和感想，不设框架，随便聊聊。</p><p><br/></p><p><strong><span style="font-size: 24px;">考察三个方面</span></strong></p><p>　　无论是哪一种面试，主要就看三个方面：<strong>现在能力如何，未来潜力如何，人品如何。</strong>第一个因素是最重要的，因为后面两个因素有太多的人为判断因素，无法量化。所谓的面试准备，“现在能力如何”很难有大的改变，后面两个因素是可以短时间做一些准备的。</p><p>　　之前有一个很典型的case，有个小伙子能力很强，笔试和初试评价都很高，最后终试的时候，评价特别低，就是因为其被判断为潜力不够。没有钻研精神，对新技术不感兴趣，不愿意深入思考，不主动学习……</p><p><strong><br/></strong></p><p><strong><span style="font-size: 24px;">毕业学校</span></strong></p><p>　　总有学生会问类似的问题：你们公司是不是特别看重学历和毕业院校，是不是只招211，985高校的学生？我第一次听见这些问题还好，后来经常听见类似问题。</p><p>　　我发现一个规律：问这些问题的人，一定不是985或者211高校的学生，而且问这些问题的人，内心又有着无限的自卑，总是觉得别人会看不起自己。</p><p>　　其实换一个角度看就会明白，笔试和面试是为什么？不就是在筛选么？重点大学已经帮助做了一些筛选了，所以某些公司只看学校也是正常的。关键在于自己有实力，公司错过了，是公司的损失。</p><p><strong><br/></strong></p><p><strong><span style="font-size: 24px;">学习能力</span></strong></p><p>　　还有学生会非常强调自己的学习能力，说自己最大的优势就是学习能力强。在所有学生的简历中，绝大部分的同学都会写自己的学习能力强，所以这个不要做为一个特点。</p><p>　　面试过程中，一再重点强调自己学习能力强的学生，一般都是能力不行的，当自己能力不够强的时候，才会这么强调自己学习能力强。聪明一点的学生，都不这么说，他们只需要讲述一些例子，让面试官得出一个结论：这个学生学习能力强，适应能力强。</p><p><strong><br/></strong></p><p><strong><span style="font-size: 24px;">最大的挫折</span></strong></p><p>　　我经常问应聘者一个问题：你过去几年受过的最大挫折是什么？应聘者回答后，我还会问：你是如何走出来的？这个时候的回答千差万别，有的人回答是失恋，有的人回答是亲人去世，有的人回答是考试失败。</p><p>　　我听到最奇葩的一个回答是：跟室友吵了一架。后来才知道那个同学的经历是，从小别人都让着她，那次室友没有让着她，打击很大。一个人能承受多少委屈，也可能会取得多大的成绩。其实后面一个问题的回答更加重要，会反映出一个人的做事方式，一个人是否足够阳光。</p><p><strong><br/></strong></p><p><strong><span style="font-size: 24px;">小清高</span></strong></p><p>　　技术比较牛、条件比较好的人，一般都有点小清高。有时候不愿意参加宣讲会，有的甚至连笔试都懒得去。其实这种学生容易失去一些机会，而且在面试过程中，容易挂掉。从整体来看，现在是买方市场，每个公司都重视人才，但一定不是这个时候。</p><p>　　因此，开始的时候还是低调、主动一些比较好。发offer之前，主动权在公司，一旦你拿到了offer，主动权就在你了，你可以谈条件，公司这时候才会真正重视你这个人才。</p><p><strong><br/></strong></p><p><strong><span style="font-size: 24px;">关于简历</span></strong></p><p>　　很多公司面试，其实没有笔试环节，大多是通过简历筛选，然后通知面试。一些同学希望自己的简历脱颖而出，搞得很特别，出发点不错，但经常适得其反。因为你不知道面试官是什么性格，你搞得花花绿绿，有人觉得漂亮，也可能有人觉得太浮躁。</p><p>　　如果你条件还可以，简历格式还是普通一点比较好，不要剑走偏锋。也不要用招聘网站上模版一样格式的简历，很容易被忽略掉的。比较好的简历，不求高端大气上档次，但求低调奢华有内涵。建议不要有表格，用word写个两页比较合适。</p><p>　　内容方面也有要考虑的地方，有大面上的介绍，还要有重点的描述。把一些关键词放进去，否则搜索筛选简历不会进入视线。然后重点介绍自己最擅长的部分，一定是最擅长的，因为面试官一定会问简历上的相关问题。</p><p>　　如果做好了，简历可以做为一个钩子，引导面试官问你一些问题，你要是准备好了答案，在答案中再准备一个钩子，让面试官问下一个问题……</p><p>　　还有一个最关键点，针对你要面试的公司做简历修改。一般公司都有岗位描述，他们需要什么样的技能，你就重点描述什么样的技能。不要所有公司投递的简历都是一样的。</p><p><strong><br/></strong></p><p><strong><span style="font-size: 24px;">穿着礼仪</span></strong></p><p>　　面试仪表其实没那么重要。如果你平时就很正式，那就穿的正式，如果你平时从没有穿过西服，面试时穿一件西服，也会觉得特别别扭。在互联网公司面试，穿着并不重要，反而穿得太正式了，会显得另类。</p><p>　　以前有一个典故，说微软穿着很随意，都是牛仔裤+T恤，IBM穿着很讲究，西装革履，一次微软和IBM的人约了一个会议，大家都考虑对方的感受，最后开会时发现微软的人都穿着西装，IBM的人都穿着T恤。（其他需要正式点的行业，还是穿正装，比如保险银行业、法律相关的）</p><p>　　一般不要浓妆艳抹，当然打扮得特别漂亮的，也有好处，就算实力不行，也有机会过初试。以前初试官偶尔把美女放过来给我终试，评语是：面试辛苦，放松一下：）。</p><p>　　最好的礼仪，其实是多说几句公司的好话，称赞公司有多么好，自己对公司多向往什么的，面试官一般都觉得受用。</p><p><strong><br/></strong></p><p><strong><span style="font-size: 24px;">了解公司</span></strong></p><p>　　还有的同学，除了准备点技术问题以外，其他的一概没有准备。不知道公司背景，不清楚公司业务，然后嘴上还会说，对这个公司非常感兴趣，非常希望加入这个公司……这种人很容易就被刷掉了。其实花半个小时去了解公司背景和主要业务，哪怕只看一些新闻，也会增加面试官的好感。</p><p>　　如果希望加入一家公司，建议去多用这个公司的产品，关注公司所在的行业，经常看看这个公司的新闻，想办法去认识一下公司内部的人，了解一些情况。没有认识的人怎么办？现在微博、微信这么发达，甚至还有陌陌、来往，你还找不到人么？</p><p><strong><br/></strong></p><p><strong><span style="font-size: 24px;">带三样东西</span></strong></p><p>　　面试的时候，记得带三样东西：<strong>一份简历，白纸、和笔。</strong></p><p>　　简历一定要准备一份，原因很多。阿里是要求所有面试线上化，所以一般都不给面试官简历，造成面试官必须用电脑看，电子版哪有纸质版舒服啊；有可能是两个面试官，但他们只准备了一份简历；还有可能助理忘记打印简历给面试官了，很多面试官都是被安排面试的，如果助理不得力，简历也要自己打印。自己准备一份简历，可以让面试官有个好印象。</p><p>　　白纸和笔，都是在面试过程中可以帮助自己的。我面试过很多学生，当我要求他们描述某个项目或者算法时，他们用手势比划半天，有的同学把我手上的笔和纸拿过去，让我只好眼巴巴看着。</p><p>　　另外，用笔来画项目流程图或者技术框架，比用语言描述更加形象。画完之后，还可以把白纸一起交给面试官，嘿嘿，保证面试官对你印象深刻。</p><p><strong><br/></strong></p><p><strong><span style="font-size: 24px;">谈薪资</span></strong></p><p>　　谈薪水是个有点敏感的话题。一般都是在最后HR来跟你谈的，但不保准面试过程中有些面试官会主动的问。</p><p>　　有次我问学生薪水期望时，有学生傻傻的回答说：不是最后才谈么，怎么现在就谈了？我当时就想，我想什么时候谈就什么时候谈你管得着么？谈薪水，最标准最讨巧的回答是类似这样的：薪水是很重要，但我更看重这个机会，只要薪资差不多就好了。</p><p>　　每个公司都有自己的薪资体系，不会因为你的要求而增多多少，也不会因为你不要求而减少太多。在面试过程中，如果你已经有了其他公司给你offer，你可以委婉的表达一起别的公司给的offer是多少，如果这个公司很想让你过来，薪资方面一定会考虑的。</p><p>　　而且一旦到了最后谈薪资的时候，你可以明确提出自己的要求，这个时候是HR谈了，公司已经确定要给你offer，HR不会轻易的否决你的面试结果，所以可以大胆的谈。</p><p><strong><br/></strong></p><p><strong><span style="font-size: 24px;">不知道</span></strong></p><p>　　面试的时候经常会遇到一个情况，不知道该如何回答面试官的问题。我见过好多学生，遇到问题不知道，连忙回答说：我以前学过但是忘了，我再看看书就完全知道了。这种让面试官很无语，所有的问题都是这样啊，看看书都知道了。最好的方式，就是望着面试官傻笑说：不好意思啊，我不知道该怎么回答，确实不知道。</p><p>　　正常情况下，面试官的问题一定有一些是面试者回答不上来的，有时候面试官是故意在这么做，显得自己比较牛逼，打压一下面试者的预期。否则后面offer没法谈了。但在面试过程中，应该表现出足够的自信，答不出来也没有关系；当然也不要傲慢，否则搞得面试官心情不好就麻烦了。</p><p><strong><br/></strong></p><p><strong><span style="font-size: 24px;">我的面试经历</span></strong></p><p>　　我自己其实只被正式面试过两次，第一次是我还在天大上研究生，华为过来面试说可以去他们那里做毕业课题设计，我去面试了，后来我们学院通过了两个人，我也通过了，不过后来我没有去；第二次面试是加入雅虎的面试，还是周鸿祎和他的技术大拿李钊面试的，后来就加入雅虎了。回想这两次，我都没有用什么面试技巧，那时候也很少去想面试技巧，就是很真实的回答了他们的问题。</p><p>　　讲了这么多，最后我想说，大巧若拙，最好的技巧就是没有技巧。只要做好了足够的准备，那么就真实的表现自己。好了，就讲这些吧，希望能帮助到你。</p><p><strong><br/></strong></p><p><strong><span style="font-size: 24px;">面试小技巧</span></strong></p><p>　　写了这么多，最后说几个小技巧：</p><ul class=" list-paddingleft-2" style="list-style-type: square;"><li><p>面试之前，可以去厕所洗把脸，然后把嘴张大，然后深呼吸。为什么要这样？你自己试试就知道了，会让你不紧张。</p></li><li><p>面试过程中，注意看着面试官，然后微笑。大部分面试官都在装正经，你如果能让场面不那么正式，会拉近距离。</p></li><li><p>面试过程中，注意多用“咱们”，少用“你们”。例如：咱们公司在某个产品上如何如何……很容易就拉近距离了。</p></li><li><p>面试结束，可以微笑着问问还有没有下一次面试，如果面试通过有的面试官会告诉你的，至少可以安心吧。</p></li></ul><p>　　所有面试结束，一定要记住跟你谈offer的那个人的电话或者联系方式，隔几天跟联系一下问问进度。公司内部其实相当混乱，很容易就遗漏某个人了。</p><p>　　面试结束后，如果你感觉还好，可以在微博上表扬一下公司，然后@他们招聘微博什么的，每个公司招聘团队都会关注微博上的反馈，说不定就拿你的微博作为截图发给内部的高管和老大了，可以拿来邀功啊。你帮助了他们，他们至少不会亏待你吧。</p>','面试，web','网络','1','0','0','1476533439','1197','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('40','17','php根据分页符实现文章分页','当我们文章内容太长时，出于许多方面的考虑，站长往往要用到分页，所以内容分页在网站中，是必不可缺的。在这里我要分享一个根据分页符实现文章内容分页的方法……','','<p>　　当我们文章内容太长时，出于许多方面的考虑，站长往往要用到分页，所以内容分页在网站中，是必不可缺的。在这里我要分享一个根据分页符实现文章内容分页的方法，他适应于原生PHP and　thinkPHP。<br/></p><pre class="brush:as3;toolbar:false">$str&nbsp;&nbsp;&nbsp;=&nbsp;&nbsp;&nbsp;&#39;文章内容文章内容文章_ueditor_page_break_tag_内容文章内容文章内容&#39;;
//_ueditor_page_break_tag_这是百度编辑器分页符，每个编辑器或有不同，要根据实际生产修改
function&nbsp;pages($str){
&nbsp;$page&nbsp;=&nbsp;!empty($_GET[&#39;p&#39;])&nbsp;?&nbsp;$_GET[&#39;p&#39;]&nbsp;:&nbsp;1;
&nbsp;$arr&nbsp;=&nbsp;explode(&quot;_ueditor_page_break_tag_&quot;,$str);
&nbsp;&nbsp;&nbsp;if(count($arr)==1){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;$str;
&nbsp;&nbsp;&nbsp;}else{
	echo&nbsp;$arr[$page-1];
	echo&nbsp;&#39;&#39;;
	&nbsp;&nbsp;for($i=1;$i&lt;=count($arr);$i++){
		if($page&nbsp;==&nbsp;$i){
		&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;&quot;&quot;.$i.&quot;&nbsp;&quot;;
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}else{
		&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;&quot;&quot;.$i.&quot;&nbsp;&quot;;
&nbsp;&nbsp;&nbsp;&nbsp;		}
	}
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;&#39;&#39;;
&nbsp;&nbsp;&nbsp;}
}</pre><p><br/></p>','php,web','清蝎','0','0','0','1477297889','1226','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('41','11','改版','有人说我的首页不好，华而不实，想了想，也觉得有点道理，于是打算改版。效果做出来了，先呈现给大家看看……','/uploads/20180416\4fcb98e39dea132cf1f3798438873f89.jpg','<p>　　有人说我的首页不好，华而不实，想了想，也觉得有点道理，于是打算改版。效果做出来了，先呈现给大家看看，如果小伙伴们有什么建议，欢迎留言。因为还有些细节方法的问题，可能本周日前，可以改好，到时就会改版了……</p><p>　　好了，呈上效果图:</p><p><img src="/uploads/20161025/1477408522558545.gif" title="1477408522558545.gif" alt="王赤清个人网站首页效果图.gif"/></p>','琐碎，阅读，web','王赤清','0','0','0','1477408882','208','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('42','18','用 PHP 爬虫做旅游数据分析','随着物质的提高，旅游渐渐成为人们的焦点，火热的国庆刚刚过去，乘着这股余热，我想很多人都想知道，大家一...','http://online.sccnn.com/desk2/1231/08.jpg','<p style="    border: 1px solid #ccc;    padding: 10px;    margin: 5px;    border-left: 5px solid #92a931;    background: #f6f6f6;"><strong>声明</strong>：采集程序只做技术研究使用，切不可做出格的事以免惹来麻烦首先感谢广大网友的支持，现将源代码公开，地址如下：https://github.com/owner888/phpspidergithub<br/>地址的demo目录下的 mafengwo.php 和 mafengwo.sql ...</p><p><br/></p><p>本程序所采集的数据都是马蜂窝上面公开的数据，并未触及隐私内容，也不公开数据库，广大网友无需担心。</p><p><span class="crayon-v" style="border: 0px; margin: 0px; padding: 0px; font-size: inherit !important; font-family: inherit; height: inherit; line-height: inherit !important; font-weight: inherit !important; color: rgb(0, 45, 122) !important;"><br/></span></p><p><span class="crayon-v" style="border: 0px; margin: 0px; padding: 0px; font-size: inherit !important; font-family: inherit; height: inherit; line-height: inherit !important; font-weight: inherit !important; color: rgb(0, 45, 122) !important;"><span style="color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 15px; line-height: 24px; background-color: rgb(255, 255, 255);">随着物质的提高，旅游渐渐成为人们的焦点，火热的国庆刚刚过去，乘着这股余热，我想很多人都想知道，大家一般会去哪里玩呢，于是我花了10分钟写了一个采集马蜂窝游记的小程序，当然速度能有这么快，完全依赖于PHP著名爬虫框架phpspider。</span></span></p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);"><br/></p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">国际惯例，我们先来看看代码怎么写，算作抛砖引玉吧&nbsp;^_^</p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">马蜂窝不同于常规网站，因为并发量高并且某些数据需要实时，比如观看人数，点赞人数，所以网站多处使用了Ajax，而Ajax对于普通采集者来说，是个比较大的问题。</p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">观察了一下马蜂窝网站，最终确定了采集路线：</p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">获取热门城市 -&gt; 获取城市下的游记列表 -&gt; 获取游记内容 -&gt; 提取游记内容的游记标题、城市、出发时间等，接下来我们用三个步骤来实现它。。。</p><h3 style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 20px; font-stretch: normal; line-height: 30px; font-family: &quot;Microsoft YaHei&quot;, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(46, 46, 46); white-space: normal; background-color: rgb(255, 255, 255);">1、获取热门城市</h3><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);"><a href="http://www.mafengwo.cn/mdd/citylist/21536.html" style="border: 0px; margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 153, 204);">http://www.mafengwo.cn/mdd/citylist/21536.html</a></p><p><a href="http://image.sowm.cn/v2M3Aj.png" target="_blank"><img src="/ueditor/php/upload/image/20161103/1478169584611129.png" title="" alt=""/></a></p><p><span style="color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 15px; line-height: 24px; background-color: rgb(255, 255, 255);">首先我们要采集下这些热门城市</span></p><p><a href="http://www.phpchina.com/data/attachment/portal/201610/18/090628xwegm4mxonwkm64l.jpg" target="_blank"><img src="/ueditor/php/upload/image/20161103/1478169589920264.jpg" title="" alt=""/></a></p><p><span style="color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 15px; line-height: 24px; background-color: rgb(255, 255, 255);">当我们点击页数的时候，发现他的数据是Ajax加载的，末页是297，而且使用的是POST方法</span></p><p><a href="http://image.sowm.cn/UveQZr.png" target="_blank"><img src="/ueditor/php/upload/image/20161103/1478169591619117.png" title="" alt=""/></a></p><p><span style="color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 15px; line-height: 24px; background-color: rgb(255, 255, 255);">提交的参数如下：</span></p><p><a href="data/attachment/portal/201610/18/090652m5z5zm5g2255i855.jpg" target="_blank"><img src="/ueditor/php/upload/image/20161103/1478169595448375.png"/></a></p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">很明显这个page就是页数了，这里就有个问题，phpspider框架是有 URL 去重机制的，POST的话URL只有一个，但是query_string是不影响POST数据的，我们可以在后面加上 ?page=1|2|3…，所以我们代码可以这么写：</p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">设置列表页规则：</p><pre class="brush:as3;toolbar:false">&#39;list_url_regexes&#39;&nbsp;=&gt;&nbsp;array(
&nbsp;&nbsp;&nbsp;&nbsp;&quot;http://www.mafengwo.cn/mdd/base/list/pagedata_citylist?page=d+&quot;,
)</pre><p><span class="crayon-sy" style="border: 0px; margin: 0px; padding: 0px; font-size: inherit !important; font-family: inherit; height: inherit; line-height: inherit !important; font-weight: inherit !important; color: rgb(51, 51, 51) !important;"><span style="color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 15px; line-height: 24px; background-color: rgb(255, 255, 255);">在入口回调函数入口所有城市列表：</span></span></p><pre class="brush:as3;toolbar:false">$spider-&gt;on_scan_page&nbsp;=&nbsp;function($page,&nbsp;$content,&nbsp;$phpspider)
{
&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;上面Ajax分页的末页是297页
&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;($i&nbsp;=&nbsp;0;&nbsp;$i&nbsp;&nbsp;$url,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;method&#39;&nbsp;=&gt;&nbsp;&#39;post&#39;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;fields&#39;&nbsp;=&gt;&nbsp;array(
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;mddid&#39;=&gt;21536,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;page&#39;=&gt;$i,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;)
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;热点城市列表页URL入队列
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$phpspider-&gt;add_url($url,&nbsp;$options);
&nbsp;&nbsp;&nbsp;&nbsp;}
};</pre><p><br/></p><h3 style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 20px; font-stretch: normal; line-height: 30px; font-family: &quot;Microsoft YaHei&quot;, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(46, 46, 46); white-space: normal; background-color: rgb(255, 255, 255);">2、获取热门城市下的游记列表</h3><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">点击进入一个城市后，我们可以看到他下面的游记列表</p><p><img src="/ueditor/php/upload/image/20161103/1478169762392561.jpg"/></p><p><span style="color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 15px; line-height: 24px; background-color: rgb(255, 255, 255);">当然和上面一样，也是Ajax加载的，我们可以打开chrome的开发者工具，点击Network，然后随意点击一个分页得到Ajax的URL：</span></p><p><img src="/ueditor/php/upload/image/20161103/1478169762789321.jpg"/></p><p><span style="color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 15px; line-height: 24px; background-color: rgb(255, 255, 255);">和城市列表一样，也是POST，参数如下：</span></p><p><img src="/ueditor/php/upload/image/20161103/1478169763404508.png"/></p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">很明显page就是页数了，当然我们直接通过POST方式访问Ajax地址：</p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);"><a href="http://www.mafengwo.cn/gonglve/ajax.php?act=get_travellist" style="border: 0px; margin: 0px; padding: 0px; text-decoration: none; color: rgb(0, 153, 204);">http://www.mafengwo.cn/gonglve/ajax.php?act=get_t…</a></p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">是直接报错的，他需要来路，综合以上，我们代码可以这么来写：</p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">首先我们要在 on_start 回调函数里面加上来路URL</p><pre class="brush:as3;toolbar:false">$spider-&gt;on_start&nbsp;=&nbsp;function($phpspider)&nbsp;
{
&nbsp;&nbsp;&nbsp;&nbsp;$phpspider-&gt;add_header(&#39;Referer&#39;,&#39;http://www.mafengwo.cn/mdd/citylist/21536.html&#39;);
};</pre><p><span style="color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 15px; background-color: rgb(255, 255, 255);">和上面获取城市列表一样，设置列表匹配规则：</span><span style="color: rgb(46, 46, 46); background-color: rgb(248, 248, 248);">&nbsp; &nbsp;</span><span style="color: rgb(46, 46, 46); background-color: rgb(248, 248, 248);">&nbsp;</span></p><pre class="brush:as3;toolbar:false">&#39;list_url_regexes&#39;&nbsp;=&gt;&nbsp;array(
&nbsp;&nbsp;&nbsp;&nbsp;&quot;http://www.mafengwo.cn/gonglve/ajax.php?act=get_travellist&amp;mddid=d+&quot;,
)</pre><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">然后在 on_list_page 回调里面判断如果是第一页就获取总页数，然后循环入队列：</p><pre class="brush:as3;toolbar:false">preg_match(&#39;#&lt;span&nbsp;class=&quot;count&quot;&gt;共&lt;span&gt;(.*?)&lt;/span&gt;页#&#39;,&nbsp;$data_page,&nbsp;$out);
for&nbsp;($i&nbsp;=&nbsp;0;&nbsp;$i&nbsp;&lt;&nbsp;$out[1];&nbsp;$i++)&nbsp;
{
&nbsp;&nbsp;&nbsp;&nbsp;$v&nbsp;=&nbsp;$page[&#39;request&#39;][&#39;fields&#39;][&#39;mddid&#39;];
&nbsp;&nbsp;&nbsp;&nbsp;$url&nbsp;=&nbsp;&quot;http://www.mafengwo.cn/gonglve/ajax.php?act=get_travellist&amp;mddid={$v}&amp;page={$i}&quot;;
&nbsp;&nbsp;&nbsp;&nbsp;$options&nbsp;=&nbsp;array(
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;url_type&#39;&nbsp;=&gt;&nbsp;$url,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;method&#39;&nbsp;=&gt;&nbsp;&#39;post&#39;,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;fields&#39;&nbsp;=&gt;&nbsp;array(
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;mddid&#39;=&gt;$v,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;pageid&#39;=&gt;&#39;mdd_index&#39;,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;sort&#39;=&gt;1,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;cost&#39;=&gt;0,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;days&#39;=&gt;0,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;month&#39;=&gt;0,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;tagid&#39;=&gt;0,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;page&#39;=&gt;$i,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;)
&nbsp;&nbsp;&nbsp;&nbsp;);
&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;游记列表页URL入队列
&nbsp;&nbsp;&nbsp;&nbsp;$phpspider-&gt;add_url($url,&nbsp;$options);
}</pre><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">通过上面两个步骤，我们就把所有热门城市下的游记列表都放入到了队列，接下来我们进行第三步，从这些列表里面获取内容页URL，然后提取内容。</p>_ueditor_page_break_tag_<h3 style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 20px; font-stretch: normal; line-height: 30px; font-family: &quot;Microsoft YaHei&quot;, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(46, 46, 46); white-space: normal; background-color: rgb(255, 255, 255);">3、获取热门城市下的游记列表</h3><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">在 on_list_page 方法里面会得到列表页的内容，从这些内容里面我们可以提取内容页的URL</p><pre class="brush:as3;toolbar:false">//&nbsp;获取内容页
preg_match_all(&#39;#&lt;a&nbsp;href=&quot;/i/(.*?).html&quot;&nbsp;target=&quot;_blank&quot;&gt;#&#39;,&nbsp;$html,&nbsp;$out);
if&nbsp;(!empty($out[1]))&nbsp;
{
&nbsp;&nbsp;&nbsp;&nbsp;foreach&nbsp;($out[1]&nbsp;as&nbsp;$v)&nbsp;
&nbsp;&nbsp;&nbsp;&nbsp;{
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$url&nbsp;=&nbsp;&quot;http://www.mafengwo.cn/i/{$v}.html&quot;;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;内容页URL入队列
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$phpspider-&gt;add_url($url);
&nbsp;&nbsp;&nbsp;&nbsp;}
}</pre><p><span style="color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 15px; line-height: 24px; background-color: rgb(255, 255, 255);">接下来我们来配置field提取内容页字段</span></p><pre class="brush:as3;toolbar:false">&#39;fields&#39;&nbsp;=&gt;&nbsp;array(
&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;标题
&nbsp;&nbsp;&nbsp;&nbsp;array(
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;name&#39;&nbsp;=&gt;&nbsp;&quot;name&quot;,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;selector&#39;&nbsp;=&gt;&nbsp;&quot;//h1[contains(@class,&#39;headtext&#39;)]&quot;,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;required&#39;&nbsp;=&gt;&nbsp;true,
&nbsp;&nbsp;&nbsp;&nbsp;),
&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;分类
&nbsp;&nbsp;&nbsp;&nbsp;array(
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;name&#39;&nbsp;=&gt;&nbsp;&quot;city&quot;,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;selector&#39;&nbsp;=&gt;&nbsp;&quot;//div[contains(@class,&#39;relation_mdd&#39;)]//a&quot;,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;required&#39;&nbsp;=&gt;&nbsp;true,
&nbsp;&nbsp;&nbsp;&nbsp;),
&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;出发时间
&nbsp;&nbsp;&nbsp;&nbsp;array(
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;name&#39;&nbsp;=&gt;&nbsp;&quot;date&quot;,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;selector&#39;&nbsp;=&gt;&nbsp;&quot;//li[contains(@class,&#39;time&#39;)]&quot;,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;required&#39;&nbsp;=&gt;&nbsp;true,
&nbsp;&nbsp;&nbsp;&nbsp;),
)</pre><p><span style="background-color: rgb(255, 255, 255); color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 15px;"><br/>设计一张数据表：</span><br/></p><p><img src="http://ww3.sinaimg.cn/mw690/6941baebjw1f8rmgvq236j20b703amxb.jpg"/></p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">当然我们还可以获取游记的浏览量、收藏、分享、置顶、游玩金额等等，太多了，方法类似。</p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">到此程序就设计完了，总共不到200行的代码，得益于phpspider自带了多进程采集功能，数据很快就采集完成，总共7W多点。</p><p><img src="http://ww4.sinaimg.cn/mw690/6941baebjw1f8rmgvi0awj207802cjrc.jpg"/></p><p><img src="http://ww4.sinaimg.cn/mw690/6941baebjw1f8rmgv7h0ij20g00ibn3j.jpg"/></p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">得到这些数据以后，我们能干什么呢？！</p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">Top10 旅游城市分别是</p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);"><img src="http://ww4.sinaimg.cn/mw690/6941baebjw1f8rmgv0b0yj21ig0dc75q.jpg"/></p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">可以看得出，云南是一个好地方，也是博主日夜思念的地方啊。。。</p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">五一和国庆期间旅游城市占比</p><p><img src="http://ww2.sinaimg.cn/mw690/6941baebjw1f8rmguezrqj20qo0dcwfc.jpg"/><img src="http://ww1.sinaimg.cn/mw690/6941baebjw1f8rmgu4b0lj20qo0dcaay.jpg"/></p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">可以看得出，五一的时候大家喜欢去西藏玩，国庆却更青睐青岛，好吧，这两个地方博主都没去过，表示好受伤~_~!</p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">接下来我们来看看这一年来北京和杭州的旅游旺季</p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);"><img src="http://ww2.sinaimg.cn/mw690/6941baebjw1f8rmjryfe6j21ig0dc75u.jpg"/></p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">可以看出七八月份去北京的人会比较多，这个时候北京是最爽的，不热也不冷，博主就曾经有有一年8月份去的北京，舒服死了^_^</p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">我们再来看看杭州</p><p><img src="http://ww1.sinaimg.cn/mw690/6941baebjw1f8rmgthz7nj21ig0dc0u8.jpg"/></p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">看得出3月底到4月中旬是杭州适合游玩的季节啊，那时候春暖花开，天气也不错，听说太子湾公园每年那时候都会有樱花和郁金花展，非常美，艾玛旅游病又犯了~_~!</p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">好吧文章到此就结束了，其实还想分析更多，比如采集热门路线啊，热门景点啊，热门图集啊，还有旅游路线的价位啊，最终形成一个旅游的APP，如果你们有好的想法，也可以来告诉我，我把他采集下来，供大家参考 ^_^</p><p style="border: 0px; margin: 0px 0px 20px; padding: 0px; font-size: 15px; color: rgb(46, 46, 46); font-family: &quot;Microsoft YaHei&quot;, 宋体, &quot;Myriad Pro&quot;, Lato, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; line-height: 24px; white-space: normal; background-color: rgb(255, 255, 255);">最后，对源代码感兴趣的同学可以上github搜索phpspider哈 ^_^</p>','php,学习,分享','网络','1','0','0','1478170138','1064','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('43','18','Javascript函数节流','最近在做网页的时候有个需求，就是浏览器窗口改变的时候需要改一些页面元素大小，于是乎很自然的想到了window的resize事件,于是乎……','http://img7.doubanio.com/lpic/s26050200.jpg','<p>最近在做网页的时候有个需求，就是浏览器窗口改变的时候需要改一些页面元素大小，于是乎很自然的想到了window的resize事件,于是乎我是这么写的</p><pre>&lt;!DOCTYPE&nbsp;html&gt;
&lt;html&gt;
&lt;head&gt;
&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;Throttle&lt;/title&gt;
&lt;/head&gt;
&lt;body&gt;
&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;n=0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;resizehandler(){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(new&nbsp;Date().getTime());
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(++n);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}

&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;window.onresize=resizehandler;
&nbsp;&nbsp;&nbsp;&lt;/script&gt;
&lt;/body&gt;
&lt;/html&gt;</pre><p>功能倒是实现了，但是我拖拽的方式改变浏览器窗口大小的时候看了下控制台</p><p><a href="http://images.cnitblog.com/blog/349217/201311/02153131-52d114a4acfb42cc97158a8f37a3a45d.png"><img title="image" src="http://images.cnitblog.com/blog/349217/201311/02153135-56cb85f5163c494fa440bce40ae2440c.png" alt="image" width="730" height="446"/></a></p><p>&nbsp;</p><p>没错，简单的一个拖拽让我的resizeHandler()方法执行了52次，这完全不是我想要的效果，实际上我的resizeHandler()方法的代码很复杂，甚至会使用ajax向服务器发送请求，要是简单的一次改变窗口大小就要调用52次这还了得</p><h3>函数节流</h3><p>其实我的本意只是窗口resize后页面做一些调整就可以，而window的resize事件并不是在resize结束后才触发的，具体则么个频率我也不知道，但却是在不停的调用，直到窗口大小不再变化。其实类似的机制还有鼠标的mousemove，都是在短时间内重复触发。</p><p>在《JavaScript高级程序设计》中有专门应对此问题的函数节流</p><pre>function&nbsp;throttle(method,context){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;clearTimeout(method.tId);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;method.tId=setTimeout(function(){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;method.call(context);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;},500);
}</pre><p>原理很简单，利用定时器，让函数执行延迟500毫秒，在500毫秒内如果有函数又被调用则删除上一次调用，这次调用500毫秒后执行，如此往复。这样我刚才的代码可以改为</p><pre>&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;n=0;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;resizehandler(){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(new&nbsp;Date().getTime());
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(++n);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;throttle(method,context){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;clearTimeout(method.tId);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;method.tId=setTimeout(function(){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;method.call(context);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;},500);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}
&nbsp;&nbsp;&nbsp;&nbsp;window.onresize=function(){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;throttle(resizehandler,window);
&nbsp;&nbsp;};
&lt;/script&gt;</pre><p>拖拽一下试试，果真只执行了一次</p><p><a href="http://images.cnitblog.com/blog/349217/201311/02153137-b43bbe84ff71446ea8f9b7a74b14395c.png"><img title="image" src="http://images.cnitblog.com/blog/349217/201311/02153138-a96b605d946046519fe6959dd2fef6e2.png" alt="image" width="602" height="428"/></a></p><h3>另一种做法</h3><p>网上还有一种函数节流方案，它是这么做的</p><pre>function&nbsp;throttle(method,delay){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;timer=null;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;function(){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;context=this,&nbsp;args=arguments;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;clearTimeout(timer);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;timer=setTimeout(function(){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;method.apply(context,args);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;},delay);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}
}</pre><p>调用一下试试，一样的效果</p><pre>&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;n=0;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;resizehandler(){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(new&nbsp;Date().getTime());
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(++n);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;throttle(method,delay){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;timer=null;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;function(){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;context=this,&nbsp;args=arguments;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;clearTimeout(timer);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;timer=setTimeout(function(){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;method.apply(context,args);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;},delay);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}

&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//因为返回函数句柄，不用包装函数了
&nbsp;&nbsp;&nbsp;&nbsp;window.onresize=throttle(resizehandler,500);
&lt;/script&gt;</pre><p><a href="http://images.cnitblog.com/blog/349217/201311/02153139-01fcbf984e6244fb99f4d98b463ce8f5.png"><img title="image" src="http://images.cnitblog.com/blog/349217/201311/02153140-ff88bcdf0d37475ba34b754ce01bb726.png" alt="image" width="636" height="410"/></a></p><h3>比较</h3><p>两种方法都是利用了setTimeout，不同的是第二种方法加入的函数延迟执行时间，这个在第一种方案中很容易也具有此功能，加一个参数的事儿。</p><p>但第一种方案把tId设为函数的一个变量保存，而第二种创建了一个闭包来存储。个人觉得差距不大，很喜欢第一种，简单，高效。</p><h3>新需求</h3><p>有一天做了个类似的东西，就像百度首页输入自动提示一样的东西，我在text上绑定keyup事件，每次键盘弹起的时候自动提示，但是又不想提示那么频繁，于是我用了上面方法，但是悲剧了，只有停止输入等500毫秒才会提示，在输入过程中根本就没有提示。看了一下代码，可不是嘛，只要是用户会盲打，在500毫秒内按一下键盘，提示函数就会不断被延迟，这样只有停下来的时候才会提示，这就没意义了。</p><p>能不能在函数节流的基础上间隔固定时间就执行一次？</p><h3>小改动</h3><p>在网上搜了一下我们可以根据第二种写法（第一种为函数拓展多个变量感觉有些不好）做些改动，添加一个参数作为到固定间隔必须执行</p><pre>function&nbsp;throttle(method,delay,duration){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;timer=null,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;begin=new&nbsp;Date();
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;function(){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;context=this,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;args=arguments,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;current=new&nbsp;Date();
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;clearTimeout(timer);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(current-begin&gt;=duration){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;method.apply(context,args);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;begin=current;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}else{
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;timer=setTimeout(function(){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;method.apply(context,args);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;},delay);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}
}</pre><p>这样每次我们判断间隔了多久，要是超过设置时间则立即执行一次，以刚才例子试一试效果</p><pre>window.onresize=throttle(resizehandler,100,200);</pre><p><a href="http://images.cnitblog.com/blog/349217/201311/02153141-091296f5a0af4250871aeacc53c61db8.png"><img title="image" src="http://images.cnitblog.com/blog/349217/201311/02153141-a031bb259b70436490d3e2e92c885c35.png" alt="image" width="638" height="450"/></a></p><p>果真既没有频繁执行也没有就最后执行</p><p>&nbsp;</p>','分享，web','网络','1','0','0','1478406854','768','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('44','11','鸡足寺，最豪华的红地毯和它的原住民','香山红叶闻名遐迩，每年赏枫期，交通堵塞。人们争先恐后想看看先辈陈毅盛赞的“西山红叶好，露重色欲浓”。山野之间满山红艳艳的是中国东北，吉林辽阔的红叶林让人难忘它的粗犷，本溪红叶谷“寒山转苍翠，秋水日潺湲”，让人在滋润瀑布间欣赏红叶流水慢门的细腻……','http://img.mp.itc.cn/upload/20161129/69a3db2efe524f61aacf1a3d2c6a1185_th.jpg','<p><img src="http://img.mp.itc.cn/upload/20161129/029da7396c724a4a9f2ed27b778be53c_th.jpg"/></p><p>　　香山红叶闻名遐迩，每年赏枫期，交通堵塞。人们争先恐后想看看先辈陈毅盛赞的“西山红叶好，露重色欲浓”。山野之间满山红艳艳的是中国东北，吉林辽阔的红叶林让人难忘它的粗犷，本溪红叶谷“寒山转苍翠，秋水日潺湲”，让人在滋润瀑布间欣赏红叶流水慢门的细腻。而日本的红叶，因为多在公园和寺庙神社庭院之中，红叶密集红似火独具特色，其中令人过目难忘的当属滋贺县鸡足山的红叶地毯。有红叶的地方不少，让人欣赏落红之美，唯独鸡足寺，因而挺冷清的小寺庙成为日本赏枫胜地之最。鸡足寺附近还有其他很多寺庙神社，都在小山村附近，欣赏最美红地毯之余，也不忘看看附近风景。</p><p>　　鸡足寺红地毯</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/69a3db2efe524f61aacf1a3d2c6a1185_th.jpg"/></p><p>　　摄影/安心</p><p>　　世界上最豪华的红地毯的枫树有三百多年树龄，一共也不过两百多株。看这历史，也许种枫也是日本学习我们庭院风时的改良，广种枫树，欣赏树树秋声的盛景。春如樱花般清纯烂漫，秋如红叶般浓烈炙热，这也是日本对这两种颜色喜欢的极致。</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/845413a39175419d9eea91fa66c0ac1a_th.jpg"/></p><p>　　目及之处，落红满地 摄影/安心</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/cd96cfa7d292440a89141085a13517dd_th.jpg"/></p><p>　　日本柴犬的小眼睛也被枫叶点燃 摄影/安心</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/cd3664dfc5ca40ab95fd323261f86853_th.jpg"/></p><p>　　摄影/安心</p><p>　　榈庭多落叶，慨然知已秋，庭院深深的落叶红更加有了人间美好韵味。</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/c2e3c78ad01f433dad2ffa5ab00aa908_th.jpg"/></p><p>　　泉水叮咚知秋浓 摄影/安心</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/5dc044fa5a90454a812f493f34bad404_th.jpg"/></p><p>　　摄影/安心</p><p>　　苔痕上阶绿，疑是二月红花开。两百多棵老枫树密集生长，树上挂满苔藓。这儿的冷清，就等着一年这一季秋天的炙热燃情。</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/48308e1de1ed42d391a2bccceb9a9edd_th.jpg"/></p><p>　　记住这一年最美光景，最是橙黄橘绿时 摄影/安心</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/fb122a487b5b46aea9a4411102470c7b_th.jpg"/></p><p>　　沿途山上细密枫树，幽蓝神秘 摄影/安心</p><p>　　摄影/安心</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/64c3e10231544d3a90b78c9712606c01_th.jpg"/></p><p>　　光影变幻，幽绿中暗藏生机。这鸡足寺的红叶有份浓情炙热的美。而不远处的兴志漏神社枫叶则有份萧杀之感。</p><p>　　兴志漏神社</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/4a25cf12b75b43b29690ca0edcd55137_th.jpg"/></p><p>　　纪念之石 摄影安心</p><p>　　神社中供奉佐须之男神。日本的佛教来自中国，而他们的神社则是本土的信仰，更具有他们的民族特点。佐须之男神，原本因为调皮捣蛋不被天神看好，因为喜欢一位美女，制作了丛云宝剑，帮着她父亲杀死了大蟒蛇，也算为民除害。因而昭和60年，在神社100米附近发现炼铁炉的痕迹有一定道理。</p><p>　　这一堆的石头有些像西藏的玛尼石，应该也是一份属于他们的祭奠方式，石头堆衬着枫叶红，自然有一份悲凉之感。</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/39d2b52dd3584febaec58b50d024a601_th.jpg"/></p><p>　　石雕如绿色高帽 摄影/安心</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/94664dca20ff45fdb3e4475cc7e0a878_th.jpg"/></p><p>　　雨径绿芜合，霜园红叶多 摄影./安心</p><p>　　摄影/安心</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/b73cb8992a49496da0a9a3ad323414d1_th.jpg"/></p><p>　　落叶星星点点，观者的佛珠有了几分禅意。</p><p>　　摄影/安心</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/75f32491d634439ca34d9e357ca9e1b0_th.jpg"/></p><p>　　一位日本画家，画下满园萧瑟秋色之美。</p><p>　　如果说鸡足寺和兴志漏都是神佛的居住地，神秘静谧。那么环绕这里的小山村就是别样的素朴安详。</p><p>　　寺庙附近小山村</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/6061fd421ee942a08fdfa86cd9fbf2dc_th.jpg"/></p><p>　　摄影/安心</p><p>　　已是秋天，稻谷收割完毕，田野多是荒芜，但有辣椒园和枫叶争红。最吸引人的是辣椒园前有自制小小屋，屋子里放着小把辣椒，贴着日文的金额，那小罐子是蓄钱罐。日本小山村很多这样的无人售货点，买东西自觉付钱在储钱罐里。其实路不拾遗，夜不闭户是中国秦朝以来就有的古风，中国的云南、广西的有些村落也保留这人和人间自律信任的遗风。如果这种美好能长存，世间多么美好！</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/ff236a48f2034842ac77925182c48bec_th.jpg"/></p><p>　　摄影/安心</p><p>　　也有摊位有专人售卖，日本山村的阿婆会对着镜头羞涩的微笑。</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/c829ebc1dd164842aeb9722385b9e81e_th.jpg"/></p><p>　　田间漫步归家 摄影/安心</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/f9b0f73d10394b46b9d6a2dbce8bf989_th.jpg"/></p><p>　　柿子窗帘 摄影/安心</p><p>　　日本人很整洁，屋前房后即使杂物也摆放整齐有序。这屋檐下晾晒的柿子受了枫叶的渲染，也是秋天的独特风景。</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/b103be7d088047e596b461aee3a07ac7_th.jpg"/></p><p>　　房前总有放雨具的小框，不用担心雨具遗失 摄影/安心</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/e64a52c75f164307af760742357679a0_th.jpg"/></p><p>　　杂物间 摄影/安心</p><p>　　一处杂物间刚好敞开，室内一览无余，用具摆放有序。住宅环境表现了一个人的生活习惯。家庭小环境决定村环境，村环境决定一个区环境，然后是全国的大环境。所以日本的每个公厕无异味，备用厕纸总是不少，这种维护环境的好习惯也非一两日的努力可以达到的。</p><p style="text-align: center;"><img src="http://img.mp.itc.cn/upload/20161129/c14e85b3a7aa496c88454367d23b74a7_th.jpg"/></p><p>　　鸡足寺红地毯 摄影/安心</p><p>　　鸡足寺红地毯，无专人看管，仅仅围着低矮的栏杆，但也无人踩踏，有人无意间进入，会有人善意提醒。再回头看看，留住这么美的红地毯长存心中。</p>','旅游','网络','1','0','0','1480481350','928','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('45','18','js实现简单登录注销功能','js验证用户身份，登录成功之后跳转到首页。代码如下……','http://www.xuecan.net/uploads/allimg/160830/1-160S00Z105.png','<p>　　js验证登录，验证身份成功之后跳转到首页。理念与php登录一样，数据采用json。代码如下：</p><pre class="brush:html;toolbar:false">&lt;!DOCTYPE&nbsp;html&gt;
&lt;html&gt;
&nbsp;&nbsp;&nbsp;&nbsp;&lt;head&gt;
&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;
&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;登录&lt;/title&gt;
&lt;/head&gt;
&lt;body&gt;
&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type=&quot;text&quot;&nbsp;id=&quot;name&quot;&gt;&lt;br&gt;
&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type=&quot;password&quot;&nbsp;id=&quot;password&quot;&gt;&lt;br&gt;
&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type=&quot;submit&quot;&nbsp;value=&quot;登录&quot;&nbsp;id=&quot;submit&quot;&gt;
&lt;/body&gt;
&lt;/html&gt;
&lt;script&gt;
&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;json={
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&quot;name&quot;:&quot;张三&quot;,
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&quot;password&quot;:&quot;123456&quot;
&nbsp;&nbsp;&nbsp;&nbsp;}
&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;submit=document.getElementById(&quot;submit&quot;);
&nbsp;&nbsp;&nbsp;&nbsp;submit.onclick=function(){
&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;name=document.getElementById(&quot;name&quot;);
&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;password=document.getElementById(&quot;password&quot;);
&nbsp;&nbsp;&nbsp;&nbsp;if(name.value&amp;&amp;password.value){
&nbsp;&nbsp;&nbsp;&nbsp;if(name.value==json.name){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(password.value==json.password){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;document.cookie=&quot;user=&quot;+name.value;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;document.write(&quot;登录成功！&quot;);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;location.href=&quot;/&quot;;
&nbsp;&nbsp;&nbsp;&nbsp;}else{
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;密码输入错误&quot;);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}
&nbsp;&nbsp;&nbsp;&nbsp;}else{
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;用户名不存在&quot;)
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}
&nbsp;&nbsp;&nbsp;&nbsp;}else{
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;用户名或密码不能为空&quot;);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}
&nbsp;&nbsp;&nbsp;&nbsp;};
&lt;/script&gt;</pre><p>这是登录页页，下面是首页，首页包括显示用户名和注销功能：</p><pre class="brush:html;toolbar:false">&lt;!DOCTYPE&nbsp;html&gt;
&lt;html&gt;
&lt;head&gt;
&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;
&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;首页&lt;/title&gt;
&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&gt;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;user=&nbsp;document.cookie;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;click(){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(!user){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;location.href=&quot;/logo.html&quot;;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;};
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;};
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;del(){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;exp&nbsp;=&nbsp;new&nbsp;Date();
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;exp.setTime(exp.getTime()-1);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;cval=&nbsp;user.split(&quot;user=&quot;)[1];
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(cval!=null){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;document.cookie=user+&quot;=&quot;+cval+&quot;+;expires=&quot;+exp.toGMTString();
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;退出成功&quot;);
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;location.href=&quot;/logo.html&quot;;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;};
&lt;/script&gt;
&lt;/head&gt;
&lt;body&nbsp;onLoad=&quot;click();&quot;&gt;
&nbsp;&nbsp;&nbsp;&nbsp;我的用户名：&lt;script&gt;document.write(user.split(&quot;user=&quot;)[1]);&lt;/script&gt;&lt;br&gt;
&nbsp;&nbsp;&nbsp;&nbsp;&lt;a&nbsp;href=&quot;javascript:del();&quot;&gt;退出登录&lt;/a&gt;
&lt;/body&gt;
&lt;/html&gt;</pre><p>怎么样，是不是很简单。</p>','js，登录','清蝎子','0','0','0','1489648780','799','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('46','10','phpStudy 安装免费SSL证书','现在许多国内许多网站都开始安装SSL证书了，像百度，淘宝之类的，而国外像google,twitter早就安装了SSL,于是也开始动手安装，效果如……','http://upload.chinaz.com/2016/0409/1460168057530.png','<p>　　现在许多国内许多网站都开始安装SSL证书了，像百度，淘宝之类的，而国外像google,twitter早就安装了SSL,于是也开始动手安装，效果如下图：</p><p><img src="/uploads/20170117/1484654602412495.png" title="1484654602412495.png" alt="QQ图片20170117200234.png"/></p><p>&nbsp; &nbsp;https 时代已经到来，给网站添加 ssl 证书也是迟早的事情了。<a href="https://google.com.hk" target="_self">谷歌</a>从 2017 年起，Chrome 浏览器将会把采用 HTTP 协议的网站标记为「不安全」网站，苹果从 2017 年 iOS App 将强制使用 HTTPS；在国内热火朝天的小程序也要求必须使用 HTTPS 请求。</p><p>　　免费的 ssl 供应商有很多，之前沃通也推出了免费证书，不过直到后面爆出的一系列丑闻，也是老流氓，这里不多赘述。</p><p>&nbsp;&nbsp;好了，下面开始分享我是如何安装的。</p><p>　　自先是去申请SSL,我是在腾讯云申请的1年期，腾讯SSL申请地址是：<a href="https://console.qcloud.com/ssl" target="_self">https://console.qcloud.com/ssl</a></p><p>　　这里是说安装，关于申请，各位可以按官方提示，一步步来就是了。好了，申请到了证书，就下载下来，下载下来的有各各版本，Apache,IIS,Nginx,Tomcat。因为我是用Apache,所以只须Apache版，其余的都不须要，解压后，可以看到有三个文件，1_root_bundle.crt,2_www.qilinge.la.crt,3_www.qilinge.la.key.</p><blockquote style="background-color: rgb(146, 208, 80);"><p>I. 根证书：1_root_bundle.crt</p><p>II. 证书公钥：2_www.qilinge.la.crt</p><p>III. 证书私钥：3_www.qilinge.la.key</p></blockquote><p>　　要安装SSL,首先要确保，phpStudy安装时已添加SSL支持模块。<br/></p><p>　　<strong>修改配置：</strong></p><p><strong>　　</strong>第一步：</p><p>　　　　打开Apache目录下的 httpd.conf配置文件，找到 #LoadModule ssl_module modules/mod_ssl.so　，删除行首的配置语句注释符号“#”。</p><p>　　第二步：</p><p>　　　　打开Apache目录下的 vhosts.conf配置文件，编辑</p><pre class="brush:js;toolbar:false">Listen&nbsp;443DocumentRoot&nbsp;&quot;E:\www&quot;
ServerName&nbsp;www.qilinge.la
SSLEngine&nbsp;on
SSLProtocol&nbsp;all&nbsp;-SSLv2&nbsp;-SSLv3
SSLCipherSuite&nbsp;ALL:!ADH:!EXPORT56:RC4+RSA:+HIGH:+MEDIUM:+LOW:+SSLv2:+EXP:+eNULL
SSLCertificateFile&nbsp;&quot;D:\phpStudy\Apache\conf\ssl\2_www.qilinge.la.crt&quot;
SSLCertificateKeyFile&nbsp;&quot;D:\phpStudy\Apache\conf\ssl\3_www.qilinge.la.key&quot;
SSLCertificateChainFile&nbsp;&quot;D:\phpStudy\Apache\conf\ssl\1_root_bundle.crt&quot;
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Options&nbsp;FollowSymLinks&nbsp;ExecCGI
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;AllowOverride&nbsp;All
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Order&nbsp;allow,deny
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Allow&nbsp;from&nbsp;all
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Require&nbsp;all&nbsp;granted</pre><p>&nbsp; 保存，Apache重启，ok，在浏览器中输入：<a href="https://www.qilinge.la">https://www.qilinge.la</a></p><p>&nbsp; SSL就好装好了。</p><p>PS:仅供参考</p><p>&nbsp;&nbsp;phpstudy配置指定路径访问https</p><pre class="brush:as3;toolbar:false">RewriteEngine&nbsp;on&nbsp;
RewriteCond&nbsp;%{REQUEST_URI}&nbsp;/homework&nbsp;
RewriteRule^(.*)?$&nbsp;https://%{SERVER_NAME}$1[L,R]</pre><p>&nbsp; 好了，保存退出，并重启Apache, 若有报错或者无法正常启动，请看错误日志。</p>','web','清蝎子','0','0','0','1484659526','1379','2','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('47','11','萧峰的降龙掌与段誉六脉神剑，对战谁更胜一筹？','　记得看《天龙八部》有一段桥段，是说当年萧峰与阿朱中了马夫人奸计，误将段正淳当成了带头大哥，结果误将阿朱打死，这也埋下了萧峰最后悲剧的伏笔……','/uploads/20180416\00b2291828724f65435b6c2d3cc6bef7.jpg','<p><span style="color: rgb(70, 70, 70); font-family: ">　　记得看《天龙八部》有一段桥段，是说当年萧峰与阿朱中了马夫人奸计，误将段正淳当成了带头大哥，结果误将阿朱打死，这也埋下了萧峰最后悲剧的伏笔。</span></p><p>　　记得阿朱临死前，说过一句话：“<span style="word-wrap: normal; word-break: normal; line-height: 21px;">大理段家有六脉神剑，你打死了他们镇南王，他们岂肯干休？大哥，那易筋经上的字，咱们又不识得……</span>”可以看出，阿朱愿意赴死，并不是为了自己老爹，只是因为知道，萧峰不一定打得过段家的六脉神剑。原因就是空有易筋经，咱们不识得上面的字。也就是说，如果萧峰练了易筋经，那么就未必打不赢大理段家六脉神剑。</p><p>　　作为天龙中，从未一败的萧峰，如果真的走上了与义弟段誉PK的路，那结局会如何能？是否真如阿朱所说，萧峰打不过六脉神剑呢，这就不得不将萧峰的降龙掌与段誉的六脉神剑作个对比。当然，比武还得看各人临场发挥。我们只能用实际参数比较，发挥是没办法计算在内：</p><p>　　<img src="/uploads/20170326/1490460955736725.jpg" title="1490460955736725.jpg" alt="timg.jpg"/></p><p>　　首先，我们来看看两人的轻功和内功。</p><p>　　在轻功方面方面，段誉有凌波微步，小说中，段誉也正是靠这一步法，几次在生死危机中脱离危险。而萧峰在天龙中，没看到他有什么著名轻功步法的。但在小说十四章《<span style="word-wrap: normal; word-break: normal; line-height: 21px;">剧饮千杯男儿事</span>》，萧峰还是跟段誉比过脚力，在原著中写道：“<span style="word-wrap: normal; word-break: normal; line-height: 21px;">那大汉已知段誉内力之强，犹胜于己，要在十数里内胜过他并不为难，一比到三四十里，胜败之数就难说得很，比到六十里之外，自己非输不可。</span>”这大汉就是萧峰了。从这里可以看出，这时候的段誉，在内力方面，是胜过萧峰的。轻功方面则是刚开始难分仲伯，但时间长了，萧峰就比不上段誉了。</p><p>　　再来看看杀伤力。</p><p>　　萧峰的降龙二十八掌，后来化繁为简，为降龙十八掌，我们可以看出，降龙十八掌是乔峰与虚竹两人通力合作的结果。当然，旧版本还是降龙十八掌，我们就以降龙十八掌作基，与之比较大理段家的六脉神剑。</p><p>　　关于杀伤力方面，萧峰的在聚贤庄与少室山证明了他北乔峰的威名并非虚名，更是在辽国兵变中，于五十万敌军阵中活捉敌军首领皇太叔耶律重元，从而当上了南院大王。而反观段誉，无论如何，他的六脉神剑绝对无法与萧峰的战绩比较。当然，最后他得了鸠摩智的毕生功力后，与虚竹子两人连手将耶律洪基于军阵中捉住。</p><p>　　基本可以这么说，萧峰的战斗优势在于团战，掌力所过，基本非死即伤。而段誉的优势在于单打独斗，在少室山萧峰见段誉与慕容激斗，心下寻思“……<span style="word-wrap: normal; word-break: normal; line-height: 21px;">三弟剑法如此神奇，我若和慕容复易地而处，确也难以抵敌……</span>”</p><p><img src="http://timgsa.baidu.com/timg?image&quality=80&size=b9999_10000&sec=1490471102831&di=2e4279c9eba5ab56174ecf9181e89932&imgtype=0&src=http%3A%2F%2Fi2.sinaimg.cn%2Fgm%2Fo%2Fn%2F2009-11-27%2FU1806P115T9D355908F167DT20091127161322_c.jpg"/></p><p>　　说了杀伤力，我们再来看看持久力。</p><p>　　持久力，往往可以判断一个场决斗的最终输赢走势。而内力的消耗，往往是判断持久力的准线。降龙十八掌乃是天下有数的阳刚掌法，可以说掌掌都是非常消耗内力的，而六脉神剑更是夸张，纯以内力使无形剑气，可以说，萧峰的降龙掌与段誉的六脉神剑都是拼的内力。一人内功终有尽时，而萧峰如果用降龙十八掌，能持久多长时间呢？其实书中已有答案，游坦之和慕容复联手与萧峰相斗，书中明确说了，萧峰可以坚持半个时辰。</p><p>　　为什么萧峰时间持久力是半个时辰呢，因为萧峰掌掌都是用的降龙掌，他只有用降龙掌才能化解危局，不让游坦之和慕容复近身，为的是不使冰蚕寒毒侵袭入身。可以说这时萧峰被迫一掌掌拍出降龙十八掌，还不能停，连继不断的打出降龙十八掌。书中说萧峰可以打半个时辰，也就是1个小时。</p><p>　　而段誉的六脉神剑，如果也连继不停的使，能使多久呢？这个书中没有答案，我们就无从得知了。</p><p>　　看天龙的朋友，.历来都觉得萧峰如果与段誉比试，有人认为萧峰比段誉强，也有认为段誉比萧峰强。孰强孰弱，只能看读者自己了，但从武学上来讲，六脉神剑已经出了现剑气了，而剑气这个名字可以说开始向仙侠的范围靠拢，而降龙十八掌则给人的感觉，还是在武侠的范畴之内。</p><p>　　记得枯荣大师曾说过一句话，“……<span style="word-wrap: normal; word-break: normal; line-height: 21px;">当此末世，武学衰微……“。可以看出，在天龙世界里，武林中人都认为当时是武学末世，那六脉神剑当是武学鼎盛时传下来的功夫。关于扫地僧，被人传的神乎其神，则不一定了，书中曾写：那老僧适才在藏经阁上击打二人，只不过令他们暂时停闭气息、心脏不跳，当是医治重大内伤的一项法门。大家都以为他有起死回生之力给唬住了，实是一大误区。由此可见，扫地僧在天龙的世界，的确了得，但要说天下第一，则也未必，比起逍遥派来也很难说。</span></p>','武侠','清蝎子','0','0','0','1490461065','876','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('48','11','看了电视剧《择天记》，漫聊关于忠于原著……','说实话，猫大的书还是喜欢《间客》和《将夜》，对于《择天记》我就是看了个开头，看到教宗死后，余人当上了皇帝，主角陈长生离开京都后就没怎么看……','/uploads/20180416\74b1dab1abd0201308357e49d14e6da9.jpg','<p>　　说实话，猫大的书还是喜欢《间客》和《将夜》，对于《择天记》我就是看了个开头，看到教宗死后，余人当上了皇帝，主角陈长生离开京都后就没怎么看了，跟猫大前几本来说，感觉《择天记》给人的期望相差太大。</p><p>　　对于择天记改编成电视剧，本来有点期待，但看了第一集，还没看后面了——关了。虽然对于国人来说，大多数拍摄电视剧，还是以“忠于原著”作为卖点，但我感觉这是一种耻辱。要知道，编剧完全是另一种艺术创作，但过于强调忠于原著，就变成加减删除了。以原著为基石，无论翻拍多少次，如果编剧就是加减删除，那么也翻不出什么思想亮点浪花的话，感觉就是编剧的失败。</p><p>　　记得前不久看了篇媒体文章，介绍说当年日本拍《西游记》，他们直接将唐僧改成女的。他们脑洞突破天际，虽然国人喝倒彩，但人家毕竟勇于突破。国人不知为何，似乎更喜欢因循守旧，一切照着规矩来。像金庸剧，翻拍无数，但无论如何，故事线总是照着书中来。没有编剧自己思想内涵在里面，同一部剧，翻拍的剧横向对比，你会发现，编剧也只是加了一点爱情调味料，或这部是辣，下部是咸。这道菜还是这道菜，并没什么区别，永远拍不出自己的思想。</p><p>　　当然，可能这与国内市场环境有关。可能国内人，还是更喜欢“守规矩”的片子。当年周星驰的《大话西游》，可谓彻底颠覆了人们对于西游人物的三观。被所谓文化人骂作垃圾片的大话，可能大众突然接触，也会感到不大能适应（从票房结果来看）。直到过了几年之后，才被人慢慢接受，然后突然喜欢上了这部电影。大话可以说拍出了自己的思想，而不是翻拍《西游》。今年上映的《伏妖篇》，感觉还在《西游》框架之内，并没作多少突破。</p><p>　　现在国内拍出部片子，宣传用语常用“忠于原著”，对于这类片，我一般都不去碰。我之所以去碰这部《择天记》，一是因为看到书迷说无法接受拍出来的电视剧，与想像中不符。我以为，他拍出了自己的故事框架，二是因为猫大的书被拍成电视剧，好奇心作祟，于是，我跑去看了第一集，然后，没有然后……</p><p><img src="http://timgsa.baidu.com/timg?image&quality=80&size=b9999_10000&sec=1493052838923&di=d0357a2d75c7536ccd386dda234b3b35&imgtype=0&src=http://tpic.home.news.cn/xhCloudNewsPic/xhpic1501/M01/3C/C8/wKhTlVjkYr2EJEipAAAAAOtBDjk475.jpg" width="300"/></p>','电视剧，猫腻','清蝎子','0','0','0','1493049616','731','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('49','10','带音效下拉菜单教程','在这个教程中我们将讲述如何利用HTML5开发酷炫的音效下拉菜单。下拉菜单有css3动画效果（菜单元素有整洁的悬浮特效）同时也使用了html5的Audio元素来添加菜单的音效。下面，我们开始吧……','/uploads/20170706/1499355845489557.png','<div></div><p>　　在这个教程中我们将讲述如何利用HTML5开发酷炫的音效下拉菜单。下拉菜单有css3动画效果（菜单元素有整洁的悬浮特效）同时也使用了html5的Audio元素来添加菜单的音效。下面，我们开始吧</p><p>　　首先：<span style="font-size: 14px;">我们应该准备菜单的HTML布局。每个菜单元素，实际上是一个无序的列表项。</span></p><p><span style="font-size: 14px;">　　</span></p><pre class="brush:as3;toolbar:false">&lt;!DOCTYPE&nbsp;html&gt;
&lt;html&gt;
&lt;head&gt;
　　&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;
　　&lt;title&gt;音效菜单&lt;/title&gt;
　　&lt;link&nbsp;rel=&quot;stylesheet&quot;&nbsp;href=&quot;Css/Css.css&quot;&gt;
&lt;/head&gt;
&lt;body&gt;
　　&lt;ul&nbsp;class=&quot;nav&quot;&gt;
　　　　&lt;li&gt;nav1&lt;/li&gt;
	&lt;li&gt;nav2
　　&nbsp;&nbsp;&nbsp;&nbsp;　　&lt;ul&gt;
　　&nbsp;&nbsp;&nbsp;&nbsp;　　　　&lt;li&gt;2F&lt;/li&gt;
	　　　　&lt;li&gt;2F&lt;/li&gt;
	　　　　&lt;li&gt;2F&lt;/li&gt;
	　　&lt;/ul&gt;
	&lt;/li&gt;
	&lt;li&gt;nav3&lt;/li&gt;
	&lt;li&gt;nav4&lt;/li&gt;
	&lt;li&gt;nav5
	　　&lt;ul&gt;
	　　　　&lt;li&gt;5F&lt;/li&gt;
	　　&lt;/ul&gt;
	&lt;/li&gt;
　　&lt;/ul&gt;
&lt;/body&gt;
&lt;script&nbsp;src=&quot;Js/javascript.js&quot;&gt;&lt;/script&gt;
&lt;/html&gt;</pre><p><span style="font-size: 14px;"></span>下面，我们给他简单的添加点样式：<br/></p><pre class="brush:css;toolbar:false">html,body{
	margin:&nbsp;0;
	padding:&nbsp;0;
	background:&nbsp;#333;
}
ul,li{
	list-style-type:&nbsp;none;
	color:&nbsp;#fff;
	margin:&nbsp;0;
	padding:&nbsp;0;
}
ul.nav{
	margin:10px&nbsp;auto;
	width:&nbsp;343px;
}
.nav&nbsp;li{
	float:&nbsp;left;
	width:&nbsp;66px;
	height:&nbsp;30px;
&nbsp;&nbsp;&nbsp;&nbsp;text-align:&nbsp;center;
&nbsp;&nbsp;&nbsp;&nbsp;line-height:&nbsp;30px;
	background:&nbsp;#000;
	border:&nbsp;1px&nbsp;solid&nbsp;#fff;
	border-left-color:&nbsp;transparent;
	cursor:&nbsp;pointer;
}
.nav&nbsp;li:hover{
	background:&nbsp;#00bcd4;
}
.nav&nbsp;li:nth-child(-n+1){
	border-left:&nbsp;1px&nbsp;solid&nbsp;#fff;
}
.nav&nbsp;li&nbsp;ul&nbsp;{
	position:absolute;
	display:&nbsp;none;
}
.nav&nbsp;li:hover&nbsp;ul{
	display:&nbsp;block;
}
ul.nav&nbsp;li&nbsp;ul&nbsp;li&nbsp;{
	float:none;
	background:#000;
	border:&nbsp;1px&nbsp;solid&nbsp;#fff;
&nbsp;&nbsp;&nbsp;&nbsp;border-top:&nbsp;none;
}
ul.nav&nbsp;li&nbsp;ul&nbsp;li:nth-child(-n+1){
	border-top:&nbsp;1px&nbsp;solid&nbsp;#fff;
}</pre><p>现在，是时候添加音乐了。首先，我们应该准备一个空队列（用来存储音频文件）</p><pre class="brush:js;toolbar:false">//&nbsp;是时候添加音频文件了。
var&nbsp;media&nbsp;=&nbsp;[];&nbsp;
//音频文件
media&nbsp;=&nbsp;new&nbsp;Audio(&#39;lab/Media/Gliss_S08MU.mp3&#39;);
//调整音频音量大小，0~1之间
media.volume&nbsp;=&nbsp;.3;
//获取菜单节点
var&nbsp;nav&nbsp;=&nbsp;document.querySelectorAll(&#39;.nav&nbsp;li&#39;);	

var&nbsp;addEvent&nbsp;=&nbsp;(function(){
	return&nbsp;function(dom,&nbsp;type,&nbsp;fn){
		if&nbsp;(&nbsp;dom.nodeName&nbsp;||&nbsp;dom&nbsp;===&nbsp;window)&nbsp;{
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dom.addEventListener(type,&nbsp;fn,&nbsp;false);
	&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;else&nbsp;if(dom&nbsp;&amp;&amp;&nbsp;dom.length){
			for(var&nbsp;i&nbsp;=&nbsp;0;&nbsp;i&nbsp;&lt;&nbsp;dom.length;&nbsp;i++)&nbsp;{
				addEvent(dom[i],&nbsp;type,&nbsp;fn);
			}；
		}；
	}；
})();
//移入声音
addEvent(nav,&nbsp;&#39;mouseover&#39;,&nbsp;function(event){		
	media.currentTime&nbsp;=&nbsp;0;
	media.play();
});
//移除声音
addEvent(nav,&nbsp;&quot;mouseout&quot;,&nbsp;function(event){
	media.pause();
});</pre><p>哇！我们已经完成了( ͡° ͜ʖ ͡°)……</p><p>打开看看效果吧！</p><p><img src="/uploads/20170706/1499355845489557.png" title="1499355845489557.png" alt="QQ截图20170706224345.png"/></p><p><br/></p><p><a href="javascript:;">演示效果</a></p><div></div>','下拉菜单,教程','清蝎子','0','0','0','1499356486','764','1','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('50','10','做了个在线音乐播放器','偶然见到网易云音乐的API文件，闲际无聊间，做了个线音乐播放器。因为我的本地计算机删了IE,一直用chrome再者IE对H5支持不是很好，所以兼容性没考虑IE……','/uploads/20180416\8d2e89cdc8a99279312b641c738306a6.jpg','<p>偶然见到网易云音乐的API文件，闲际无聊间，做了个线音乐播放器因为在网上闲逛。</p><p>因为我的本地计算机删了IE,一直用chrome再者IE对H5支持不是很好，所以兼容性没考虑IE先来看看几张效果图</p><p><img src="/uploads/20170711/1499774853431595.png" title="1499774853431595.png" alt="1499774853431595.png" width="800" height="411"/></p><p><img src="/uploads/20170711/1499775103757379.png" title="1499775103757379.png" width="800" height="410" alt="1499775103757379.png"/></p><p><img src="/uploads/20170711/1499775117794086.png" title="1499775117794086.png" width="800" height="410" alt="1499775117794086.png"/></p><p>因为本站PHP版本太低，只好移到个人网站里面去了 演示地址：<a href="http://y.chiqinga.com" target="_self">http://y.chiqinga.com</a>&nbsp;</p><p><br/></p><p>源码下载：<a href="http://pan.baidu.com/s/1o8wSsFC" target="_self">http://pan.baidu.com/s/1o8wSsFC</a></p><p><br/></p><p>51,只有166KB源文件很小。&gt;</p><p>PS</p><p><br/></p><p>1.因为网易云音乐的API经常有时候卡顿（打开他官网。不知是一个人的原因，还是大家都会卡。)&gt;2.本播放器不支持IE浏览器不支持IE浏览器、不支持IE重要的事说三遍3.因为是第一版本，所以还没写移动端，暂时不支持移动端。4.目前还有些BUG.比方，有时歌词同步会出现偶尔性BUG5.由于其音乐资源来自于网易云音乐，其相应的音乐版权归网易云音乐所有！请支持正版。本音乐播放器只是学习交流，不做任何商业用途。6.因为版权问题，源码请勿做商业用途，并于24小时内删除！</p><p>谢谢配合</p>','音乐播放器,API','王赤清','0','0','0','1499775448','644','0','');
INSERT INTO `article` (`id`,`cid`,`title`,`duction`,`thumb`,`content`,`keywords`,`author`,`tag`,`istop`,`isshow`,`time`,`number`,`feedback`,`ip`) VALUES ('51','10','Ubuntu 改造高仿 Mac OX界面','说实话，已经很久没有写博了，正好近期整了次ubuntu，于是打算记录下来，以备喜欢的小伙伴们参考……','/uploads/20180416\4672fd68eb2adeb75eb6d4da718e46e8.jpg','<p>&nbsp;&nbsp;&nbsp;&nbsp;说实话，已经很久没有写博了，正好近期整了次ubuntu，于是打算记录下来，以备喜欢的小伙伴们参考：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;啦……啦……啦……下面先放出一张整好的效果图出来：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;</p><p><img src="/uploads/20170830/1504063035403785.png" title="1504063035403785.png" alt="1504063035403785.png" width="649" height="365"/></p><p>&nbsp;&nbsp;&nbsp;&nbsp;上图就是桌面效果图了，下面跟着我一步步操作吧<br/></p><p>&nbsp;&nbsp;&nbsp;&nbsp;第一步，当然是去找壁纸了，先找张自己喜欢的壁纸，这是我从网上搜寻到的壁纸包<br/></p><p>&nbsp;&nbsp;&nbsp;&nbsp;http://drive.noobslab.com/data/Mac-13.10/MBuntu-Wallpapers.zip</p><p>&nbsp;&nbsp;&nbsp;&nbsp;这里面有很多mac系例壁纸，大家可以下载下来解压看看，找张自己喜欢的，设置为壁纸，在桌面右键—&gt;更改桌面背景，就可以设置壁纸了，跟win系统差不多吧！<br/></p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;接下来，就是第二步了，就是安装主题MacBuntu-OS-X</p><p>&nbsp;&nbsp;&nbsp;&nbsp;打开终端（ctrl+alt+t)在终端输入以下命令<br/></p><pre class="brush:as3;toolbar:false">sudo&nbsp;add-apt-repository&nbsp;ppa:noobslab/macbuntu
sudo&nbsp;apt-get&nbsp;update
sudo&nbsp;apt-get&nbsp;install&nbsp;macbuntu-os-icons-lts-v7
sudo&nbsp;apt-get&nbsp;install&nbsp;macbuntu-os-ithemes-lts-v7</pre><p>&nbsp;&nbsp;&nbsp;&nbsp;安装Plank Dock</p><pre class="brush:as3;toolbar:false">sudo&nbsp;apt-get&nbsp;install&nbsp;plank</pre><p>&nbsp;&nbsp;&nbsp;&nbsp;安装好了plank Dock后，我们按下窗口键/win键，搜索plank点击启动，这时，我们桌面底部就出现了Dock栏</p><p>&nbsp;&nbsp;&nbsp;&nbsp;接下来，我们开始安装Tweak tool用来启动我们的主题和图标哈～</p><pre class="brush:as3;toolbar:false">sudo&nbsp;apt-get&nbsp;install&nbsp;unity-tweak-tool
sudo&nbsp;apt-get&nbsp;install&nbsp;gnome-tweak-tool</pre><p>&nbsp;&nbsp;&nbsp;&nbsp;安装好了后，我们就启动Tweak tool，选择我们前面安装好的主题和图标，如图示</p><p>&nbsp;&nbsp;&nbsp;&nbsp;<img src="/uploads/20170830/1504066568599265.png" title="1504066568599265.png" alt="1504066568599265.png"/></p><p>&nbsp;&nbsp;&nbsp;&nbsp;选择好了我们的主题和图标，别忘记还有开机启动程序，我们要添加Plank，这样我们就不用每次开机都去手动开启Pank</p><p>&nbsp;&nbsp;&nbsp;&nbsp;现在，我们来修改桌面左上角的“Ubuntu桌面”字样<br/></p><pre class="brush:as3;toolbar:false">cd&nbsp;&amp;&amp;&nbsp;wget&nbsp;-O&nbsp;Mac.po&nbsp;http://drive.noobslab.com/data/Mac/change-name-on-panel/mac.po
cd&nbsp;/usr/share/locale/en/LC_MESSAGES;&nbsp;sudo&nbsp;msgfmt&nbsp;-o&nbsp;unity.mo&nbsp;~/Mac.po;rm&nbsp;~/Mac.po;cd</pre><p>&nbsp;&nbsp;&nbsp;&nbsp;这里有二点要说明一下，第一，如果你是系统是英文版的，这样改没事，但如果你是中文版，命令 ~/en/~ 这个文件代表的是英文，你要改成中文，中文是zh_CN，也就是说第二行命令要改成</p><pre class="brush:as3;toolbar:false">cd&nbsp;/usr/share/locale/zh_cn/LC_MESSAGES;&nbsp;sudo&nbsp;……（略）</pre><p>&nbsp;&nbsp;&nbsp;&nbsp;第二点是，你执行了以上命令，要重启一次，才会生效。<br/></p><p>&nbsp;&nbsp;&nbsp;&nbsp;下面，我们替换启动器图标<br/></p><p>&nbsp;&nbsp;&nbsp;&nbsp;<img src="http://img.blog.csdn.net/20161222181451387?watermark/2/text/aHR0cDovL2Jsb2cuY3Nkbi5uZXQvU3Vuc2hpbmVfaW5fTW9vbg==/font/5a6L5L2T/fontsize/400/fill/I0JBQkFCMA==/dissolve/70/gravity/Center"/></p><p>&nbsp;&nbsp;&nbsp;&nbsp;对，就是这个果标<br/></p><pre class="brush:as3;toolbar:false">wget&nbsp;-O&nbsp;launcher_bfb.png&nbsp;http://drive.noobslab.com/data/Mac/launcher-logo/apple/launcher_bfb.png
sudo&nbsp;mv&nbsp;launcher_bfb.png&nbsp;/usr/share/unity/icons/</pre><p>&nbsp;&nbsp;&nbsp;&nbsp;这样就差不多了，我们再把左侧启动器隐藏，这个在系统设置里面<br/></p><p><img src="/uploads/20170830/1504070364584009.png" title="1504070364584009.png" alt="1504070364584009.png" width="650" height="435"/></p><p>&nbsp;&nbsp;&nbsp;&nbsp;ok,就这样了，别再整了，网上有说修改登录启动界面，千万要谨慎，三思……，想当初我也整了登录和启动界面，然后，你懂的，用了几次，没过多久开不了机了，卡在启动那里，没反应了，不得不重新安装系统，建议不安装……，当然，你技术自信，就当我没说过……</p><p><br/></p>','ubuntu','清蝎子','0','0','0','1504070776','632','0','');

